css:
@-moz-document url('http://www.dreadcast.net/Main') { 

  
 /* La taille de la zone d'affichage de l'image de l'AITL. A adapter à la taille de votre AITL : toute la hauteur définie rend impossible le cliquage "dessous"*/
  
 #zone_dataBox .aitl {height:350px}
  

/* L'image de fond de l'AITL, et le changement d' "écriture" */

#zone_dataBox .aitl {background: url('http://www.zupimages.net/up/16/24/m0db.png') 0 0 no-repeat !important;
  font-family:  "Letter Gothic Std" !important; }
  
/* changement de taille de texte, à cause de la typo Letter Gothic Std */
  #zone_dataBox .aitl .principal .texte {
                    font-size:12px !important;}
  
 /* On fait disparaître les titres Navigation et Actions */
  #zone_dataBox .aitl .navigation .titre {display: none}
#zone_dataBox .aitl .actions .titre {display: none;}
  

/* Modification de la couleur de texte des annonces (question de lisibilité, à adapter selon l'image de fond) */

#zone_dataBox .aitl .principal .texte {color: #000 !important;}

#zone_dataBox .aitl .corporations td.type1, #zone_dataBox .aitl .deces td.type1 {border-bottom: 1px #000 !important;

                                                                                 color: #000 !important;}

    





/* La couleur des titres de catégorie (annonces, entreprises, décès) et des liens de bas d'annonce ( Retour, Haut, Contacter l'auteur )*/

.couleur2 {color: #000 !important}



/* Changement d'emplacement pour la case Rédiger une annonce*/

#zone_dataBox .aitl .actions {height:45px !important;
                              top: 114px !important}
  
  
/* Idem ... plus modification de l'image de fond de cette même case*/
#zone_dataBox .aitl .menu3 {top: 00px !important;
                            background: url(http://www.zupimages.net/up/16/12/lj9j.png) 0 0 !important;
                             color:#AFB5B6;
                            opacity: 1 !important;}
 /* Animation du bouton */
#zone_dataBox .aitl .menu3:hover { background: url(http://www.zupimages.net/up/16/12/5bxn.png) 0 0 !important;
                                  color:#AFB5B6;
                                   padding-top:8px !important; /* fait descendre le texte au passage de la souris*/
                                  padding-bottom: 0px !important; /* idem, ça va avec. Et pareil à chaque fois.*/}
  
  
/* Modification de l'image de fond de Canaux officiels */
#zone_dataBox .aitl .menu1 {background: url(http://www.zupimages.net/up/16/12/lj9j.png) !important;
                                    color:#AFB5B6;
                           opacity: 1 !important;}
 /* Animation du bouton */
#zone_dataBox .aitl .menu1:hover {background: url(http://www.zupimages.net/up/16/12/5bxn.png) !important;
                                    color:#AFB5B6;
                                 padding-top:8px !important;
                                 padding-bottom: 0px !important;}
  
/* Modification de l'image de fond d'Eteindre' */
#zone_dataBox .aitl .menu2 {background: url(http://www.zupimages.net/up/16/12/92vq.png) !important;
                                    color:#AFB5B6;
                           opacity: 1 !important;}
 /* Animation du bouton */
#zone_dataBox .aitl .menu2:hover {background: url(http://www.zupimages.net/up/16/12/5bxn.png) !important;
                                    color:#AFB5B6;
                                   padding-top:8px !important;
                                  padding-bottom: 0px !important;}



}
