css:
@-moz-document domain("www.transip.nl"), domain("transip.nl") {
.alert-info {
    display: none;
}
.form-control,
select.input-sm {
    padding: 2px 4px;
    font-size: 13px;
    height: 24px;
    line-height: 24px;
}
.btn {
    padding: 3px 6px;
}
.btn-sm {
    padding: 2px 7px;
}
.input-group-btn .btn {
    padding: 1px 6px;
}
.form-horizontal .control-label {
    padding-top: 0;
}
.tag .tag-body {
    padding: 2px 5px;
    height: 24px;
}
.sublist-wrapper>.list-item>a {
    padding: 3px 15px 3px 5px;
}
.sublist-wrapper>.list-item .bulk {
    padding-top: 4px;
}
.green-background-full {
    padding-top: 18px;
    padding-bottom: 6px;
}
.list-item:before {
    transform: scale(0.6);
}
.has-no-itemtype-filter .list-item.is-subitem .subitem {
    margin-top: 2px;
}
.sublist-wrapper>.list-item .domain:after {
    top: 5px;
}
.form-panels-sm>.form-panel {
    padding: 2px 14px;
}
.overview-panel h4 {
    margin-bottom: 15px;
}
.arrow-hr-domain {
    margin-bottom: 18px;
}
.form-actions {
    margin-bottom: 5px;
}
.overview-links>.overview-link {
    height:33px;
}
.ljz-overview-panel {
    padding-top:0;
}
.ljz-overview-panel .ljz-overview-panel-heading {
    display:none;
}
.ljz-autodns-zippable .ljz-autodns-zippable-header {
    display:none;
}
.ljz-green-background-full {
    padding:14px;
}
}
