css:
@-moz-document url-prefix("https://discussion.evernote.com/") {

/* Black Evernote Forum */

/* Section titles */
.ipsType_sectionTitle {
    background: #424242;
}

/* Global font color */
a {
    color: black;
}

/* Bigger font-size for unread posts */
.ipsDataItem_unread .ipsDataItem_title {
    font-size: 17px !important;
}

/* Pin icons */
.ipsBadge {
    background-color: #1C1C1C;
}

.fa {
    color: #ffffff;
}

/* Unread dots */
.fa-circle {
    color: #111;
}

/* Usernames */
.ipsType_blendLinks a {
    color: #111;
    font-weight: bold;
}                

/* Mark site read link */
.ipsBreadcrumb [data-action="defaultStream"], .ipsBreadcrumb [data-action="markSiteRead"] {
    color: #111 !important;
    font-weight: bold;
}

/* Button text color*/
.ipsApp .ipsButton_medium {
    font-size: 11px;
    color: #111;
}

/* Button background color*/
.ipsToolList.ipsToolList_horizontal > li.ipsToolList_primaryAction .ipsButton:not( .ipsButton_link ) {
    min-width: 150px;
    background-color: #F2F2F2;
}

/* Hover color */
a:hover {
    color: #C4C4C4;
    text-decoration: underline;
}

a.ipsType_blendLinks:not( .ipsType_noUnderline ):hover, .ipsType_blendLinks:not( .ipsType_noUnderline ) a:not( .ipsType_noUnderline ):hover {
    color: #A4A4A4;
    text-decoration: underline;
}

/* Hide status icon */
.cAuthorPane .cAuthorGroupIcon {
    display: none;
}

/* Mention button */
a[data-mentionid] {
    color: #FAFAFA;
    background: #111;
}

/* Like link */
.ipsLikeRep .ipsButton_alternate {
    color: #111;
    font-weight: bold;
}

/* Submit button */
.ipsApp .ipsButton_primary {
    background: #111;
    color: #FAFAFA;
}

.ipsType_blendLinks a {
    color: #111;
}

/* Link color */
.ipsType_richText a {
    text-decoration: underline;
    color: #2dbe60 !important;
}

/* Mark forum as read link */
a[data-action='markForumRead'] {
    color: #111 !important;
}

/* Trash can icon for removing images in post editor */
.fa-trash-o {
    color: #111;
    font-weight: bold;
}

}
