css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("(https|http)://[w]*([.]|)(youtube.com[/]embed[/])\\S*(cytu.be)\\S*") {
button.ytp-mute-button.ytp-button, .ytp-mute-button.ytp-button {
  position: fixed;
  right: calc(10vw + 54px);
  top: 0px;
  height: 30px;
}

.ytp-volume-slider {
  position: fixed;
  height: 20px;
  top: 5px;
  right: 10vw;
  width: 52px;
}
}
