/*--------------------------------------------------------------------------------
  Tab Mix Plus animated progress bar (version 0.1.5)
  --------------------------------------------------------------------------------
  Author: Louis H - Tested on: Windows XP Firefox 3.0.4 - Last Update: 3.12.2008
  --------------------------------------------------------------------------------
  Description:
    Tab Mix Plus progress bar become animated when loading (like Mac/Ubuntu)
  --------------------------------------------------------------------------------*/

@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");


/* hide the progress bar for selected tab (if you also have Fission) */

.tabbrowser-tab[selected="true"] > .tab-middle.box-inherit.tab-image-middle.tab-body > .tab-text-container > .tab-progress > .progress-bar {
/* display:none !important; */
}



/* Background image of progress bar */
.tab-progress > .progress-bar {

background-image:  
url("data:image/png;base64,") !important;

background-repeat: repeat-x !important;

border-right: solid thin rgba(255,255,255, 0.6) !important;
opacity: 0.9 !important;

/* 
  == Background Color of progress bar, enable it if you don't want the color of Tab Mis Plus option == 
*/
/*background-color: #666666 !important;*/
}
