css:
@-moz-document domain("www.tecmundo.com.br") {
body {
    background-color:#0D0D0D !important;
}

.nzn-ads-top {
    background: #131313 ! important;
}

.nzn-main-wrapper {
    background: #131313 ! important;
}

.nzn-main-button {
    background: #cc181e ! important;
}

.nzn-main-button:hover {
    background: #cc181e ! important;
}

.content-wrapper {
    background: #131313 ! important;
}


.nzn-intervention-wrapper {
    background: #131313 ! important;
}

.blue-theme .navbar, .navbar, body .blue-theme.navbar {
    background: #cc181e ! important;
}

.nzn-carousel-next:before, .nzn-carousel-prev:before {
    background: #131313 ! important;
}

.nzn-carousel-next i, .nzn-carousel-prev i {
    font-size: 20px;
    position: absolute;
    top: 50%;
    display: block;
    width: 20px;
    margin-top: -20px;
    padding: 10px;
    color: #fff;
    border-radius: 20px;
    background: #cc181e ! important;
    box-shadow: 0 1px 2px rgba(0,0,0,.1);
}

.nzn-mosaic-wrapper {
    position: relative;
    z-index: 1;
    margin: 20px auto;
    background: #131313 ! important;
}

.article-header {
    border-top: 2px solid #cc181e ! important;
    border-bottom: 2px solid #cc181e ! important;
}

.blue-theme .navbar-nav-wrapper, .navbar-nav-wrapper, body .navbar.blue-theme .navbar-nav-wrapper {
    border-top-color: #cc181e ! important;
}

.blue-theme .search-input:hover, .search-input:hover {
    border-color: red ! important;
}

.article .main-wrapper {
    background-color: #131313 ! important;
}

html {
    color: #d9d9d9 ! important;
}

.article-title {
    color: #d9d9d9 ! important;
}

.article-text b, .article-text h2, .article-text h3, .article-text strong {
    color: #d9d9d9 ! important;
}


.blue-theme a, a {
    color: #cc181e ! important;
}

.about .label-author, .about .label-tag {
    color: #cc181e ! important;
}

.about a:hover, .blue-theme .about a:hover {
    color: #cc181e ! important;
    border-color: #cc181e ! important;
}

.blue-theme .section-title, .section-title {
    color: #cc181e ! important;
    border-bottom: 4px solid #cc181e;
}

.navbar-nav-link {
    color: #ccebff ! important;
}

.more-news .news-title {
    color: #d9d9d9 ! important;
}

.blue-theme a, a {
    color: #cc181e ! important;
}

.references-title {
    color: #d9d9d9 ! important;
}

.blue-theme .news-item:hover .news-title, .news-item:hover .news-title {
    color: #cc181e ! important;
}

.sidebar-news .news-title {
    color: #d9d9d9 ! important;
}

.comments-box {
    min-height: 160px;
    padding: 49px;
    text-align: center;
    border: 1px solid #ddd;
    border-radius: 2px;
    background-color: #ddd ! important;
}
}
