css:
@-moz-document url-prefix("http://doc.akka.io/api/"), url-prefix("http://www.scala-lang.org/api"), url-prefix("http://www.scala-lang.org/files/archive/api") {
body * {
  background-color: black;
  font-weight: bold;
}

.signature,
.kind,
.extype {
    color: #315479;
}

#constructors > h3,
#template .types > h3,
#template .values > h3,
#template .values > h3,
#template .values > h3 {
    border-bottom: 1px solid grey;
    background: none;
    background-color: black;
    color: white;
}

#signature,
#filter,
#textfilter > .input > input,
.type #definition,
.value #definition,
#constructors > h3,
.fullcommenttop,
#mbrsel > div > ol#implicits > li.in,
#mbrsel > div > ol#implicits > li.in > span,
#mbrsel > div > ol > li.in,
#mbrsel > div > ol > li.in > span,
#mbrsel > div > ol#linearization > li.in,
#mbrsel > div > ol#linearization > li.in > span,
#tpl,
#tpl .packages > ol,
#tpl ol > li.pack,
.ui-layout-resizer,
.ui-layout-pane {
	background: none;
    background-color: black;
    color: white;
}

#types > ol,
#values > ol,
#constructors > ol,
div.fullcommenttop .block,
#comment,
#mbrsel-input {
  background-color: black;
  color: white;
}
#index-input,
#tpl ol > li.pack,
#mbrsel-input {
  border: 1px solid grey;
}


#mbrsel > div > ol > li.in > span,
#mbrsel > div > ol > li.out > span,
#mbrsel > div > ol#linearization > li.in > span,
#mbrsel > div > ol#implicits > li.in > span,
#mbrsel > div > span.filtertype,
.signature,
#signature > span.modifier_kind,
#letters > a, #letters > span,
#kindfilter > a,
#kindfilter > a:hover,
#mbrsel > div > a,
#signature > span.symbol {
  text-shadow: initial;
  color: white;
}
#template .values .signature .name {
  color: green;
}
.class-diagram > svg > g > polygon {
  fill: black;
}

#mbrsel > div > span.filtertype {
  color: white;
}

#mbrsel {
    background-color: black;
    background-image: initial;
}
#textfilter {
    background: none;
}
#textfilter > .input > input {
    color: white;
}
#textfilter > .pre,
#textfilter > .post {
    display: none;
}

#mbrsel > div > ol > li.in > span,
#mbrsel > div > ol#linearization > li.in > span,
#mbrsel > div > ol#implicits > li.in > span {
    text-decoration: underline;
}

::-webkit-scrollbar {
    width: 6px;
    height: 6px;   
}

::-webkit-scrollbar-corner,
::-webkit-scrollbar-track {
  background-color: black;
}
::-webkit-scrollbar-thumb:vertical {
 min-height: 120px;   
}

::-webkit-scrollbar-thumb {
    background-color: grey;
}
}
