css:
@-moz-document url-prefix("https://laravel.com"), url-prefix("https://www.laravel.com") {
.docs article h2 a,
nav.main ul.main-nav > li > a,
* {
	color: white;
}

* {
 
    background: #333333;
    text-shadow: none;
}


nav.main *,
nav.main,
footer.main *,
footer.main {
    background: #222222;
}


.browser-window,
.browser-window .top-bar,
code,
.token {
 	background: transparent;   
}

:not(pre) > code[class*="language-"],
code[class^=" language"],
pre[class^=" language"],
.browser-window .window-content pre[class*="language-"]{
 	background: #00242E;  
    color: white;
}

.token.operator {
 	color: white;
}

.token.comment {
 	color: #629755;
}

.token.keyword {
 	color: #CC7832;   
}

.token.function {
    color: #FFC660;
}

.token.scope {
 	color: #A987C6;   
}

.token.string {
 	color: #6A8759;   
}

.token.variable {
 	color: #9876AA;   
}

code[class*="language-"], pre[class*="language-"] {
 	text-shadow: none;   
}

.dropdown-menu > li > a {
    color: inherit;
}

blockquote * {
 	background: inherit;   
}
}
