css:
/* i really want this to be global */
/* Global */
.qwebirc-qui {
    font-family: Consolas, "Lucida Console", monospace;
}

/* Toolbar */
.qwebirc-qui .outertabbar_top {
    background-color: #2D3034;
    height: 40px;
    padding-top: 7px;
}

.dropdown-tab {
    margin: 0px 10px;
    -webkit-filter: invert(100%);
}

.qwebirc-qui .dropdownmenu {
    background-color: #FFF6E5;
    opacity: 0.98;
    border: none;
}

.qwebirc-qui .dropdownmenu a {
    color: #2D3034;
    padding: 5px 10px;
}

.tabbar a.tab {
    border-radius: 0px;
    border: none;
    background-color: #FFF6E5;
    color: #2D3034;
    cursor: pointer;
    margin-right: 10px;
    padding: 2px 10px 3px 10px;
}

.tabbar a.tab:hover,
.tabbar a.tab:hover > .tabclose {
    border-radius: 0px;
    border: 0px;
    background-color: #7ECEFD;
    color: #FFF6E5;
}

.qwebirc-qui .tabclose {
    vertical-align: inherit;
    font-size: 12px;
    padding-left: 8px;
}

/* Topic */
.qwebirc-qui .topic {
    display: none !important;
}

/* Chat Panel */
.qwebirc-qui .lines {
    background-color: #212326;
    color: #FFF6E5;
    top: 40px !important;
    height: calc(100vh - 28px - 40px) !important;
}

.Xc4 {
    display: none;
}

.Xc4 + span {
    opacity: 0.4;
}

span.timestamp {
    color: #FF7F66;
}

span.timestamp + span {
    color: #7ECEFD;
}

.hyperlink-whois {
    color: #7ECEFD;
}

.qwebirc .colourline .Xu {
    text-decoration: none;
}

.colourline {
    line-height: 18px;
    padding-bottom: 2px;
}

.colourline span a {
    color: #2185C5;
}

.lastpos {
    border-top: 1px solid #404449 !important;
}

.Xc4 + span > .hyperlink-whois {
    font-weight: bold;
    text-decoration: underline;
}

.linestyle2 {
    background-color: rgba(255, 255, 255, 0.02);
}

/* Nicks List */
.qwebirc-qui .nicklist {
    background-color: #2D3034;
    top: 40px !important;
    width: 141px;
    border-left: none;
    height: calc(100vh - 28px - 40px) !important;
}

.qwebirc-qui .nicklist a,
.qwebirc-qui .nicklist a.selected {
    border: none;
    color: #FFF6E5;
    font-size: 12.8px;
    line-height: 16px;
    padding-left: 10px;
    overflow-x: hidden;
    cursor: pointer;
}

.nicklist a:hover {
    background-color: rgba(255, 255, 255, 0.1);
}

.nicklist a.selected {
    border: none !important;
    background-color: #212326 !important;
    padding-bottom: 5px;
}

/* Input Box */
.bottomboundpanel {
    background: #404449;
    border-top: none;
    bottom: 0px;
    height: 30px;
}

.bottomboundpanel input {
    background: transparent;
    color: #FFF6E5;
    padding-top: 4px;
}

.bottomboundpanel input:focus {
    outline: none;
}

/* Custom Scrollbars */
::-webkit-scrollbar {
    width: 12px;
}

::-webkit-scrollbar-track {
    background-color:#2D3034;
    -webkit-border-radius: 0px;
    border-radius: 0px;
}

::-webkit-scrollbar-thumb {
    -webkit-border-radius: 10px;
    border-radius: 0px;
    background: #404449;
}
