css:
@-moz-document url-prefix(http://forum.jogos.uol.com.br/) {
  body {
    background-color: #000 !important;
  }

  .menuAbas {
    display: none !important;                  
  }

  #estatisticas {
    display: none !important;
  }

  .format-option {
    padding: 15px 0px !important;
  }

  .deletado {
    background-color: #c1272d !important;
    color: #fff !important;
    padding: 5px 250px !important;
  }

  #footer.center {
    display: none !important;
  }
  input#user {
    margin-top: -3px !important;
  }
  input#pass {
    margin-top: -3px !important;
  }
  .barrauol-bg.barrauolbg-pos1.full {
    display: none !important;                 
  }

  #header-facebook-like {
    display: none !important;
  }

  .bread-container {
    margin-top: -7px !important;
    margin-left: 7px !important;
  }
 
  .search-on-forum {
    margin-top: -7px !important;
  }

  .search-submit-button {
    margin-top: -9px !important;
    width: 15px !important;
    height: 15px !important;
    padding-left: 5px !important;
  }

  .stars-li {
    margin-top: 7px !important;
  }

  #header-actions-ul li a {
    color: #999 !important;  
  }

  .quote {
    border: 1px solid #A5A5A5 !important;
  }

  .boxGoto {
    height: 29px !important;
    margin: 0 18px 10px !important;
  }

  #content-inside, div#social-div, .boxGotoContent, div#importTab1, .star, .posts-container, div#previewContent, .topicRow, div#importTab1 dd .icon {
    background-color: #c4c2c2 !important;
  }
  
  input#search-field, input, button, textarea, select, optgroup, option, div#estatisticas .texto {
    background-color: #f2f2f2 !important;
    margin-top: -10px;
    border: 2px;
    border-color: black;
  }
  
  .postRow {
    background-color: #f2f2f2 !important;
  }
  
  .quote {
    background-color: #EAEAEA !important;
  }
  
  .search-on-forum {
    color: #666 !important;
    border: 1px;
  }
  
  div#top {
    margin-top: -150px !important;                      
  }
  div#publicidade {
    display:none !important;
  }
  
  .row1 {
    background-color: #e7e7ed !important;
  }
  
  div#legendas {
    background-color: #fff !important;
    margin: 5px 17px !important;
  }
  
  #content-inside {
    border-left: 4px solid #626262 !important;
    border-right: 4px solid #626262 !important;
  }
  
  #header-actions, .bg-color2, .actualPage, .h-bg-color2:hover {
    background: #626262 none repeat scroll 0 0 !important;
  }
  
  .blue-button, #header-menu, .bg-color1, .h-bg-color1:hover {
    background-color: #333 !important;
  }
  
  #header-menu .drop:hover > .item-menu, div#importTab1 dd .data, div#importsContent .titulo, h3 .titulo, #noticiasUl .titulo, .publishDate, .tituloCaixaHome, .tituloCaixaJogos {
    color: #333 !important;
  }

  table#topics td .titulo, table#postsUser td .titulo, div#content .breadcrumb a, table#foruns h3 a, table#foruns a, table#topics a, h3 a:hover, div#importTab2 a:hover, h1 a:hover, .spanH1 *:hover, div#quickAnswerForm a:hover, .texto a, .post-assinatura a, #optionsButtonsBottom .optionMenu a, .userLevel, #recent-trending-topics a, .size3, .size2, .size1 {
    color: #555 !important;
  }

table#foruns td, table#topics td, table#postsUser td {
    padding: 7px 5px !important;  
  }

#content-inside {
    padding-top: 10px !important;
  }

  div#content h2{
    color: #fff !important;
  }
  
  div#contentDiv .texto > a {
  text-decoration: underline !important;
  }
/* ------------------Logo-------------------- */ 
  #station-logo {
  text-shadow:1px 1px 0 #c4c2c2,
-1px -1px 0 #c4c2c2,
1px -1px 0 #c4c2c2,
-1px 1px 0 #c4c2c2,
0px 1px 0 #c4c2c2,
1px 0px 0 #c4c2c2,
0px -1px 0 #c4c2c2,
-1px 0px 0 #c4c2c2,
4px 4px 3px #000;
  }
  
  #uol:hover::before, #station-logo a:hover {
    color: #4e4e4e !important;
  }
  
/* ------------------Responder-------------------- */  
  .formSend, .divSmilesPag {
    background-color: #474848;
    color: #000 !important;
  }
  
  #contador-messageContent, .formSend a, .smiles-pagging, .title, .uolmais-buttons > a {
    color: #f2f2f2 !important;
  }
  
  .formSend-field-noborder b, .formSend-field b, .formSend-video b {
    color: #adc2f1 !important;
  }
  
  .divSmilesPag a {
    color: #46bed9 !important;
  }
}
