css:
@-moz-document domain("voxed.net") {
/* Inicio */
-vox-list {
    background-color: #fff;
}
.vox-list .vox {
	border: none;
    background-color: #fff;
  	border-radius: 50%;
}
.vox-list .vox .over {
	border: none;
}
/*vox-list .vox:hover {
  -webkit-transform: rotate(360deg);
  transform: rotate(360deg);
}*/
.vox-list .vox:hover {
	-webkit-transform:none;
}
.vox-list .vox>a img {
    border-radius: 50%;
}
.vox-list .vox>a img:hover {
	-webkit-transform: rotate(360deg);
  	transform: rotate(360deg);
}
.vox-list .vox:hover h3 {
	background-color:transparent;
}
  
/* Vox */
.vox-single .vox-view .breadcrumbs {
	border-radius: 0;
}
.vox-single .vox-view .meta {
	border-radius: 0;
}
.vox-single .vox-view .content .image a {
	border: none;
  	background-color: transparent;
}

/* Comments */
.vox-single .vox-comments .comments-box {
	border: none;
}
.vox-single .vox-comments .comments-box .preview .attach-vox {
	background-color: transparent;
}
.vox-single .vox-comments .comments-box .preview .attach-vox>img {
	border: none;
  	border-radius: 50%;
}
.vox-single .vox-comments .comments-box .box>textarea{
	border: none;
}
.vox-single .vox-comments .comments-list .comment .image>img {
	border-radius: 50%;
}
.dropdown-pane {
	border: none;
}
  
/* Archivo */
.archive .filter {
	border: none;
}
.archive .archive-list {
	border: none;
}
  
/* Login */
.login-box {
	border: none;
}
}
