css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("startpage.com")
{

  #home_wrap
  {
    background-color: #000!important;
    background-image: url(https://i.imgur.com/miNL0k7.jpg) !important;
    background-attachment: fixed !important;
    background-position: right 10px !important;
    background-repeat: no-repeat !important;
    background-size: 100% 100% !important;
  }

  #results, #content-box, #wrapper, #page-wrapper
  {
    background-color: #000!important;
    background-image: url(https://i.imgur.com/miNL0k7.jpg) !important;
    background-attachment: fixed !important;
    background-position: right 50px !important;
    background-repeat: no-repeat !important;
    background-size: 100% 100% !important;
  }

  #main_link img
  {
    opacity: 0 !important;
  }

  #content
  {
    background-image: url(https://i.imgur.com/ODBnFs4.png) !important;
    background-repeat: no-repeat !important;
    background-position: 50% 35% !important;
    background-size: 300px 150px !important;
  }

  .navbar .navbar-header
  {
    background-image: none !important;
    background-color: #000000 !important;
  }

  #pagenavigation #jumpsbar span.active, #pagenavigation .numbers span.active
  {
    color: #000000 !important;
    background-color: #0089FD !important;
    background-image: none !important;
  }

  .result
  {
    margin-bottom: 3px !important;
    border-radius: 15px !important;
    border: 1px #0089FD solid !important;
  }

  #pics-full-display
  {
    border-radius: 15px !important;
    background: #0089FD !important;
  }

  .sidebar-collapse
  {
    margin-bottom: 3px !important;
    border-radius: 15px !important;
    border: 1px #0089FD solid !important;
    background-color: #000000 !important;
  }

  .text-fields, input, .input_field,
.wrapper-dropdown, .dropdown li, .slide-menu li
  {
    background-color: #222222 !important;
  }

  #footer, .home-wrapper, #pics-lemmon-slider-id,
.dropdown li:hover
  {
    background-color: #000000 !important;
  }

  #menu-id ul li a:hover
  {
    background: #222222 !important;
  }

  #field_size, .settings .form-group .input-group input
  {
    border: 1px #0089FD solid !important;
  }

  #footer, .direct_results_box
  {
    border-top: 1px #0089FD solid !important;
  }

  .navbar-default,
.slide-wrapper .slide-menu > li,
.direct_results_box
  {
    border-bottom: 1px #0089FD solid !important;
  }

  .side_list_menu
  {
    border-bottom: none !important;
  }

  .wrapper-dropdown::after,
.slide-wrapper .slide-menu
  {
    border-color: #0089FD !important;
  }

  .top_menu a:hover,
#menu-id ul li a:hover
  {
    text-decoration: underline !important;
  }

  #sponsored_container1, .newLookDesk
  {
    display: none !important;
  }

  .result, .direct_results_box
  {
    background-color: rgba(0, 0, 0, 0.6) !important;
  }

  .set-heading
  {
    margin-bottom: 3px !important;
    border-radius: 15px !important;
    border: 1px #0089FD solid !important;
    background: #222222 !important;
  }

  .top_menu a, .result_url_heading, .result, #caption a
  {
    color: #0089FD !important;
  }

  input, .desc, .clk, .proxy, .url, .wrapper-dropdown,
.any-span, label, h1, h2, h3, .co-title, .set-heading,
.co-description, li, .dropdown li a, .slide-menu a,
.direct_results_box, 
#full-image-page, #full-image-page-anonymous, #full-image-view
  {
    color: #FFFFFF !important;
  }

  #full-image-page, #full-image-page-anonymous, #full-image-view
  {
    font-size: 20px !important;
  }

  #sponsored_container2
  {
    display: none !important;
  }
}
