css:
/* Hidden elements */
/* By Team5ooo */
@-moz-document domain(dba.dk) {
.banner,
.google-adsense,
.srpListingGallery,
.communication-banner,
.promotionContainer,
tr.cpcListing,
#fbLikeHeading,
.fb-like,
#google-csa-container-top,
.topBannerWrapper,
.bannerContainer,
.adslot.adslot-megaboard,
.adslot.adslot-wallpaper,
.adslot.adslot-skyscraper,
.gallery-component,
.gcsa-container-primary,
.search-result tr.listing-cas.listing-cas-dco,
.adslot.adslot-article,
.adform-adbox-fixed,
.adslot-picturebrowser
{
	display:none !important;
}
body {
	background:none !important;
 	background-image:none !important;
}

	
	
	.searchResults .details a {
	color: #0020C2;
	text-decoration: none;
}
	
	
.searchResults .image-placeholder {
	width: 140px;
	height: 106px;
	display: block;
}

	.srpListView .thumbnailContainer a {
	width: 140px;
	height: 106px;
}

	.srpListView .thumbnail {
	display: block;
	max-width: 140px;
	max-height: 106px;
	overflow: hidden;
	padding: 0;
}
	
	
	.expandable-box.expandable-box-collapsed > a {
	height: 20px;
	font-size: 14px;
	font-weight: bold;
	line-height: 20px;
	overflow: hidden;

}	
}

/* By Team5ooo */
