css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("m.dba.dk") {






.ad,
.gcsa-container-primary,
.gcsa-container-secondary,
.adslot.adslot-mobilesrp
{
	display:none !important;
}

.frontpage {
	background-color: #101010;
background: -moz-linear-gradient(100% 100% 90deg,#101010,#101010); 
	color: #fff;
	text-align: center;
	position: relative;
}

.searchBar {
	min-height:28px;
	padding:10px;
	padding-right:62px;
	position:relative;
}
.searchBar input.text {
	border-style:solid;
	border-color:#202020;
	border-width:1px 0 1px 1px;
	font-size:16px;
	height:20px;
	padding:3px;
	width:100%;
	display:block;
	-webkit-border-radius:0;
}


.thumbnailContainer
{
width: 140px;
height: 106px;
}


.searchResults .thumbnailContainer {

width: 140px;
height: 106px;

}
}
