css:
@-moz-document domain("play.spotify.com") {
.text-success {
    color: #1ed760
}
.btn-primary {
    background: #1db954;
    border-color: #1db954
}
.btn-primary:not(.just-changed):not(.stay-added):hover {
    background: #1ed760;
    border-color: #1ed760
}
.btn.btn-add:hover:not(.just-changed):not(.stay-added) {
    border-color: #1ed760;
}
.btn.btn-add:hover:not(.just-changed):not(.stay-added):after {
    border: 1px solid #1ed760
}
.btn-active:not(.btn-link) {
    border-color: #1db954
}
.btn-active:not(.btn-link):after {
    border: 1px solid #1db954
}
.btn-active.btn-icon.btn-link {
    color: #1db954
}
.btn.btn-add.added {
    border-color: #1db954
}
.btn.btn-add.added:after {
    border: 1px solid #1db954
}
.btn:hover:not(.just-changed):not(.stay-added):after {
    display: block;
    border: 1px solid #1ed760;
}
.dropdown-interior-menu>li>a.checked:before,
.dropdown-menu>li>a.checked:before {
    color: #1db954
}
.tr-drag-indicator {
    border-top: 1px solid #1db954;
}
a.list-group-item.active {
    background-color: #1db954;
    border-color: #1db954
}
.list-group-item.list-group-item-social.added .btn.btn-add {
    border-color: #1db954
}
.list-group-item.list-group-item-social.added .btn.btn-add:after {
    border: 1px solid #1db954
}
.nav-pills>li.active>a,
.nav-pills>li.active>a:focus,
.nav-pills>li.active>a:hover {
    background-color: #1db954
}
.label-success {
    background-color: #1db954
}
.slider.enabled {
    background: #1db954;
}
.has-success .checkbox,
.has-success .checkbox-inline,
.has-success .control-label,
.has-success .help-block,
.has-success .radio,
.has-success .radio-inline {
    color: #1ed760
}
.has-success .form-control {
    border-color: #1ed760
}
.has-success .input-group-addon {
    color: #1ed760;
    border-color: #1ed760
}
.dropdown-menu>.active>a,
.dropdown-interior-menu>.active>a,
.dropdown-menu>.active>a:hover,
.dropdown-interior-menu>.active>a:hover,
.dropdown-menu>.active>a:focus,
.dropdown-interior-menu>.active>a:focus {
    color: #1ed760;
}
.list-group-item.list-group-item-media.active:before {
    background: #1ed760;
}
.list-trackset tbody tr:hover:not(.disc-row).playing .list-item-hl,
.list-playlist tbody tr:hover:not(.disc-row).playing .list-item-hl,
.list-popular tbody tr:hover:not(.disc-row).playing .list-item-hl,
.list-album tbody tr:hover:not(.disc-row).playing .list-item-hl,
.list-search tbody tr:hover:not(.disc-row).playing .list-item-hl,
.list-messages tbody tr:hover:not(.disc-row).playing .list-item-hl,
.list-dev tbody tr:hover:not(.disc-row).playing .list-item-hl {
    color: #1db954;
}
.list-trackset tbody tr.playing td,
.list-playlist tbody tr.playing td,
.list-popular tbody tr.playing td,
.list-album tbody tr.playing td,
.list-search tbody tr.playing td,
.list-messages tbody tr.playing td,
.list-dev tbody tr.playing td,
.list-trackset tbody tr.playing .list-item-hl,
.list-playlist tbody tr.playing .list-item-hl,
.list-popular tbody tr.playing .list-item-hl,
.list-album tbody tr.playing .list-item-hl,
.list-search tbody tr.playing .list-item-hl,
.list-messages tbody tr.playing .list-item-hl,
.list-dev tbody tr.playing .list-item-hl,
.list-trackset tbody tr.playing td a,
.list-playlist tbody tr.playing td a,
.list-popular tbody tr.playing td a,
.list-album tbody tr.playing td a,
.list-search tbody tr.playing td a,
.list-messages tbody tr.playing td a,
.list-dev tbody tr.playing td a,
.list-trackset tbody tr.playing td a:hover,
.list-playlist tbody tr.playing td a:hover,
.list-popular tbody tr.playing td a:hover,
.list-album tbody tr.playing td a:hover,
.list-search tbody tr.playing td a:hover,
.list-messages tbody tr.playing td a:hover,
.list-dev tbody tr.playing td a:hover {
    color: #1db954;
}
.list-trackset tbody tr.added .btn.btn-icon.list-track-plus,
.list-playlist tbody tr.added .btn.btn-icon.list-track-plus,
.list-popular tbody tr.added .btn.btn-icon.list-track-plus,
.list-album tbody tr.added .btn.btn-icon.list-track-plus,
.list-search tbody tr.added .btn.btn-icon.list-track-plus,
.list-messages tbody tr.added .btn.btn-icon.list-track-plus,
.list-dev tbody tr.added .btn.btn-icon.list-track-plus {
    border-color: #1db954;
}
.list-trackset tbody tr.added .btn.btn-icon.list-track-plus:after,
.list-playlist tbody tr.added .btn.btn-icon.list-track-plus:after,
.list-popular tbody tr.added .btn.btn-icon.list-track-plus:after,
.list-album tbody tr.added .btn.btn-icon.list-track-plus:after,
.list-search tbody tr.added .btn.btn-icon.list-track-plus:after,
.list-messages tbody tr.added .btn.btn-icon.list-track-plus:after,
.list-dev tbody tr.added .btn.btn-icon.list-track-plus:after {
    border: 1px solid #1db954;
}
.nav>li.active>a:after {
    background-color: #1ed760;
}
.main-menu.narrow-menu a.active:after,
.main-menu.narrow-menu a.selected:after {
    background-color: #1ed760;
}
.main-menu.narrow-menu a#nav-messages.active,
.main-menu.narrow-menu a#nav-notifications.active {
    color: #1ed760;
}
.main-menu.narrow-menu a#nav-messages.active:before,
.main-menu.narrow-menu a#nav-notifications.active:before {
    color: #1ed760;
}
.main-menu.wide-menu a.active:after,
.main-menu.wide-menu a.selected:after {
    border-right: 5px solid #1ed760;
}
.main-menu.wide-menu .highlighted-folder>a:after {
    background-color: #1ed760;
}
.popover-nav .nav>li.active>a:after {
    background-color: #1ed760;
}
.progress-bar .inner,
.progress-bar-thick .inner {
    background: #1ed760;
}
.modal-image-upload .drop-area {
    color: #1ed760;
    border: 2px dashed #1ed760;
}
.modal-image-upload .progress-bar {
    background-color: #1ed760;
}
.download-dialog a:hover {
    color: #1ed760;
}
#section-settings .sp-toggle.active .handle:hover {
    color: #1ed760;
}
.button-primary {
    background-color: #1db954!important;
    border-color: #1db954!important
}
.button-primary:hover {
    background-color: #1ed760!important;
    border-color: #1ed760!important
}
.button-add:hover {
    border-color: #1ed760
}
.added .button-add,
.button-add.added {
    border-color: #1db954;
}
.added .button-add.just-changed,
.button-add.added.just-changed,
.added .button-add.stay-added,
.button-add.added.stay-added {
    border-color: #1db954
}
.trend-mark.trend-up {
    color: #1ed760
}
.tl-row.playing .tl-cell,
.tl-row.playing .tl-cell a:link,
.tl-row.playing .tl-highlight {
    color: #1db954
}
.tl-row.added .tl-save .btn {
    border-color: #1db954
}
.tl-row.added .tl-save .btn:after {
    border: 1px solid #1db954
}
.tl-row:hover.playing .tl-highlight {
    color: #1db954
}
.tl-sort-asc:after,
.tl-sort-desc:after {
    color: #1db954;
}
.offline .tl-row.offline-downloaded.playing .tl-cell,
.offline .tl-row.offline-downloaded.playing .tl-cell a:link,
.offline .tl-row.offline-downloaded.playing .tl-cell .tl-highlight {
    color: #1ed760!important
}
.list-item-collection::before,
.list-item-local::before {
    color: #1db954;
}
.list-header-sort-asc::after,
.list-header-sort-desc::after {
    color: #1db954;
}
.tl-asc::after,
.tl-desc::after {
    color: #1db954;
}
#shuffle.active,
#repeat.active,
#queue.active {
    color: #1ed760;
}
#bar-inner {
    background: #1ed760;
}
}
