css:
@-moz-document domain('trello.com') {
  .list-card-labels {
    margin-top: 0 !important;
    margin-bottom: 0 !important;
  }
  .card-label {
    height: 6px !important;
    border-radius: 2px !important;
  }
  .list-card-details {
    padding: 4px 4px 2px 6px !important;
  }
  .list-card-details .badge {
    margin-right: 2px !important;
    margin-bottom: 2px !important;
    padding-left: 0 !important;
    padding-right: 0 !important;
  }
  .u-fancy-scrollbar::-webkit-scrollbar {
    width: 4px !important;
    height: 4px !important;
  }
  .list-cards {
    margin-left: 0 !important;
    margin-right: 4px !important;
  }
  .list-header {
    padding-top: 2px !important;
    padding-bottom: 2px !important;
  }
  .list-header-name {
    font-size: 12px !important;
  }
  .open-card-composer {
    font-size: 12px  !important;
    padding: 2px 10px 4px !important;
  }
  .list-wrapper.mod-add {
    width: auto !important;
  }
  .board-header {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
  }
  .list-header-menu-icon.icon-overflow-menu-horizontal {
    height: 6px !important;
    line-height: 6px !important;
  }
  .list-card {
    margin-bottom: 3px !important;
  }
  .list-card-members .member-avatar,
  .list-card-members .member {
    width: 20px !important;
    height: 20px !important;
  }

  .list-card-members,
  .badges {
    display: none !important;
  }

  .list-wrapper {
    width: 250px !important;
  }
}
