css:
@-moz-document domain("tparser.org"){

body
{
    font-family: 'Open Sans',sans-serif;

    background-color: #fff;
}

body:before
{
    position: fixed;
    z-index: 100;
    top: -10px;
    left: 0;

    width: 100%;
    height: 10px;

    content: '';

    -webkit-box-shadow: 0 0 10px rgba(0,0,0,.8);
       -moz-box-shadow: 0 0 10px rgba(0,0,0,.8);
            box-shadow: 0 0 10px rgba(0,0,0,.8);
}

#lg
{
    font-size: 41pt;

    position: absolute;
    left: 50%;

    float: none;

    width: auto;
    height: auto;
    margin-left: -98px;

    background: 0 0;
    text-shadow: 0 4px 3px rgba(0,0,0,.4),0 8px 13px rgba(0,0,0,.1),0 18px 23px rgba(0,0,0,.1);
}

#tb
{
    width: 100%!important;
}

#sh
{
    padding-left: 0;

    border-radius: 0;
    background: 0 0;
    background-color: #91ced7;
    box-shadow: 2px 4px 3px rgba(0,0,0,.3);
}

#bk
{
    height: 100px;
}

#bk > h1
{
    height: 50px;
    margin-top: -20px;

    text-align: center;
}

#ra
{
    width: 969px;
    margin: 0 auto;
}

#pt
{
    position: absolute;
    left: 50%;

    height: 25px;
    margin-left: -264px;
    padding: 10px;

    -webkit-transition: border .3s;
       -moz-transition: border .3s;
         -o-transition: border .3s;
            transition: border .3s;

    border: none;
    border-bottom: solid 2px #c9c9c9;
}

#pt:focus
{
    border-bottom: solid 2px #969696;
}

#pl
{
    display: block;
    float: none;

    width: 635px;
    height: 53px;
    margin: 20px auto 0;

    background-color: transparent;
}

#pt
{
    width: 500px;
}

#kp
{
    float: right;

    width: 47px;
    height: 44px;

    transition: background-color border .2s ease;

    border: none;
    border-bottom: solid 2px #20844a;
    background-color: #27ae60;
}

#kp:hover
{
    background-color: #39ce77;
}

#kp:active
{
    margin-top: 9px;

    border-bottom: none;
}

#lp
{
    top: 15px;
    left: 13px;
}

.rel
{
    position: static;
}

#suggest
{
    position: absolute;
    left: 50%;

    width: 519px;
    margin-top: 47px;
    margin-left: -264px;
}

.sugg
{
    padding: 5px 10px;
}

#ra
{
    margin-top: 100px;
}

#kt
{
    position: fixed;
    bottom: 0;
    left: 0;

    width: 100%;

    border-top: 2px solid #91ced7;
    background-color: white;
}

#ml
{
    text-align: center;
}

#rl
{
    font-size: 13pt;

    text-align: center;
}

#f3,
#f4,
#f5,
#f6,
#f7,
#f8
{
    font-size: 15px;

    margin: 0 auto;
}

.tr,
#dw0,
#dw1,
#dw2,
#sw0,
#sw1
{
    margin: 0 auto;
}

#k1,
#k2,
#k3,
#k4
{
    margin-left: 11px;
}

#filtr
{
    position: absolute;
    top: 236px;
    left: 50%;

    margin-left: -250px;
}

#fl
{
    font-size: 11pt;

    width: 500px;
    height: 34px;
    padding-left: 2px;

    border: solid 1px #b3b3b3;
    outline: none;
    box-shadow: 7px 7px 5px -5px rgba(0,0,0,.2);
}

#ref
{
    width: auto;
}

#table
{
    width: 1100px;
    margin: 100px auto;
}

#sz,
#s,
#l
{
    font-size: 13px;
}

#r2 #name
{
    width: 100%;
}

#nr {
    left: 0;
    text-align: center;
    font-size: 13pt;
}

#pg{left:50%;top:50%;position:absolute;margin: -25px 0 0 -25px;}
}
