css:
@-moz-document url("https://raidforums.com/index.php") {
/*[[Dbgimage]]*/
body {
    background-image: url("/*[[Bgimage]]*/") !important;
    background-repeat: no-repeat;
    background-size: cover !important;
}
.headermain {
    display: none !important;
}
.smallheader {
    display: none !important;
}
.breadcrumb {
    display: none !important;
}    
.newsticker {
    display: none !important;
} 
fieldset {
    display: none !important;
}   
div#mytabs_full {
    display: none !important;
}
tbody#boardstats_e {
    display: none !important;
}
.solfooter {
    display: none !important;
}
.istatwrap {
    display: none !important;
} 
thead {
    display: none !important;
}
#container {
    background: transparent !important;
    border: 1px solid transparent !important;
}
.tborder, .smallheader, .forumstyle, .breadcrumb {
    -webkit-box-shadow: 0px 2px 3px 0px rgba(0,0,0,0) !important;
    -moz-box-shadow: 0px 2px 3px 0px rgba(0,0,0,0) !important;
    box-shadow: 0px 2px 3px 0px rgba(0, 0, 0, 0) !important;
}
.tborder {
    border: 3px solid rgba( 0, 0, 0, 0) !important;
}
.tborder tbody tr:last-child td:last-child {
    border-radius: 10px !important;
}
.tcat {
    border-radius: 10px !important;
    border-bottom: none !important;
}

/*SCROLLBAR*/

::-webkit-scrollbar-thumb {
    background: /*[[Color]]*/ !important;
}

/*HIGHLIGHT*/

::-moz-selection { /* Code for Firefox */
    color: #fff;
    background: /*[[Color]]*/;
}
::selection {
    color: #fff; 
    background: /*[[Color]]*/;
}

/*SHOUTBOX*/

.contentShout>.tabShout.selected {
    background: /*[[Color]]*/ !important;
    color: #fff !important;
}
div.yuieditor-dropdown {
    border: 1px solid rgba(0, 0, 0, 0.54) !important;
    background: rgba(45, 45, 45, 0.72) !important;
}
.yuieditor-more {
    border-top: 1px solid rgba(0, 0, 0, 0.54) !important;
}
div.yuieditor-dropdown a, div.yuieditor-dropdown a:link {
    color: #fff !important;
}
.yuieditor-more:hover {
    background: rgba(0, 0, 0, 0.59) !important;
}
.yuieditor-emoticons-ul>li:hover {
    background: /*[[Color]]*/ !important;
    border-radius: 5px;
    opacity: 0.7;
}
.modal {
    background: #2d2d2d !important;
}
.tcat {
    color: #7A7A7A;
    font-size: 12px;
    text-shadow: none !important;
    background: none !important;
    border-top: none !important;
    border-bottom: none !important;
}
td.tcat {
    color: #7A7A7A !important;
    font-size: 12px !important;
    text-shadow: 0px -1px 0px #151515 !important;
    background: #303030 !important;
    border-top: 1px solid #3F3C3C !important;
    border-bottom: 1px solid #3C3838 !important;
}
button, input.button {
    background: /*[[Color]]*/ !important;
}
a:link {
    color: /*[[Color]]*/ !important;
}
a:visited {
    color: /*[[Color]]*/ !important;
}
a:link:hover {
    opacity: 0.6;
}
a:visited:hover {
    opacity: 0.6;
}
input.textbox:focus, textarea:focus, select:focus {
    outline: 2px solid /*[[Color]]*/ !important;
}
.jGrowl .jGrowl-notification, .jGrowl .jGrowl-closer {
    color: #fff !important;
    border-color: #3DA9A9 !important;
}
}
