css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blog.fefe.de") {
body {
  background-color: #ebebeb;
  width: 900px;
  margin: 0 auto;
  padding-top: 20px;
  padding-bottom: 60px;
  font-family: "Myriad Pro", "HelveticaNeue-Light", "Helvetica Neue Light", "HelveticaNeue", "Helvetica Neue", 'TeXGyreHerosRegular', "Helvetica", "Tahoma", "Geneva", "Arial", sans-serif; font-weight:300; font-stretch:normal;
	background-image: url('data:image/png;base64,')!important;

}

h2:first-letter { color: gold; }



a {
  text-decoration: none;
	color: #262A3C;
	border-bottom: 1px solid rgb(63, 65, 127)!important;
  box-shadow: 0px -3px 0px rgb(63, 65, 127) inset!important;
  transition: background 0.15s cubic-bezier(0.33, 0.66, 0.66, 1) 0s!important;
}

a:hover {
  color: #FFD93F;
  background: #474D6E;
}

h2 {
  font-family: "Myriad Pro";
  font-weight: bold;
  font-size: 80px;
  margin-bottom: 20px;
}

li b {
  font-weight: bold;
}

h2 a {
  color: #262A3C;
  color: #262A3C;
}

h3 {
  font-family: "Myriad Pro";
  color: #000;
	border-right: 0.2em solid #5C5B83;
	border-bottom: 0.2em solid #5C5B83;
	border-top: 1px solid #5C5B83;
	border-left: 1px solid #5C5B83;
	background-color: #ABAEB3;

}

ul {
  margin-left: 0px;
  margin-bottom: 40px;
  padding-left: 0px;
}

ul>li {
  font-size: 16px;
  line-height: 1.4em;
  list-style-type: none;
  position: relative;
  margin-bottom: 20px;
  padding-left: 40px;
  padding-top: 40px;
  padding-right: 40px;
  padding-bottom: 60px;
  background: #BEBEBE;
  color: #000;
  box-shadow:0 5px 6px rgba(91, 91, 131, 10.3), 0 0 40px rgba(91, 91, 131, 0.2) inset;
}

li li{
  padding: 110px 110px 10px 15px;
  font-size: 16px;
  margin-bottom: 0px;
  margin-left: 25px;
  list-style-type: disc;
  list-style-position: outside;
  box-shadow: none;
}

ul>li:before, ul>li:after {
  content:"";
  position:absolute;
  z-index:-2;
}

body > ul > li > a:first-child {
  position: absolute;
  bottom: 30px;
  right: 40px;
  font-family: Georgia;
  font-size: 12px;
}

body > ul > li > a:first-child::before {
  font-size: 100%;
  content: "Permalink: ";
}

blockquote {
  font-family: "Myriad Pro";
  background-color: #D9D9D9;
	border-left: 4px solid #5C5B83;
  padding-left: 10px;
  margin-left: 20px;
}


/* For Screens smaller than 800px width. Smaller margins on boxes and flexible widths */

@media only screen and (max-width: 800px){
  body {
    padding: 5px;
    width: 93%;
    margin: 0 auto;
  }
  h2 {
    font-size: 50px;
    margin-bottom: 20px;
  }
    
  ul {
    margin-left: 0px;
    margin-bottom: 20px;
    padding-left: 0px;
  }
    
  ul>li {
    margin-bottom: 15px;
    padding: 25px 25px 50px 25px;
  }

  blockquote {
    padding-left: 0;
    margin-left: 15px;
  }
}

}
