@-moz-document domain("sgtools.info") {
  /* This work, "SGTools Blue", is a derivative of "SteamGifts v2 Dark-Blue" by Mullins, used under CC BY.
   "SGTools Blue" is licensed under CC BY by knsys.
   @link: https://userstyles.org/styles/126004/sgtools-blue
   
   Creative Commons: http://creativecommons.org/licenses/by/4.0/
   SteamGifts v2 Dark-Blue: https://userstyles.org/styles/110491/steamgifts-v2-dark-blue
   Mullins: https://userstyles.org/users/267199
   */

  /*body*/
    body a,
.hoverTooltip {
        text-decoration: none !important;
        border: 0 !important;
    }

#wrapper {
        background: radial-gradient(rgb(70,80,110),rgb(30,40,70));
    }

    body,
#content .left .list li {
        color: #eef;
    }

    h1,
        h2,
        h3,
#content .subtitle,
#content .wrap .left .before-list {
        color: #fff;
    }

.fa-check:before {
        color: #84FB87;
    }

#footer {
        font-size: 12px;
        background: #212532;
        color: #eef;
        height: 44px;
    }

    hr {
        border: 0;
        height: 1px;
        background: rgba(255,255,255,.3);
    }


  /*nav*/
.nav__button-container:not(.nav__button-container--notification) {
        box-shadow: none;
        background: #757e8f;
    }

.nav__button-container:not(.nav__button-container--notification) {
        box-shadow: none;
        background: #757e8f;
        transition: .1s;
    }

.nav__button {
        color: #E4E6EF;
        text-shadow: none;
        transition: .1s;
    }

.nav__button-container:not(.nav__button-container--notification) .nav__button:hover {
        background: #8c94a3;
        color: #2D3748;
    }

.nav__button-container:not(.nav__button-container--notification) .nav__button.is-selected {
        background: #2F323C;
        box-shadow: none;
        color: #aec5f3;
        text-shadow: none;
    }

.nav__button-container.is-selected .nav__button {
        background: #c9cdd7;
        color: #3c465c;
        text-shadow: none;
        transition: .1s;
    }

.nav__button-container.is-selected .nav__button:hover:not(.is-selected) {
        background: #657EBA;
        color: #E0E3EB;
    }


  /*search*/
    input[type="text"],
        select,
        button {
        padding-left: 5px;
        box-shadow: none;
        border: 0;
        width: 410px;
    }

.form_realcv_order label {
        width: 365px;
    }

.form_realcv_order input[type="checkbox"] {
        margin-right: 0;
    }


  /*results*/
.total {
        border: 0;
        background: rgba(0,0,0,.2);
        border-radius: 10px;
        padding-top: 10px;
    }

.legend {
        border-radius: 6px !important;
        margin-bottom: 40px !important;
    }

.cmGame {
        background: linear-gradient(90deg,#186514,#1E7818,#186514);
        border: 0;
        margin-bottom: 1px;
        text-shadow: 1px 1px 0 #444;
    }

.cmGame:first-child {
        border-radius: 6px 6px 0 0;
    }

.cmGame:last-child {
        border-radius: 0 0 6px 6px;
    }

.cmGame.notActivatedGame,
.cmGame.multiplewins {
        background: linear-gradient(90deg,#8D1D1D,#A52121,#8D1D1D);
        border: 0;
        text-shadow: 1px 1px 0 #444;
    }

.cmGame.bundled {
        background: linear-gradient(90deg,#1D668D,#2276A4,#1D668D);
        border: 0;
        text-shadow: 1px 1px 0 #444;
    }

.cmGame.dmed,
.cmGame.notActivatedDLC,
.cmGame.cantCheckActivated {
        background: linear-gradient(90deg,#6B6B6B,#787878,#6B6B6B);
        border: 0;
        text-shadow: 1px 1px 0 #444;
    }

.nonactivated-dlcs,
.nonactivated-nolonger,
.nonactivated-whitelisted,
.nonactivated-cantcheckactivated,
.show-hide-rules {
        text-decoration: none;
        color: #EEAE93;
    }

.cmGame.notActivatedDLC,
.cmGame.notFound {
        border-radius: 6px;
    }


  /*dashboard*/
#content .featured__outer-wrap {
        border: 6px solid #f0f2f5;
        border-radius: 5px;
        opacity: .5;
        transition: .2s;
    }

#content .featured__outer-wrap:hover {
        opacity: 1;
        box-shadow: 0 0 10px 3px rgba(255,255,255,.4);
    }


  /*ga detail*/
.rules .fa-exclamation:before {
        color: #84FB87;
    }

.gamanageicons {
        color: #222;
    }


  /*ga filters*/
    div#GaFormRealCV,
        div#GaFormRules,
        div#GaFormMisc,
        div#GaFormAdvanced {
        border: 0;
        background: linear-gradient(rgba(0,0,0,.3),rgba(0,0,0,.2));
        padding: 6px 8px;
        width: 700px;
        display: block;
        margin: 0 auto 10px auto;
        float: none;
        border-radius: 5px;
        font-size: 13px;
    }

    div#GaFormRealCV td,
        div#GaFormRules td,
        div#GaFormMisc td,
        div#GaFormAdvanced td {
        padding: 2px 0;
    }

    div#GaFormRealCV table,
        div#GaFormRules table,
        div#GaFormMisc table,
        div#GaFormAdvanced table {
        border-spacing: 0;
        border-collapse: separate;
    }

    div#GaFormAdvanced input {
        margin-top: 5px;
        width: 690px;
    }

    button#Update,
        button#Create,
        button#send,
        button#check,
        button#Check,
.creatorTools>button,
        button#checkGrp,
        button#checkGrp {
        background: linear-gradient(#ddd,#bbb);
        border-radius: 4px;
        cursor: pointer;
    }

    button#Update:hover,
        button#Create:hover,
        button#send:hover,
        button#check:hover,
        button#Check:hover,
.creatorTools>button:hover,
        button#checkGrp:hover,
        button#checkGrp:hover{
        background: #fff;
    }

    button#checkGrp:disabled {
        color: #111 !important;
        cursor: default;
    }

    button#checkGrp:disabled:hover {
        background: linear-gradient(#ddd,#bbb);
    }


  /*button-ize*/
.manageGa>a,
#gaurl>a {
        display: inline-block;
        background: rgba(0,0,0,.3);
        margin-bottom: 20px;
        padding: 7px 10px;
        border-radius: 4px;
        font-size: 14px;
        transition: .2s;
    }

.manageGa>a:hover,
#gaurl>a:hover {
        background: rgba(180,180,200,.3);
    }


  /*enter ga button*/
    button#getlink {
        background: linear-gradient(#529227,#457C21);
        border-radius: 4px;
        cursor: pointer;
        color: #fff !important;
        transition: .2s;
    }

    button#getlink:hover {
        box-shadow: 0 0 25px 5px rgba(200,255,200,.5);
    }


  /*pie-chart*/
    div>svg>rect {
        fill: none;
        color: #fff;
    }

    div#charts {
        border-radius: 5px;
        background: linear-gradient(rgba(200,200,220,.2),rgba(200,200,220,.5));
        padding: 5px 0 5px 5px;
    }

    div#charts .center {
        margin-left: 380px;
    }


  /*entries*/
.entry {
        border: 0;
        border-radius: 5px;
        color: #111;
    }


  /*check group rules*/
.groupRuleResult.rulePassed {
        background: #74CA57;
        color: #111;
        border: 0;
        border-radius: 4px;
        padding: 3px 8px;
        margin-left: 10px;
        font-family: arial;
        font-size: 12px;
    }

.groupRuleResult.ruleFailed {
        background: #E49292;
        color: #111;
        border: 0;
        border-radius: 4px;
        padding: 3px 8px;
        margin-left: 10px;
        font-family: arial;
        font-size: 12px;
    }

  /* sidebar */
.sidebar__heading {
        border-bottom: 1px solid rgba(255,255,255,.3);
        box-shadow: none;
        text-shadow: none;
        color: #E8ECF9;
    }

.sidebar__navigation {
        text-shadow: none;
    }

.sidebar__navigation__item__name,
.sidebar__navigation__item:not(.is-selected) i {
        color: #ECF9E3;
    }

.sidebar__navigation__item.is-selected .sidebar__navigation__item__name,
.sidebar__navigation__item.is-selected i {
        color: #82D246;
    }

.sidebar__navigation__item__link {
        border: 0;
    }

.sidebar__navigation__item__link:hover {
        background: linear-gradient(rgba(255,255,255,.3),rgba(255,255,255,.1));
        border: 0;
    }

.sidebar__navigation__item__link:not(:hover) .sidebar__navigation__item__underline {
        border-bottom: 1px dotted rgba(255,255,255,.3);
        box-shadow: none;
    }

.sidebar__navigation__item__count {
        color: #DBE3F2;
    }
}
