css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stocktwits.com") {

body {background-color: Wheat !important;}

a:hover   { color: yellow !important ; text-decoration: blink !important;}

.twitupdate li {display: block !important }
.twitupdate li {line-height: 35px !important}
ul.twitupdate {background-color: Beige !important}
ul.twitupdate {border:1px solid Tan !important}
ul.twitupdate {margin-bottom:0px !important ; margin-top: 5px !important }

div#globalheader {position:absolute !important ; top: 5px !important ; left: 10px !important; }
div#globalheader img {margin-top: -25px !important ; width: 200px !important ; height: 95px !important; border: 0px !important}
div#globalcrop {display:none !important}

div#latestshadow {display:none !important}


/* POSTING BOX*/
div#twitpost { margin-left: 220px !important ; margin-bottom: -5px !important}

/*REMOVE POST IT BUTTON */
input#twitter_update_submit {display:none !important}

span.topleft-stream {display:none !important}

p.feedthis {display:none !important}
div.rightbox {display:none !important}
div.leftbox h1 {display:none !important}
div#footer {display:none !important}
div#cloud-inside {display:none !important}

div#streams-body {margin-top: -35px !important }
ul.twitupdate li {margin-left: -35px !important}

li.profilepic {}
li.profilepic img {float:left !important ; width: 30px !important; height: 30px !important; border: 0px !important ; padding-right: 10px !important}

li.profilename {float:right !important}
li.profilename a {color: SaddleBrown !important }
li.entry {}
li.reply {display:none !important} 
span.bticker {display:none !important}

.ticker {color: IndianRed !important; font-size:30px !important;font-weight:bold !important ; text-transform: uppercase !important }

.financetalk {color:yellow !important ; display:none !important}

}
