css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hackforums.net") {
  
  body {
    background-color: #202020;
    background-image: url("http://www.rivalchoice.com/wp-content/uploads/2016/03/Dark-Background-HD-Wallpapers.jpg");
    background-repeat: no-repeat;
    background-origin: center center;
    background-position: center center;
    background-attachment: fixed;
    background-size: cover;
  }
  
  a, a:active, a:link:active {
    outline: none;
  }
  
  .navigation a:hover, .navigation a:active, a:hover, a:link:hover, a.subject_new:hover {
    color: #ffffff;
  }
  
  .thead {
    border-radius: 1px 1px 0px 0px !important;
    background: #505050;
  }
  
  #panel {
    border: none;
  }
  
  .tfoot {
    border-radius: 0px 0px 1px 1px !important;
    background: #505050;
  }
  
  .bottommenu {
    background: #505050;
    border: none;
  }
  
  .tborder {
    border: none;
    border-radius: 1px !important;
    background: #252525;
  }
  
  .menu ul {
    border-radius: 1px 1px 0px 0px !important;
    background: #505050;
    padding-top: 1px;
    padding-bottom: 3px;
    margin-top: 15px;
  }
  
  .menu ul a:hover {
    color: #ffffff;
  }
  
  .logo a img {
    border-radius: 10px;
    padding: 15px;
    filter: brightness(110%);
    transition: all 250ms;
    transition-timing-function: ease-in-out;
    transform: scale(1.15);
  }
  
  .logo a img:hover {
    opacity: 1;
    filter: brightness(125%);
  }
  
  .navButton:hover {
    background: inherit;
    border-top: none;
  }
  
  .shadetabs {
    margin-bottom: 10px;
    margin-left: 0;
  }
  
  .shadetabs li a {
    background-color: #666666;
    box-shadow: none !important;
    border-radius: 1px !important;
    transition: all 0.5s;
  }
  
  .shadetabs li a:hover {
    background-color: #888888;
    color: #ffffff;
  }
  
  .shadetabs li a.selected {
    color: #ffffff;
    background-color: #888888;
    top: inherit;
  }
  
  .bitButton {
    border-radius: 1px;
    box-shadow: none !important;
    background: #666666;
    border: none !important;
    transition: background-color 0.5s;
  }
  
  .button {
    border-radius: 1px;
    box-shadow: none !important;
    background: #666666;
    border: none !important;
    transition: background-color 0.5s;
  }
  
  .button:hover {
    background: #888888;
    color: #ffffff;
  }
  
  .bitButton:hover {
    background: #888888;
    color: #ffffff;
  }
  
  .pagination a {
    background-color: #666666;
    box-shadow: none !important;
    border-radius: 1px !important;
    transition: background-color 0.5s;
  }
  
  .pagination a:hover {
    background-color: #888888;
    color: #ffffff;
  }
  
  .pagination .pagination_current {
    background-color: #888888;
  }
  
  .footerad {
    margin-top: 25px;
  }
  
  .group0 {
    color: #FFAA00;
  }
  
}
