css:
/* i really want this to be global */


/* THIS CODE HIDES SUPERFLUOUS DETAILS */
.highly-highlights-list .highly-highlights-list-item .highly-content sup,
.highly-inline,
.highly-article-summary-current-highlighter-info {
    Display: none;
}

/* THIS CODE FORMATS THE WIDTH AND DIMENSIONS OF THE HIGHLIGHTS SNIPPET AREA */
.highly-summary .highly-highlights-list {
    background: white;
    margin-left: -100px;
    margin-right: -100px;
    margin-bottom: -130px;
}
.highly-highlights-list .highly-highlights-list-item {
    border-top: 1px solid #ffffff;
    margin-top: 15px;
}
