@-moz-document url("https://www.touhou-project.com/"), url("https://www.thp.moe/"), url("https://www.touhou-project.com/?page=news"), url("https://www.touhou-project.com/?page=faq"), url("https://www.touhou-project.com/?page=rules"), url("https://www.touhou-project.com/?page=irc"), url("https://www.touhou-project.com/?page=index"), url("https://www.thp.moe/?page=news"), url("https://www.thp.moe/page=rules"), url("https://www.thp.moe/?page=irc"), url("https://www.thp.moe/?page=index"), url("https://www.thp.moe/?page=faq") {
html {
	background-color: #3a3a3a;
	color: #dadada;
	font-family: "Trebuchet MS", "Tahoma", "Verdana", "Arial", sans-serif;
	font-size: 13px;
}

body {
	width: 85%;
	margin: 0 auto;
	background-color:  #3a3a3a;
}


/** logo **/

.logo { 
	background: url('https://www.touhou-project.com/logob.jpg') top  no-repeat;
	width: 546px;
	height: 109px;
	margin:  auto;

}

/** menu **/

.menu ul {
	list-style: none;
	margin: 0;
	padding: 7px 0;
	border-bottom: 1px solid #dadada;
	font-weight: bold;
	text-align: center;
	white-space: nowrap;
}

.menu ul li {
	display: inline;
	margin: 0 2px;
}

.menu ul a {
	text-decoration: none;
	padding: 0 0 3px;
	border-bottom: 4px solid #CCC;
	color: #7e949e; 
}

.menu ul a.active, .menu ul a:hover {
	border-color: #FFF;
	color: #FFF;
}

/** boxies **/

.box { 
	background: #3a3a3a;
	border: 1px solid #000000;
	margin: 15px 0 0 0;
}


.box .box-title {
	font-size: 18px;
	background: #2d2d2d;
	padding: 2px;
}

/** info **/
.info{
width:100%;
}
.info .box-content{
padding: 0 15px 5px 15px;
}
/** boards **/

.boards{
margin-top:auto;}
.boards .box-content{
	display:flex;
flex-flow: column nowrap;
padding-bottom:0px;
}
.boards .Gene{
order:1;
}
.boards .Othe{
order:2;
}
.boards .Stor{
order:3;
}

.boards ul {
/*	margin-left: 15%;*/
	font-weight: bold;
}

.boards ul li {
	font-weight: normal;
text-align: left;
	list-style: none;
}


/** recent images **/

.recent-images {
	width: 160px;
float:left;
margin-bottom: 10px;
}


.recent-images .box-content {
	height: auto;
	text-align: center;
	overflow: hidden;
	padding: 5px 0 ;
}

.recent-images ul {
	width: 100%;
	margin: 0;
	padding: 0;
}

.recent-images ul li {
	background: #2b2b2b;
	list-style: none;
	width: 140px;
	height: auto;
	margin: 5px auto;
	padding: 5px;
}

.recent-images ul li img {
	max-width: 250px;
	max-height: 150px;
}

.megabox{background: none;
	border:none;
	width: calc(100% - 170px);
	float: right;
	margin-bottom: 10px;}
/**  Posts **/

.last-new .box-content {
	padding: 0 10px;
	margin-left: 15px;
}

.latest-bumped ul li {
text-align: left;
margin-right:1%;
margin-left: 1%;
}
.mystery .box-content{
text-align: left;
margin-left: 2%;
}

table, th, td {
	border: 1px solid black;
	padding: 3px;
}

.news .box-content{
padding: 3px;
}
.faq .box-content{
padding: 3px;
}

/** footer **/

.footer {
	text-align: center;
	font-size: 0.9em;
	margin-top: 5px;
	clear:both;
}
  
.footer p {
	color: white !important;
	font-weight: bold;
}

.footer a {
	color: #3399ff !important;
}

/** formating **/

a {
	color:#7e949e;
}

a:hover {
	color:#aab8bf;
}



/*mobile only stuff*/

@media screen and (max-width: 725px) { 

html {
        font-family: "Trebuchet MS", "Tahoma", "Verdana", "Arial", sans-serif;
        font-size: 13px;
}
body {
        width: 98%;
}

.logo { 
        background: url('https://www.touhou-project.com/logom.gif') left  no-repeat;
        width: 320px;
        height:  64px;
margin:auto;
/*margin: 0 0 0 -3px;*/
}
.latest-bumped ul li {
	text-align: left;
	margin-right: 0%;
	margin-left:0;
	font-size:.9em;
}


.boards ul li {
text-align: left;
}
.megabox{
width:100%;
}

.boards,
.latest-bumped, 
.mystery,
.last-new {
        float: none;
        width: 100%;
	margin:auto;
}

.recent-images {
display:none;
float:none;
width:auto;
}
}
.recent-images ul li:nth-child(n+7){display:none;}


@media screen and (min-width: 1465px) {

body{ width:75%;
}
.megabox {
	float: right;
	width:calc(100% - 180px);
}
.recent-images{
	float:left;
	width:170px;
}

.boards .box-content{
	display:flex;
	flex-flow:row wrap;
	justify-content:flex-start;
	margin:auto;
}
.recent-images ul li {
        width: 160px;
}

}
