css:
@-moz-document url-prefix("http://jvforum.fr/") {
.sheet {
background: url(http://johnnysgamecafe.fr/wp-content/uploads/2015/02/Persona-4-01.jpg) fixed no-repeat center;
background-size: cover;
}
.menu {
margin: 15px 10px;
background: hsla(0, 0%, 0%, 0.67);
padding: 2px 0;
border-radius: 3px;
}
.menu__item+.menu__item {
border-top: 1px solid hsl(0, 0%, 28%);
}
.menu__item {
display: block;
color: hsl(0, 0%, 0%);
font-weight: bold;
cursor: pointer;
padding: 13px 10px;
}
a_hover {
color: hsl(0, 0%, 0%);
}
.pagination-topic__action-button {
background: transparent;
display: table-cell;
width: 1px;
}
.message__content {
display: block;
margin-left: 0px;
background: hsla(57, 100%, 51%, 0.85);
border-radius: 3px;
box-shadow: inset 0 1px 0 hsla(0, 0%, 100%, .05), 0 2px 5px hsla(0, 0%, 0%, .15);
}
.message__visible {
border-radius: 3px;
background: grey;
color: hsla(53, 100%, 50%, 1);
box-shadow: inset 0;
}
.message__visible {
background: hsla(240, 40%, 41%, 0);
color: grey;
}
.message__byline-author-pseudo {
color: hsl(0, 0%, 10%);
font-weight: bold;
line-height: 30px;
}
.message__byline-date-inline {
font-size: 12px;
cursor: default;
background: hsl(57, 69%, 23%);
box-shadow: inset 0 1px 0 hsla(0, 0%, 100%, .05), 0 1px 4px hsla(0, 0%, 0%, .15);
padding: 2px 4px;
color: hsl(0, 0%, 10%);
border-radius: 2px;
}
.menu__item:hover {
background: hsla(0, 68%, 11%, 0.48);
}
.quote {
margin: 1em 0;
border-left: 3px solid rgba(0, 0, 0, 0.42);
padding: 1px 8px;
background: rgb(216, 240, 29);
color: hsl(240, 8%, 87%, .2);
font-size: 13px;
}
.header__home-link {
color: hsla(0, 0%, 12%, 0);
background: url(http://image.noelshack.com/fichiers/2016/13/1459194536-persona.png)center no-repeat;
padding: 10px 20px;
display: inline-block;
}
.header {
display: table;
width: 100%;
margin-top: -5px;
font-size: 16px;
font-weight: bold;
background: hsla(0, 0%, 0%, 0.78);
box-shadow: inset 0 -3px 3px rgba(0, 0, 0, .2);
}
.header__forum-cell {
display: table-cell;
text-transform: none;
background: hsla(0, 0%, 0%, 0.04);
box-shadow: inset 0 -2px 3px rgba(0, 0, 0, 0.3);
white-space: nowrap;
}
.header__forum-link {
color: hsla(0, 100%, 100%, .73);
padding: 10px 20px;
display: inline-block;
}
.page-title__link,
.page-title--topic {
color: hsla(0, 0%, 50%, 1)
}
.button--cta {
background: hsla(255, 100%, 60%, 0);
color: hsla(0, 100%, 100%, .73);
}
.button--raised:active {
box-shadow: 0 2px 5px rgba(0, 0, 0, .1);
background: hsla(0, 0%, 69%, 0);
}
.button {
font-weight: bold;
font-size: 14.4px;
padding: 5px 8px;
border-radius: 3px;
letter-spacing: .5px;
cursor: pointer;
text-transform: uppercase;
-webkit-tap-highlight-color: transparent;
-webkit-appearance: none;
border: none;
background: transparent;
outline: none;
color: grey;
}
.form {
width: 100%;
background: hsla(0, 0%, 0%, 0.53);
padding: 8px;
position: relative;
z-index: 1;
box-shadow: inset 0 1px 0 hsla(0, 0%, 100%, .05), 0 2px 10px hsla(0, 0%, 0%, .2);
}
.form__topic,
.form__topic, .form__textarea {
-webkit-appearance: none;
font: 14px/1.4 -apple-system, sans-serif;
width: 100%;
border: 1px solid hsl(0, 0%, 0%);
padding: 4px 6px;
outline: none;
border-radius: 3px;
background: hsl(0, 0%, 9%);
color: hsl( 0%, 100%);
box-shadow: 0 1px 0 hsla(0, 0%, 100%, .075), inset 0 2px 8px hsla(0, 0%, 0%, 0.5);

}
.pagination-topic__page-link--active {
background: hsla(0, 0%, 0%, 0.2);
color: hsl(0, 0%, 10%);
font-weight: bold;
margin: 0;
padding: 0 2px;
}
.pagination-topic__page-link:hover {
background: hsla(0, 0%, 0%, 0);
}
a {
color: darkgreen;
text-decoration: none;
}
.topic:visited {
color: hsl(0, 0%, 10%);
}
.liste-topics {
background: hsla(329, 0%, 52%, 0.56);
display: table;
width: 100%;
margin: 10px 0;
}
.topic--visited,
.topic:visited {
color: hsla(245, 78%, 50%, 1);
}
.topic:nth-child(odd) {
background: hsla(357, 53%, 37%, 0);
}
.pagination-topic__page-link--visited {
color: hsl(0, 0%, 10%);
}
}
