css:
/* i really want this to be global */

 /* 	Material design Youtube
		@version 1.0
*/


/* General */
body {
  font-family: 'Roboto', sans-serif;
	background: #f2f2f2;
}

a {
  font-family: 'Roboto', sans-serif;
}


/* Masthead */
#yt-masthead-container {
  padding-bottom: 13px;
  background: #D50000;
  padding-top: 12px;
  border: none;
}

/* Menu */
.yt-uix-button-icon-appbar-guide {
  background: no-repeat url(http://i.imgur.com/D4oJuiR.png);
}


/* Youtube logo */
#yt-masthead #logo-container .logo {
  background: url(https://www.youtube.com/yt/brand/media/image/YouTube-logo-light.png) no-repeat center;
  background-size: 100px 60px;
  height: 32px;
}

#yt-masthead #logo-container span.content-region {
  display: none;
}


/* Searchbar */
#masthead-search-terms {
  background: rgba(255,255,255,.20);
  border-radius: 2px;
  margin-left: 22px;
  color: #fff;
}

.masthead-search-terms-border {
  box-shadow: none;
  border: none;
}

.gsok_a {
  display: none;
}

#masthead-search .search-btn-component {
  margin-left: -10px;
  background: none;
  box-shadow: none;
  border: none;
}

.gsib_a {
  padding-left: 12px;
}

#masthead-search-terms input {
  color: #dedede;
}

#masthead-search .search-btn-component .yt-uix-button-content {
  background: no-repeat url(http://i.imgur.com/rGxwdn4.png);
  margin-top: -2px;
}


/* Masthead buttons */
.yt-thumb-27 {
  background: none;
}

#upload-btn {
  background: no-repeat url(http://i.imgur.com/03CgvgD.png);
  background-position: 0px 7px; 
  text-indent: -9999px; 
  box-shadow: none;
  border: none;
}

#yt-masthead-user .yt-uix-button-icon-bell {
  background: no-repeat url(http://i.imgur.com/pVXgXIh.png);
  background-position: 0px 8px; 
  margin-left: 1px;
}


/* Appbar */
#masthead-appbar {
  border: none;
  background: #B71C1C;
  height: 50px;
}

.appbar-hidden #masthead-appbar {
  margin-top: -50px;
}

.appbar-nav-menu {
  margin-top: 5px;
}

.epic-nav-item-heading {
  font-family: 'Roboto', sans-serif; 
  letter-spacing: 0.2px; 
  font-weight: 400;
  font-size: 14px;
}

a.yt-uix-button.yt-uix-button-epic-nav-item {
 font-size: 14px;
}

.appbar-nav-menu .epic-nav-item-heading {
  color: #fff;
}

#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item {
  font-family: 'Roboto', sans-serif;
  letter-spacing: 0.2px; 
  font-weight: 300;
  color: #dedede;
}

.appbar-nav-avatar {
    margin-top: 10px;
  border-radius: 100px;
}

/* Side menu */
#appbar-guide-menu {
  box-shadow: 0 0px 15px 0 rgba(0,0,0,0.20),0 0px 40px 0 rgba(0,0,0,0.05) !important;
  top: 10px !important;
  background: #fcfcfc;
  width: 250px;
}

#guide-container {
  padding: 13px 22px;
  width: 245px;
}

.guide-notification-item {
  border-radius: 2px;
  height: 35px;
}

.guide-channels-list .guide-item {
 width: auto;
}

.guide-section-separator {
  border-bottom: none;
  height: 1px;
}


/* First section icons */
.guide-user-links {
  margin-top: 3px;
}


/* Page */
#page-container {
	margin-top: 40px;
}

.branded-page-module-title {
  padding-bottom: 26px;
  font-weight: 400;
}

.branded-page-module-title a {
  font-weight: 400 !important;
}

.multirow-shelf .yt-uix-expander-head {
  margin-top: 10px;
}


/* Subscribe button */
.yt-uix-button.yt-uix-button-subscribe-branded {
  border-radius: 10px 0 0 10px;
  height: 25px;
  width: 80px;
}

.yt-uix-button-subscribe-branded:before {
  display: none;
}

.yt-subscription-button-subscriber-count-branded-horizontal {
  height: 23px;
}

.yt-card {
  box-shadow: 0 -1px 0 #e5e5e5,0 0 2px rgba(0,0,0,.05),0 2px 4px rgba(0,0,0,.10);
  border-radius: 4px;
}

.video-thumb {
  border-radius: 3px;
  background: #fff;  
}

.yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {
  color: #464D4F;
}

.branded-page-module-title a:visited, .branded-page-module-title a {
  font-size: 18px;
  font-weight: 300;
  color: #464D4F;
}

.branded-page-box, .branded-page-box-padding {
  padding: 25px;
}


/* Footer */
body #footer-container {
  background-color: #17181c;
  margin-top: -140px;
  border: none;
}

#footer-main {
  padding-bottom: 10px;
  border: none;
}

#footer {
  padding: 28px 0px 28px 0px;
}

#footer-logo .footer-logo-icon {
  background: url(http://i.imgur.com/V7LkAC4.png) no-repeat center;
  height: 32px;
  width: 32px;
}

#footer-links {
  padding: 15px 0 0px 8px;
}


/* Video */
.watch-stage-mode #player, .watch-stage-mode #placeholder-player {
  margin-top: -40px;
}


/* 	Comments */
.comment-renderer .comment-author-thumbnail {
  border-radius: 100px;
  margin-right: 20px;
}

.comment-replies-renderer-expander-down, .comment-replies-renderer-expander-up {
  margin-left: 68px;
  margin-top: 10px;
}

.comment-simplebox-renderer .comment-author-thumbnail {
  border-radius: 100px;
}
