css:
@-moz-document domain("freecodecamp.com") {
/* Elements */

form {
    background: #1b1918 !important;
}
hr {
    color: #407ee7 !important;
    background: #766e6b !important;
}
code,
dfn,
blockquote {
    background: #2c2421 !important;
    color: #00ad9c !important;
}
a {
    color: #407ee7 !important;
}
i {
    color: #5ab738 !important;
}
div,
div b {
    color: #e6e2e0 !important;
}
input {
    background: #2c2421 !important;
    color: #766e6b !important;
}
thead {
    color: #a8a19f !important;
    border-color: #2c2421 !important;
}
th {
    color: #a8a19f !important;
    border-color: #2c2421 !important;
}
tr {
    background: #1b1918 !important;
    color: #a8a19f !important;
    border-color: #2c2421 !important;
}
table-striped,
td,
table {
    border-color: #2c2421 !important;
}
label {
    color: #766e6b !important;
}
body {
    background: #1b1918;
}
td {
    color: #766e6b !important;
}
body.no-top-and-bottom-margins {
    background: #1b1918 !important;
}
}

@-moz-document domain("freecodecamp.com") {
/* Classes */

.landing-heading {
    color: #407ee7;
}
.btn-block,
.btn-cta,
.signup-btn.btn,
.signup-btn,
.btn-big,
.btn-primary,
.btn-group,
.btn,
.input-group,
.btn-group-justified,
.button {
    text-shadow: none;
    background: #2c2421 !important;
    color: #5ab738 !important;
    border-color: #a8a19f !important;
}
.landing-skill-icon,
.font-awesome-padding,
.fa-database,
.custom-landing-skill-icon {
    color: #4077e7 !important;
}
.black-text {
    color: #d5911a;
}
.modal-content,
.modal-dialog {
    background: #1b1918 !important;
    color: #766e6b !important;
}
.modal-header,
.challenge-list-header {
    background: #2c2421 !important;
    color: #5ab738 !important;
}
.completion-icon,
.big-success-icon {
    color: #5ab738 !important;
}
.big-error-icon {
    color: #f22c40 !important;
}
.CodeMirror-line {
    color: #e6e2e0 !important;
}
.CodeMirror-gutters,
.CodeMirror-gutter,
.CodeMirror-lint-markers {
    background: #2c2421 !important;
    color: #e6e2e0 !important;
}
.cm-tag,
.cm-bracket {
    color: #00ad9c !important;
}
.cm-keyword {
    color: #f22c40 !important;
}
.cm-comment {
    color: #766e6b !important;
}
.cm-atom,
.cm-variable,
.cm-number {
    color: #6666ea !important;
}
.cm-error {
    background: #f22c40 !important;
    color: #e6e2e0 !important;
}
.cm-attribute,
.cm-property {
    color: #5ab738 !important;
}
.cm-string {
    color: #d5911a !important;
}
.map-aside {
    background: #1b1918 !important;
    color: #766e6b !important;
    border-color: #a8a19f !important;
}
.input-group,
.map-filter,
.input-group {
    background: #2c2421 !important;
    color: #766e6b !important;
    border-color: #a8a19f !important;
}
.input-group-addon {
    background: #5ab738 !important;
    border-color: #a8a19f !important;
}
.map-buttons {
    border-color: #a8a19f !important;
}
.btn-primary {
    background: #2c2421 !important;
}
.fa-search {
    color: #e6e2e0 !important;
}
/*
.map-aside-action-bar {
    background: #1b1918 !important;
    color: #e6e2e0 !important;

.map-aside-action-item, .map-aside-action-collapse, .map-aside-action-pop-out {
    background: #1b1918 !important;
    color: #766e6b !important;
}
*/

.map-accordion h2 > a {
    background-color: #2c2421 !important;
}
.certBlock,
.map-collapse,
.map-aside-body,
.map-header,
.map-fixed-header,
.mapWrapper {
    background: #1b1918 !important;
    color: #766e6b !important;
}
.fa-caret-down {
    background: transparent !important;
    color: #766e6b !important;
}
.challengeBlockTime {
    color: #766e6b !important;
}
.text-info,
.text-primary {
    color: #d5911a !important;
}
.ion-checkmark-circled {
    color: #5ab738 !important;
}
.disabled,
.ion-locked {
    color: #766e6b !important;
}
.profile-social-icons,
.fa-github-square {
    color: #407ee7 !important;
}
.cal-heatmap-container,
.domain-background,
.graph-domain,
.graph-label {
    background: #1b1918 !important;
    color: #766e6b !important;
}
.graph-rect {
    fill: #2c2421 !important;
}
.r4 {
    fill: #5ab738 !important;
}
.r3 {
    fill: #5ab738 !important;
    opacity: 0.75 !important;
}
.r2 {
    fill: #5ab738 !important;
    opacity: 0.5 !important;
}
.r1 {
    fill: #5ab738 !important;
    opacity: 0.25 !important;
}
.gitter-chat-embed,
.gitter-chat-embed-action-bar {
    background: #1b1918 !important;
    color: #766e6b !important;
}
.chat-embed-main-title {
    color: #e6e2e0 !important;
}
.gitter-chat-embed-action-bar-item,
.gitter-chat-embed-action-bar-item-pop-out,
.gitter-chat-embed-action-bar-item-collapse-chat {
    color: #407ee7 !important;
    opacity: 1 !important;
}
.gitter-chat-embed-loading-indicator,
.gitter-icon {
    color: #407ee7 !important;
}
.articleContent,
.markdown,
.article-container,
.wikiSelector,
.wiki-container,
.wiki-aside,
.wiki-aside-embed,
.wiki-aside-action-bar {
    background: #1b1918 !important;
    color: #766e6b !important;
}
.landing-page {
    color: #766e6b !important;
}
.navbar {
    background: #2c2421;
}
.navbar-right {
    background: #2c2421;
}
.panel-heading {
    background: #1b1918 !important;
    border-color: #2c2421 !important;
}
.panel-info {
    border-color: #2c2421;
}
.panel-body {
    background-color: #766e6b
}
.cm-s-monokai.CodeMirror {
    background-color: #2c2421 !important;
}
.row ng-scope {
    background: #2c2421 !important;
}
}

@-moz-document domain("freecodecamp.com") {
/* IDs */

#mainEditorPanel {
    background: #2c2421 !important;
}
#map-filter {
    border-color: #a8a19f !important;
}
#accordion {
    background-color: #1b1918 !important;
}
}
