css:
@-moz-document domain("tanzil.net") {
#top-content {
    height: 0px;
}
.top-menu {
    background: #000;
    height: 15px;
    border-bottom: 1px solid #EFEFEF;
}
.floatStart a {
    pointer-events: none;
}
#main-content {
    margin-top: -46px;
}
#side-content {
    margin-top: -46px;
    background: #FFFFFF;
    border: 1px solid #F0F0F0;
}
#page-tab ul {
    background: #fff;
    border-bottom: 1px solid #E8E8E8;
}
.qFrameTop {
    background: #fff;
    height: 5px;
}
.menu-top {
    background: #fff;
    border-bottom: 1px solid #EFEFEF;
}
.qFrameMiddle {
    background: #fff;
}
.qFrameBottom {
    background: #fff;
}
.aya.hover {
    background-color: #EFF8FF;
}
.aya {
    background-color: #fff;
}
.aya.selected,
.aya.highlight {
    background-color: #BEE9FF;
}
.suraHeaderFrame {
    background: #E5E3E3;
    border-radius: 5px;
}
.suraHeaderText {
    line-height: 19px;
}
.ayaNumber {
    color: #008EF4;
}
.ayaNumber a {
    color: #1272C3;
}
body {
    background: #F7F7F7;
}
#container {
    border: 1px solid #E2E2E2;
}
#footer-content {
    background: #FFFFFF;
    border-top: rgb(239, 238, 238) 1px solid;
    border-bottom: 0px;
}
#body-content {
    background: #fff;
}
.aya-wrapper {
    background: transparent;
}
.sub-menu {
    background: #FFFFFF;
}
.menu-body {
    border: 1px solid #FFFFFF;
}
div.jp-interface {
    background: #fff;
    border-color: #DFDFDF #DFDFDF #DFDFDF;
}
.top-menu a {
    color: #000000;
    height: 1px;
    pointer: cursor;
}
.top-menu a:hover {
    color: #000000;
    background: #000;
    height: 1px;
    cursor: pointer;
}
#page-tab li {
    border: 1px solid #ECECEC;
    background: white;
}
#page-tab .selected a,
#page-tab .selected a:hover {
    background: white;
    color: rgb(74, 74, 74);
}
#page-tab a {
    background: #FFFFFF;
}
.button.small-button {
    background: #fff;
    border: 1px solid #D0D0D0;
}
#menu-tab ul {
    background: #FFFFFF;
    border: 1px solid #FFFFFF;
    border-top: 0px;
}
#menu-tab li {
    background: #FCFCFC;
    border: 1px solid #F4F4F4;
}
#menu-tab a {
    background: #FFFFFF;
}
div.jp-volume-bar-value {
    background-color: #5E5E5E;
}
.tbox-title {
    background-color: #F3F3F3;
    padding: 2px 7px;
}
.tbox-wrapper {
    border: 1px solid #E8E8E8;
}
.searchFrame {
    border: 1px solid #E6E6E6;
    background: #FFFFFF;
}
.result {
    border-bottom: 1px solid #FFFFFF;
}
.result .row-0 {
    background: #FFFFFF;
}
.result .row-1 {
    background: #EFEFEF;
}
.result div:hover {
    background: #FFEEEE;
}
.transTip .tip-wrapper {
    border: 0px solid #FFF;
}
.transTip .tip-title {
    background-color: #F1F1F1;
    padding: 2px 10px;
}
::selection {
    background: #ffb7b7;
    /* WebKit/Blink Browsers */
}
::-moz-selection {
    background: #ffb7b7;
    /* Gecko Browsers */
}

.menu-body select {
    border: 1px solid #F3F3F3;
}

.menu-body input[type=text] {
    border: 1px solid #F3F3F3;
}
}
