css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.youtube.com") {
#ticker,#yt-consent-dialog,.yt-dialog-bg,iframe,div.yt-consent-banner{display:none!important;}
body.yt-dialog-active{height:unset; overflow:unset;}
}
@-moz-document url-prefix('https://www.google.') {
/*maps*/#consent-bump,
#lb,
#cnso,._V0h,#cnsw{display:none!important;}
html,body{overflow:unset!important;height:unset!important;}
}
@-moz-document url-prefix('https://translate.google.') {
#gb>.gb_g,
#gb>.gb_2c,
.gb_Xc{display:none!important;}
}
