css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("animeseason.com") {
@-moz-document regexp(".*-(episode|ova)-.*") {
div#container,div#main{width:1320px;}
div#right_col,div#content_wrapper{width:1320px;}
div#right_col,div#content{width:1320px;}
#content>.content_bloc{width:1300px;padding:0px;}
#video_player{width:1288px;}
#video_source,#video_source>iframe{width:1280px;height:780px;}
#header_sponsors,#right_wrapper,#feature_menu_wrapper,#hidem,#hides{display:none;}
div#must_watch_listing{width:unset;}
#must_watch_listing .must_watch_video{width:279px;}
#left_col{position:relative;left:427px;height:0px;top:-156px;margin-left:auto;margin-right:auto;float:unset!important;}
#header_animelist,#header_ongoing,#header_ongoing+ul.series{display:none}
}
#recommendations>#rec_scroll{height:auto;}
}
