css:
@-moz-document domain("g2a.com") {
#body_clicker {
	display: none !important;
}
body {
	background-attachment: fixed;
	background-position: center 0;
	background-size: cover;
}
.banner-shield,
.mp-head-giftcard,
.banner-shield-small,
#supportHubToolTip {
	display: none;
}
img[src="https://www.g2a.com/skin/frontend/gate/default/images/socializer.jpg"] {
	display: none;
}
#container2 {
	padding-bottom: 125px !important;
}
.partners-wrapper,
#foot-bottom {
	display: none;
}
#footer-wrap #footer {
	height: inherit;
}
#content-landing {
	background: RGBA(0, 0, 0, 0.2);
	margin: -1px auto 0px;
	position: relative;
	padding: 15px 14px 10px 15px;
	width: 969px;
	min-width: 969px;
	z-index: 1;
}
#footer-wrap {
	min-width: 998px;
	background: RGBA(0, 0, 0, 0.2);
	position: relative;
	zoom: 1;
}
#footer .footer-top-separator {
	background: rgba(0, 0, 0, 0);
	height: 5px;
	position: relative;
	margin-bottom: 40px;
}
.banner-fixed-footer {
	display: none;
}
.payment-method-icons li {
	display: none;
	height: 140px;
	border: 1px solid #000;
}
#footer-wrap #footer {
	border-bottom: RGBA(87, 87, 88, 0) 1px solid;
	margin: 0px;
	padding: 0 12px;
	width: 976px;
	position: relative;
}
.payment-method li.active {
	border-top: 1px solid #000;
	border-left: 1px solid #000;
	border-right: 1px solid #000;
	background-color: RGBA(0, 0, 0, 0.5);
	color: #fff;
}
.payment-method li {
	float: left;
	cursor: pointer;
	border-bottom: none;
	background-color: rgba(0, 0, 0, 0.14);
	height: 40px;
	line-height: 40px;
	width: 127px;
	text-align: center;
	font-weight: bold;
	font-size: 14px;
	color: #717274;
}
#footer-wrap #footer .foo-right .select_r2b .select_r {
	background: url(http://i.imgur.com/A9KcTMY.png);
	background-position: -342px -2px;
	border: none;
	color: #fff;
	height: 36px;
	font-size: 13px;
	width: 100px;
	position: relative;
	border-radius: 5px;
}
#footer-wrap #footer .foo-right .select_r2a .select_r {
	background: url(http://i.imgur.com/A9KcTMY.png);
	background-position: -263px -2px;
	border: none;
	color: #ffffff;
	height: 36px;
	font-size: 13px;
	width: 180px;
	position: relative;
	border-radius: 5px;
	margin-right: 10px;
}
#jq_filterloadmore {
	display: none;
	clear: both;
	background: #000;
	background: url(data:image/svg+xml;base64,);
	background: -moz-linear-gradient(top, #000 0%, #000 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #4c4f54), color-stop(100%, #3a3b3f));
	background: -webkit-linear-gradient(top, #4c4f54 0%, #3a3b3f 100%);
	background: -o-linear-gradient(top, #4c4f54 0%, #3a3b3f 100%);
	background: -ms-linear-gradient(top, #4c4f54 0%, #3a3b3f 100%);
	background: linear-gradient(to bottom, RGBA(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0.2) 100%);
	margin: 0 auto 8px;
	padding: 0px 5px;
	line-height: 60px;
	height: 58px;
	width: 955px;
	color: #fff;
	font-size: 18px;
	font-weight: bold;
	text-align: center;
	border: solid 1px #1A1C1F;
	border-radius: 0px;
	box-shadow: none;
}
.mp-front-menu > li {
	float: left;
	min-height: 66px;
	cursor: pointer;
	width: 321px;
	display: inline-block;
	position: relative;
	font-size: 16px;
	color: #fff;
	border: 1px solid #000;
	background: #000;
	background: -moz-linear-gradient(top, RGBA(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0.21) 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #4c4f54), color-stop(100%, #383b3e));
	background: -webkit-linear-gradient(top, #4c4f54 0%, #383b3e 100%);
	background: -o-linear-gradient(top, #4c4f54 0%, #383b3e 100%);
	background: -ms-linear-gradient(top, #4c4f54 0%, #383b3e 100%);
	background: linear-gradient(to bottom, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0.2) 100%);
	z-index: 10;
}
.mp-front {
	background-color: RGBA(0, 0, 0, 0);
}
.mp-front-range {
	display: block;
	width: 100%;
	background-color: RGBA(0, 0, 0, 0.2);
	height: 50px;
}
.mp-comunity,
.mp-freegames {
	float: right;
	margin-right: 10px;
	cursor: pointer;
	margin-top: 11px;
	height: 28px;
	background: #c09340;
	background: -moz-linear-gradient(top, rgba(255, 166, 0, 0.5) 0%, rgba(203, 134, 4, 0.5) 100%);
	background: -webkit-linear-gradient(top, #feb938 0%, #cb8604 100%);
	background: linear-gradient(to bottom, rgba(0, 0, 0, 0.5) 0%, rgba(0, 0, 0, 0.5) 100%);
	display: none;
	padding-right: 10px;
	border-radius: 0px;
	color: #ffffff;
	line-height: 29px;
	font-weight: bold;
	font-size: 12px;
}
.mp-range-max input {
	width: 83px;
	height: 36px;
	font-size: 14px;
	font-weight: bold;
	padding-left: 5px;
	padding-right: 5px;
	color: #30fbed;
	border: none;
	-webkit-border-radius: 3px;
	-moz-border-radius: 3px;
	border-radius: 3px;
	background: #18191b;
	background: -moz-linear-gradient(top, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0.2) 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #18191b), color-stop(100%, #18191b));
	background: -webkit-linear-gradient(top, #18191b 0%, #18191b 100%);
	background: -o-linear-gradient(top, #18191b 0%, #18191b 100%);
	background: -ms-linear-gradient(top, #18191b 0%, #18191b 100%);
	background: linear-gradient(to bottom, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0.2) 100%);
}
.mp-range-min input {
	width: 83px;
	height: 36px;
	border: none;
	background: #18191b;
	background: -moz-linear-gradient(top, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0.2) 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #18191b), color-stop(100%, #18191b));
	background: -webkit-linear-gradient(top, #18191b 0%, #18191b 100%);
	background: -o-linear-gradient(top, #18191b 0%, #18191b 100%);
	background: -ms-linear-gradient(top, #18191b 0%, #18191b 100%);
	background: linear-gradient(to bottom, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0.2) 100%);
	-webkit-border-radius: 3px;
	-moz-border-radius: 3px;
	border-radius: 3px;
	font-weight: bold;
	font-size: 14px;
	color: #30fbed;
	padding-left: 5px;
	padding-right: 5px;
}
#content {
	background: none;
	background: rgb(255, 255, 255);
	background: -moz-linear-gradient(left, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0.2) 100%);
	background: -webkit-gradient(linear, left top, right top, color-stop(0%, rgba(255, 255, 255, 1)), color-stop(100%, rgba(239, 239, 239, 1)));
	background: -webkit-linear-gradient(left, rgba(255, 255, 255, 1) 0%, rgba(239, 239, 239, 1) 100%);
	background: -o-linear-gradient(left, rgba(255, 255, 255, 1) 0%, rgba(239, 239, 239, 1) 100%);
	background: -ms-linear-gradient(left, rgba(255, 255, 255, 1) 0%, rgba(239, 239, 239, 1) 100%);
	background: linear-gradient(to right, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0.2) 100%);
}
#content .prod_name h1 {
	font-weight: normal;
	color: #fff;
	font-size: 31px;
}
.prod_associated_content {
	clear: both;
	background-color: RGBA(0, 0, 0, 0);
	overflow: auto;
	padding: 30px 13px;
}
.mp-item-bn {
	height: 56px;
	font-size: 24px;
	line-height: 56px;
	background: rgb(112, 179, 1);
	background: -moz-linear-gradient(top, rgba(112, 179, 1, 1) 1%, rgba(89, 161, 1, 1) 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(1%, rgba(112, 179, 1, 1)), color-stop(100%, rgba(89, 161, 1, 1)));
	background: -webkit-linear-gradient(top, rgba(112, 179, 1, 1) 1%, rgba(89, 161, 1, 1) 100%);
	background: -o-linear-gradient(top, rgba(112, 179, 1, 1) 1%, rgba(89, 161, 1, 1) 100%);
	background: -ms-linear-gradient(top, rgba(112, 179, 1, 1) 1%, rgba(89, 161, 1, 1) 100%);
	background: linear-gradient(to bottom, rgba(112, 179, 1, 1) 1%, rgba(112, 179, 1, 1) 100%);
	background: #7dbc00;
	background: -moz-linear-gradient(top, rgba(112, 179, 1, 1) 1%, rgba(112, 179, 1, 1) 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(1%, #7dbc00), color-stop(100%, #4a9400));
	background: -webkit-linear-gradient(top, #7dbc00 1%, #4a9400 100%);
	background: -o-linear-gradient(top, #7dbc00 1%, #4a9400 100%);
	background: -ms-linear-gradient(top, #7dbc00 1%, #4a9400 100%);
	background: linear-gradient(to bottom, rgba(112, 179, 1, 1) 1%, rgba(112, 179, 1, 1) 100%);
	width: 100%;
	height: 53px;
	-webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.28);
	-moz-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.28);
	box-shadow: 0px 1px 2px rgba(0, 0, 0, 0);
	border: none;
	-webkit-border-radius: 4px;
	-moz-border-radius: 4px;
	border-radius: 0px;
	font-weight: bold;
	margin-top: 7px;
	cursor: pointer;
	font-size: 20px;
	text-shadow: 0px 2px 2px rgba(0, 0, 0, 0);
	color: #fff;
}
#content .product_f .games-price button.btn-marketplace-items {
	height: 38px;
	width: 100%;
	display: block;
	font-size: 14px;
	line-height: normal;
	-webkit-border-radius: 0px;
	-moz-border-radius: 4px;
	border-radius: 0px;
	background: rgb(255, 119, 51);
	background: -moz-linear-gradient(top, rgba(255, 119, 51, 1) 2%, rgba(255, 119, 51, 1) 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(2%, rgba(255, 119, 51, 1)), color-stop(100%, rgba(255, 106, 50, 1)));
	background: -webkit-linear-gradient(top, rgba(255, 119, 51, 1) 2%, rgba(255, 106, 50, 1) 100%);
	background: -o-linear-gradient(top, rgba(255, 119, 51, 1) 2%, rgba(255, 106, 50, 1) 100%);
	background: -ms-linear-gradient(top, rgba(255, 119, 51, 1) 2%, rgba(255, 106, 50, 1) 100%);
	background: linear-gradient(to bottom, rgba(255, 119, 51, 1) 2%, rgba(255, 119, 51, 1) 100%);
}
/* Removing the body clicker */

body {
	background-color: rgb(30, 31, 32) !important;
}
#body_clicker {
	display: none !important;
}
#container2 {
	padding: 3% !important;
}
/* Header annoyances */

.mp-head-goldmine,
.mp-head-stc,
.mp-head-giftcard-center {
	display: none !important;
}
.mp-head-giftcard.active {
	height: 0px !important;
}
/* We don't need the footer */

#footer-wrap #footer3,
#footer-wrap,
#content-tabs > img {
	display: none !important;
}
.widget-tp-reviews {
	display: none !important;
}
/* Annoying and misleading */

.product_f-right {
	display: none !important;
}
.product_f-left {
	margin-left: 135px;
	margin-right: 150px;
}
.product_f {
	background: rgb(243, 243, 243);
}
/* More self-promotion annoyances */

.banner-shield,
.banner-shield-small,
#supportHubToolTip {
	display: none !important;
}
}
