.wfcolor-green, .wfcolor-teal, .wfcolor-aqua, .wfcolor-blue, .wfcolor-navy,
.wfcolor-fuchsia, .wfcolor-purple, .wfcolor-maroon {
	color: black !important; 
    background-color: transparent !important;
}

