css:
@-moz-document domain("slp-info.sec.samsung.net") {
/*	gitiles:*/
/*		tree page:*/
/*			list files section:*/

li.git-tree a {
    color: #678CB1 !important;
}
html {
    background-color: #234;
}
/*		commit page:*/
/*			commit header:*/

.git-commit,
.git-tag {
    background-color: #345;
    border: 0px;
}
/*				item name:*/

.git-commit,
.git-tag {
    color: #789;
}
/*				commit, author, commiter value:*/

.git-commit table td,
select {
    color: #567 !important;
}
.git-commit table tbody tr td a {
    color: #da6 !important;
}
/*			commit section:*/

pre.commit-message,
pre.tag-message {
    color: #9ab;
    background-color: #456;
    border: 0px;
}
/*			tree section:*/

ul.diff-tree li a {
    color: #da6;
}
/*			list files section:*/

.sha1 {
    color: #456;
}
ol.list a {
    color: #da6;
}
/*	project page:	*/

.projectFilterPanel input {
    background: #345;
    border: 1px solid #555;
    color: #DA6;
}
/*	review page:*/
/*		Header Sections:	*/

.infoBlock td {
    color: #567;
}
/*		Commit Sections:	*/

.GKSE20JDA4 {
    color: #789;
    background: #345;
}
.GKSE20JDP3 {
    background: #456;
    border: 0px solid #000;
    color: #9ab;
}
/*		Reviewer Sections:	*/

.infoTable td.header {
    background: #567;
    color: #9ab;
}
.infoTable td {
    border: 0px solid #000;
    background: #345;
}
.infoTable td.notVotable {
    background: #567;
}
.infoTable,
.infoBlock {
    border: 0px solid #000;
}
.addMemberTextBox {
    background: #345;
    color: #DA6;
    border: 1px solid #555
}
.missingApproval {
    color: #456;
}
/*		dependencies:*/

.changeTable .leftMostCell {
    background: #456 !important;
}
.changeTable .outdated {
    background: #345 !important;
}
.changeTable a.gwt-InlineHyperlink {
    color: #234 !important;
}
.changeTable .sectionHeader {
    background: #456;
    color: #789;
    border: 0px solid #000;
    padding-top: 5px;
    padding-bottom: 5px;
}
.changeTable .emptySection {
    border: 0px solid #000;
}
/*		Reference Version:*/

body,
table td,
select {
    color: #BD82A0;
}
.gwt-DisclosurePanel .header,
.gwt-DisclosurePanel .header a,
.gwt-DisclosurePanel .header td {
    color: #678CB1;
}
body table td select.gwt-ListBox {
    background: #345;
    border: 1px solid #555;
    color: #DA6;
}
span.gwt-InlineLabel {
    color: #678;
}
/*		Download:*/

.downloadLinkHeader {
    background: #345;
    border: 0px solid #000;
}
a.downloadLink {
    background: #345;
    color: #DA6;
    border: 0px solid #345;
    border-bottom: 1px solid #999;
    margin-right: 5px;
}
a.downloadLink:hover {
    background: #567;
    color: #DA6;
    border: 0px solid #345;
}
/*		Buttons:*/

button.gwt-Button {
    background: #345;
    border: 0px solid #000;
    border-bottom: 1px solid #666;
    color: #DA6;
}
button.gwt-Button:hover {
    background: #567;
    border: 0px solid #000;
    color: #DA6;
}
/*		Comments:*/

.commentPanelBorder {
    border: 0px solid #000;
    background: #345;
    margin: 5px;
}
.commentPanelAuthorCell {
    color: #567;
}
/*		Patchset sections:	*/

.infoBlock td.topmost {
    border: 0px solid #000;
}
.infoBlock td {
    border: 0px solid #000;
}
/*		Comments:	*/

.commentPanelSummary {
    color: #abc;
}
.commentPanelBorder.commentPanelLast {
    border: 0px solid #000;
}
/*	Frames:	*/

body,
.gwt-DialogBox .dialogMiddleCenter {
    background: #234;
    color: #eff
}
.patchContentTable .wdd {
    background: #510
}
a,
a:visited {
    color: #da6
}
.gwt-TabBar .gwt-TabBarItem {
    color: #BD82A0
}
.patchContentTable td.fileColumnHeader {
    background: #234;
    color: #678CB1;
}
.GKSE20JDFFB>a {
    background: transparent;
}
.unifiedTable,
.sideBySideScreenSideBySideTable {
    border: 0px;
}
.lineNumber.fileColumnHeader {
    border: 0px;
}
/*	Editor sections:	*/

table.patchContentTable {
    border-bottom: 0px solid #000;
}
.infoBlock td.header {
    background: #345;
    color: #678CB1;
    border: 0px solid #000;
}
.gwt-DisclosurePanel .content {
    border-left: 3px solid #456;
}
.blockHeader {
    background: #345
}
.changeTable tr:nth-child(even) {
    border: 0px solid #000;
}
.changeTable .dataCell {
    border: 0px solid #000;
}
.changeTable .changeTypeCell {
    border: 0px solid #000;
}
.changeTable tr:nth-child(odd) {
    background: #345;
    border: 0px solid #000;
}
.changeTable .leftMostCell {
    background: #567 !important;
    border: 0px solid #000;
}
.changeTable .activeRow,
.accountDashboard.changeTable .activeRow,
.accountDashboard.changeTable .activeRow a {
    background: #234 !important;
    border: 0px solid #000;
    color: #BD82A0;
}
.changeTable a.gwt-InlineHyperlink {
    color: #DA6 !important;
}
.changeTable .iconHeader {
    background: #345;
    border: 0px solid #000;
}
.changeTable .dataHeader {
    background: #345;
    color: #678CB1;
    border: 0px solid #000;
}
td.fileLine {
    background: #252525;
    border: 0px;
    border-bottom: 0px solid #000;
}
.patchContentTable .activeRow .fileLine,
.patchContentTable .activeRow .diffText,
.patchContentTable .activeRow td.commentHolder,
.patchContentTable .activeRow .wdc,
.patchContentTable .activeRow .wdd,
.patchContentTable .activeRow .wdi,
.patchContentTable .activeRow .iconCellOfFileCommentRow,
.patchContentTable .activeRow td.commentHolder.commentPanelLast {
    border-bottom: 0px;
}
.patchContentTable .skipLine .iconCell,
.patchContentTable .skipLine {
    background: #345;
    color: #eff;
}
.patchContentTable a.skipLine {
    color: #da6;
    border-bottom: 0px solid #000;
}
.wdi {
    background: #373;
    border-bottom: 0px solid #000;
}
.fileLineINSERT,
.fileLineINSERT .wdc {
    background: #242;
    border-bottom: 0px solid #000;
}
.patchContentTable .wdi {
    border-bottom: 0px solid #000;
}
.patchContentTable .wdd {
    border-bottom: 0px solid #000;
}
.fileLineDELETE,
.fileLineDELETE .wdc {
    background: #644;
    border-bottom: 0px solid #000;
}
.wdd {
    background: #955;
    border-bottom: 0px solid #000;
}
td.fileLine.wdi {
    background: #373;
    border: 0px;
    border-bottom: 0px solid #000;
}
tr.activeRow td.fileLine {
    background: #3B3A30;
    border: 0px;
    border-bottom: 0px solid #000;
}
tr.activeRow td.fileLine.wdi {
    background: #6a7;
    border: 0px;
}
td.fileLine.fileLineCONTEXT {
    border: 0px;
    border-bottom: 0px solid #000;
}
.com {
    color: #BD82A0;
}
.pln {
    color: #F3F2F1;
}
.pun {
    color: #B7E2E8;
}
.atv {
    color: #EC7600;
}
.str {
    color: #EC7600;
}
.atn {
    color: #678CB1;
}
.typ {
    color: #678CB1;
}
.lit {
    color: #CDFF22
}
.kwd {
    color: #93C763
}
.tag {
    color: #93C763
}
.vt {
    color: #999;
}
.vt .pln {
    color: #999;
    font-size: 0px;
}
.vt .pun {
    color: #999;
    font-size: 0px;
}
}
