css:
@-moz-document domain("peperonity.com") {
body{
    background-image: url(https://s-media-cache-ak0.pinimg.com/736x/16/56/df/1656df78e4e4b2b53f52d3b3965829e6.jpg) ; 
    color: red ; background-color: transparent; 
    background-attachment: fixed !important; 
    background-repeat: repeat-x !important; 
    background-size: 45% 100% !important; 
}

.background1 {
    background-image: 
        url(https://s-media-cache-ak0.pinimg.com/736x/16/56/df/1656df78e4e4b2b53f52d3b3965829e6.jpg) ; 
    color: whitesmoke ; 
    background-color: transparent; 
    background-attachment: fixed !important; 
    background-repeat: repeat-x !important; 
    background-size: 45% 100% !important; 
}

.background2 {
    background-color: rgba(0,0,0,.5); 
}

table, tbody, tr, td {
    border: 1px solid transparent; 
}

a:active, a:link, a:visited {
    color: #FB0C86; 
    text-decoration: underline;
}

.con-Box-Borders {
    border-left-width: 1px;
    border-left-style: solid;
    border-right-width: 1px;
    border-right-style: solid;
    background: transparent;
    background-repeat: no-repeat;
}

textarea {
    -webkit-appearance: textarea;
    background-color: rgba(0,0,0,.5);
    -webkit-rtl-ordering: logical;
    -webkit-user-select: text;
    flex-direction: column;
    resize: auto;
    cursor: auto;
    white-space: pre-wrap;
    word-wrap: break-word;
    border: 1px solid;
    padding: 2px;
    color: aqua;
}

.colorreplacements {
    background-color: rgba(0,0,0,.5);
    color: aqua;
}

.colorreplacements a:link, .colorreplacements a:visited, .colorreplacements a:hover, .colorreplacements a:active {
    color: #FB0C86;
}

.MyArea-Top {
    background: transparent;
}

.bgcolor_myarea {
    background: rgba(0,0,0,.6);
}

.bg-boxes {
    background-image: none;
}

.linkBlack1 a:hover {
    text-decoration: none;
    color: #FB0C86;
}

.linkBlack1 a:active, .linkBlack1 a:visited {
    color: #6257EF;
    text-decoration: underline;
}

.glowingtabs a {
    background: none;
}

.glowingtabs a span {
    background: none;
}

.glowingtabs a.current span {
    background-position: 100% -22px;
    color: #FB0C86;
}

.bgcolor_grey4 {
    background: rgba(240, 240, 240, 0);
}

.linkBlack1 {
    font-size: 11px;
    line-height: 16px;
    color: grey;
}

.bgcolor_grey3 {
    background: transparent;
}

.linkBlack1 {
    font-size: 11px;
    line-height: 16px;
    color: rgba(0,0,0,.6);
}

.bgcolor_grey5 {
    background: transparent;
}

input {
    -webkit-appearance: textfield;
    background-color: transparent;
    -webkit-rtl-ordering: logical;
    -webkit-user-select: text;
    cursor: auto;
    padding: 1px;
    border: 2px inset;
    color: whitesmoke
}

.bgcolor_grey2 {
    background: rgba(0,0,0,.6);
}

.bgcolor_grey1 {
    background: transparent;
}

.bg-boxes2 {
    background-image: none;
}

.form-default {
    color: #FB0C86;
}
}
