css:
@-moz-document domain("5headgaming.com") {
.bam_announcement.green {
    background: #7DDF00 !important;
}
.popup_menu .popup_item:hover {
    background: none;
    color: #44872B !important
}
.popup_menu .popup_item_container {
    transition: all 0.2s ease 0s;
    padding: 0px;
    text-align: left;
}
.customrep img {
    vertical-align: top !important;;
}
.fullpal, .fullpal>img {
    border-radius: 0%;
    margin-right: 5px;
    padding: 0;
}
.bam_date {
    color: #7DDF00 !important;
    font-size: 0em !important;
    margin-left: 6px !important;
}
#content {
    width: auto !important;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 5px;
    padding-bottom: 40px;
    overflow: hidden;
    background: rgb(236,238,240);
}
.team_colour a:hover {
    color: #267F00
}
.modal {
    display: none;
    width: 400px;
    text-align: left;
    background: rgba(255, 255, 255, 0);
}
.post_content .signature img {
    width: auto;
    max-width: 100%;
    height: auto;
    max-height: 300px;
    display: block;
    margin: 0 auto;
}
.server {
    height: 16px;
    line-height: 16px;
    background: rgba(206, 154, 38, 0) !important;
    padding: 0 5px;
    font-size: 9px;
    font-weight: bold;
    text-transform: uppercase;
    color: #19B4C3;
    text-shadow: rgba(0,0,0,0.2) 0px -1px 0px;
    text-decoration: underline;
}
.servernumber {
    background: rgba(255, 255, 255, 0);
    color: black;
    font-size: small;
    text-decoration: none !important;
}
.serveroffline {
    background: rgba(255, 0, 0, 0);
    color: red;
}
.serveronline {
    background: rgba(0, 128, 0, 0);
    color: green;
}
.bradius-full {
    border-radius: 5% !important;
    -moz-border-radius: 5% !important;
    -webkit-border-radius: 5% !important;
    -o-border-radius: 5% !important;
    -ms-border-radius: 5% !important;
}
#backtop:hover {
    background: rgba(52, 175, 0, .57)
}
.pmbg:hover {
    background: rgba(52, 175, 0, .57)
}
.bradius-fifty {
    border-radius: 5% !important;
    -moz-border-radius: 5% !important;
    -webkit-border-radius: 5% !important;
    -o-border-radius: 5% !important;
    -ms-border-radius: 5% !important;
}
.post .post_author div.author_statistics_default {
    background: #eee;
    float: right;
    font-size: 12px;
    padding: 11px 18px 6px 16px;
    color: #666;
    font-family: "Open Sans",sans-serif,arial;
    line-height: 1.4;
    border-radius: 15px;
    -moz-border-radius: 15px;
    -webkit-border-radius: 15px;
    -o-border-radius: 15px;
    -ms-border-radius: 15px;
}
.post {
    background: #f9f9f9;
    overflow: hidden;
    margin-bottom: 8px;
    border: 1px solid rgba(65, 210, 62, 0.22);
    border-radius: 15px;
    -moz-border-radius: 15px;
    -webkit-border-radius: 15px;
    -o-border-radius: 15px;
    -ms-border-radius: 15px;
}
.tborder tbody tr:last-child td:last-child {
    -moz-border-radius-bottomright: 15px;
    -webkit-border-bottom-right-radius: 15px;
    -o-border-bottom-right-radius: 15px;
    -ms-border-bottom-right-radius: 15px;
    border-bottom-right-radius: 15px;
}
.tborder tbody tr:last-child td:first-child {
    -moz-border-radius-bottomleft: 2px;
    -webkit-border-bottom-left-radius: 2px;
    -o-border-bottom-left-radius: 2px;
    -ms-border-bottom-left-radius: 2px;
    border-bottom-left-radius: 15px;
}
button, input.button, input.textbox, input.invalid_field, input.valid_field, select, textarea, .editor_control_bar, blockquote, .codeblock, fieldset, .pm_alert, .red_alert, .popup_menu, .postbit_buttons > a, a.button {
    border-radius: 15px;
    -moz-border-radius: 15px;
    -webkit-border-radius: 15px;
    -o-border-radius: 15px;
    -ms-border-radius: 15px;
}
.bradius {
    border-radius: 15px !important;
    -moz-border-radius: 15px !important;
    -webkit-border-radius: 15px !important;
    -o-border-radius: 15px !important;
    -ms-border-radius: 15px !important;
}
.post .post_author div.author_avatar img {
    background: none;
    margin: 0px;
    padding: 0px;
    height: 100px !important;
    width: 100px !important;
    border-radius: 5%;
    -webkit-border-radius: 5%;
    -moz-border-radius: 5%;
    -o-border-radius: 5%;
    -ms-border-radius: 5%;
    border: 5px solid #eee;
}
.thead_2 {
    background: #3c3c3c;
    color: #fff;
    font-family: Sans-Serif,Arial,"Open Sans";
    font-size: 14px;
    font-weight: 500;
    padding: 1.1em 1.325em 1em 0.975em;
    -moz-border-radius-topleft: 15px;
    -moz-border-radius-topright: 15px;
    -webkit-border-top-left-radius: 15px;
    -webkit-border-top-right-radius: 15px;
    -o-border-top-left-radius: 15px;
    -o-border-top-right-radius: 15px;
    -ms-border-top-left-radius: 15px;
    -ms-border-top-right-radius: 15px;
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
}
.tborder {
    -moz-border-radius: 15px;
    -webkit-border-radius: 15px;
    -o-border-radius: 15px;
    -ms-border-radius: 15px;
    border-radius: 15px;
}
.bam_announcement {
    text-align: center;
    margin: 10px auto;
    padding: 8px 12px;
    background: #EBEBEB;
    color: #000000;
    -moz-border-radius: 15px;
    -webkit-border-radius: 15px;
    -o-border-radius: 15px;
    -ms-border-radius: 15px;
    border-radius: 15px;
}
.thead {
    -moz-border-radius-topleft: 15px;
    -moz-border-radius-topright: 15px;
    -webkit-border-top-left-radius: 15px;
    -webkit-border-top-right-radius: 15px;
    -o-border-top-left-radius: 15px;
    -o-border-top-right-radius: 15px;
    -ms-border-top-left-radius: 15px;
    -ms-border-top-right-radius: 15px;
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
}
#backtop {
    position: fixed;
    margin: 0px;
    cursor: pointer;
    color: #fff;
    font-size: 17px;
    right: 35px;
    bottom: 25px;
    z-index: 72;
    padding: 1em 1.4em 1em 1.2em;
    transition: background-color 0.3s;
    -webkit-transition: background-color 0.3s;
    -moz-transition: background-color 0.3s;
    -o-transition: background-color 0.3s;
    -ms-transition: background-color 0.3s;
    background: rgba(27, 195, 94, 0.35);
    border-radius: 10px;
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
    -o-border-radius: 10px;
    -ms-border-radius: 10px;
}
.pmbg {
    background: #125D1B;
    display: inline-block;
    margin-top: -1px;
    margin-left: 2px;
    padding: 1px 9px 3px 8px;
    max-width: 220px;
    text-align: center;
    border-radius: 100% !important;
    -moz-border-radius: 100% !important;
    -webkit-border-radius: 100% !important;
    -o-border-radius: 100% !important;
    -ms-border-radius: 100% !important;
}
.pmbar a:hover {
    color: #267F00
}
#logo {
    background: #eceef0 url(http://i.imgur.com/gVGe5U0.png) no-repeat 0% 5%;
    background-size: cover;
    -webkit-background-size: cover;
    -o-background-size: cover;
    -ms-background-size: cover;
    padding: 4em 0px 15.725em 0px;
}
a:link {
    transition: all 0.25s ease !important;
    -webkit-transition: all 0.25s ease !important;
    -moz-transition: all 0.25s ease !important;
    -o-transition: all 0.25s ease !important;
    -ms-transition: all 0.25s ease !important;
    color: #267F00;
    text-decoration: none
}
a:visited {
    color: #267F00;
    text-decoration: none
}
#panel .upper a.login,
#panel .upper a.lost_password {
    padding: 9px 14px 8px 12px;
    margin-left: 25px;
    font-weight: 300;
    background: #267F00
}
#panel .upper a:hover {
    color: #267F00;
    text-decoration: none
}
#footer .uppermid {
    overflow: hidden;
    padding: 2.5em 0px 2.8em;
    color: #fff;
    font-size: 13px;
    background: #267F00
}
#debug a:link {
    color: #267F00
}
.forum_status i {
    display: inline-block;
    line-height: 50px;
    color: #267F00
}
.forum_on {
    color: #267F00
}
.tcat {
    background: #267F00;
    font-size: 12px;
    color: #fff;
    padding: 10px 10px 9px
}
.trow1 a:link {
    color: #267F00
}
.trow1 a:visited {
    color: #267F00
}
.trow2 a:link {
    color: #267F00
}
.trow2 a:visited {
    color: #267F00
}
.navigation {
    margin-bottom: 15px;
    color: #fff;
    border-radius: 15px;
    -webkit-border-radius: 15px;
    -moz-border-radius: 15px;
    -o-border-radius: 15px;
    -ms-border-radius: 15px;
    font-family: "Open Sans", Sans-Serif, Arial;
    font-size: 13px;
    background: #267F00;
    padding: 1.175em 1.175em 1.075em 1.275em
}
.thread_tools a:hover {
    color: #267F00;
    text-decoration: none
}
.postbit_buttons > a:link,
.postbit_buttons > a:visited,
.postbit_buttons > a:active {
    display: inline-block;
    color: #267F00;
    padding: 7px 16px 7px 14px;
    margin: 5px 2px 2px;
    font-size: 12px;
    border: 1px solid #267F00
}
.postbit_buttons > a:hover {
    background: #267F00;
    color: #fff;
    border: 1px solid #267F00
}
a.button:link,
a.button:visited,
a.button:active {
    background: #267F00;
    color: #fff;
    font-size: 14px;
    display: inline-block;
    margin: 2px 0px 6px;
    padding: 9px 19px 9px 14px
}
.jGrowl-notification {
    background-color: #267F00;
    opacity: 0.75;
    zoom: 1;
    width: 250px;
    padding: 1em;
    margin: 10px;
    text-align: left;
    display: none;
    word-break: break-all;
    min-height: 40px
}
#footer .lower a:link {
    color: #267F00;
}
}
