css:
@-moz-document domain("5headgaming.com") {
.team_colour a:hover {
    color: /*[[customcolour]]*/
}
.fullpal, .fullpal>img {
    border-radius: /*[[avatarset]]*/;
    margin-right: 5px;
    padding: 0;
}
.bradius-full {
    border-radius: /*[[avatarset]]*/ !important;
    -moz-border-radius: /*[[avatarset]]*/ !important;
    -webkit-border-radius: /*[[avatarset]]*/ !important;
    -o-border-radius: /*[[avatarset]]*/ !important;
    -ms-border-radius: /*[[avatarset]]*/ !important;
}
.pmbg {
    background: /*[[customcolour]]*/;
    display: inline-block;
    margin-top: -1px;
    margin-left: 2px;
    padding: 1px 9px 3px 8px;
    max-width: 220px;
    text-align: center;
    border-radius: 100% !important;
    -moz-border-radius: 100% !important;
    -webkit-border-radius: 100% !important;
    -o-border-radius: 100% !important;
    -ms-border-radius: 100% !important;
}
#panel .upper {
    background: /*[[customcolour2]]*/;
    color: #fff;
    clear: both;
    padding: 1.7em 0px 1.8em;
}
#backtop:hover {
    background: /*[[customcolour2]]*/
}
.bradius-fifty {
    border-radius: /*[[avatarset]]*/ !important;
    -moz-border-radius: /*[[avatarset]]*/ !important;
    -webkit-border-radius: /*[[avatarset]]*/ !important;
    -o-border-radius: /*[[avatarset]]*/ !important;
    -ms-border-radius: /*[[avatarset]]*/ !important;
}
.post .post_author div.author_statistics_default {
    background: #eee;
    float: right;
    font-size: 12px;
    padding: 11px 18px 6px 16px;
    color: #666;
    font-family: "Open Sans",sans-serif,arial;
    line-height: 1.4;
    border-radius: /*[[curvededges]]*/;
    -moz-border-radius: /*[[curvededges]]*/;
    -webkit-border-radius: /*[[curvededges]]*/;
    -o-border-radius: /*[[curvededges]]*/;
    -ms-border-radius: /*[[curvededges]]*/;
}
.post {
    background: #f9f9f9;
    overflow: hidden;
    margin-bottom: 8px;
    border: 1px solid rgba(44,130,201,.22);
    border-radius: /*[[curvededges]]*/;
    -moz-border-radius: /*[[curvededges]]*/;
    -webkit-border-radius: /*[[curvededges]]*/;
    -o-border-radius: /*[[curvededges]]*/;
    -ms-border-radius: /*[[curvededges]]*/;
}
.tborder tbody tr:last-child td:last-child {
    -moz-border-radius-bottomright: /*[[curvededges]]*/;
    -webkit-border-bottom-right-radius: /*[[curvededges]]*/;
    -o-border-bottom-right-radius: /*[[curvededges]]*/;
    -ms-border-bottom-right-radius: /*[[curvededges]]*/;
    border-bottom-right-radius: /*[[curvededges]]*/;
}
.tborder tbody tr:last-child td:first-child {
    -moz-border-radius-bottomleft: /*[[curvededges]]*/;
    -webkit-border-bottom-left-radius: /*[[curvededges]]*/;
    -o-border-bottom-left-radius: /*[[curvededges]]*/;
    -ms-border-bottom-left-radius: /*[[curvededges]]*/;
    border-bottom-left-radius: /*[[curvededges]]*/;
}
button, input.button, input.textbox, input.invalid_field, input.valid_field, select, textarea, .editor_control_bar, blockquote, .codeblock, fieldset, .pm_alert, .red_alert, .popup_menu, .postbit_buttons > a, a.button {
    border-radius: /*[[curvededges]]*/;
    -moz-border-radius: /*[[curvededges]]*/;
    -webkit-border-radius: /*[[curvededges]]*/;
    -o-border-radius: /*[[curvededges]]*/;
    -ms-border-radius: /*[[curvededges]]*/;
}
.bradius {
    border-radius: /*[[curvededges]]*/ !important;
    -moz-border-radius: /*[[curvededges]]*/ !important;
    -webkit-border-radius: /*[[curvededges]]*/ !important;
    -o-border-radius: /*[[curvededges]]*/ !important;
    -ms-border-radius: /*[[curvededges]]*/ !important;
}
.post .post_author div.author_avatar img {
    background: none;
    margin: 0px;
    padding: 0px;
    height: 100px !important;
    width: 100px !important;
    border-radius: 5%;
    -webkit-border-radius: /*[[avatarset]]*/;
    -moz-border-radius: /*[[avatarset]]*/;
    -o-border-radius: /*[[avatarset]]*/;
    -ms-border-radius: /*[[avatarset]]*/;
    border: 5px solid #eee;
}
.thead_2 {
    background: /*[[customcolour2]]*/;
    color: #fff;
    font-family: Sans-Serif,Arial,"Open Sans";
    font-size: 14px;
    font-weight: 500;
    padding: 1.1em 1.325em 1em 0.975em;
    -moz-border-radius-topleft: /*[[curvededges]]*/;
    -moz-border-radius-topright: /*[[curvededges]]*/;
    -webkit-border-top-left-radius: /*[[curvededges]]*/;
    -webkit-border-top-right-radius: /*[[curvededges]]*/;
    -o-border-top-left-radius: /*[[curvededges]]*/;
    -o-border-top-right-radius: /*[[curvededges]]*/;
    -ms-border-top-left-radius: /*[[curvededges]]*/;
    -ms-border-top-right-radius: /*[[curvededges]]*/;
    border-top-left-radius: /*[[curvededges]]*/;
    border-top-right-radius: /*[[curvededges]]*/;
}
.tborder {
    -moz-border-radius: /*[[curvededges]]*/;
    -webkit-border-radius: /*[[curvededges]]*/;
    -o-border-radius: /*[[curvededges]]*/;
    -ms-border-radius: /*[[curvededges]]*/;
    border-radius: /*[[curvededges]]*/;
}
.bam_announcement {
    text-align: center;
    margin: 10px auto;
    padding: 8px 12px;
    background: #EBEBEB;
    color: #000000;
    -moz-border-radius: /*[[curvededges]]*/;
    -webkit-border-radius: /*[[curvededges]]*/;
    -o-border-radius: /*[[curvededges]]*/;
    -ms-border-radius: /*[[curvededges]]*/;
    border-radius: /*[[curvededges]]*/;
}
.thead {
    background: /*[[customcolour2]]*/;
    -moz-border-radius-topleft: /*[[curvededges]]*/;
    -moz-border-radius-topright: /*[[curvededges]]*/;
    -webkit-border-top-left-radius: /*[[curvededges]]*/;
    -webkit-border-top-right-radius: /*[[curvededges]]*/;
    -o-border-top-left-radius: /*[[curvededges]]*/;
    -o-border-top-right-radius: /*[[curvededges]]*/;
    -ms-border-top-left-radius: /*[[curvededges]]*/;
    -ms-border-top-right-radius: /*[[curvededges]]*/;
    border-top-left-radius: /*[[curvededges]]*/;
    border-top-right-radius: /*[[curvededges]]*/;
}
#backtop {
    position: fixed;
    margin: 0px;
    cursor: pointer;
    color: #fff;
    font-size: 17px;
    right: 35px;
    bottom: 25px;
    z-index: 72;
    padding: 1em 1.4em 1em 1.2em;
    transition: background-color 0.3s;
    -webkit-transition: background-color 0.3s;
    -moz-transition: background-color 0.3s;
    -o-transition: background-color 0.3s;
    -ms-transition: background-color 0.3s;
    background: /*[[customcolour]]*/;
    border-radius: 10px;
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
    -o-border-radius: 10px;
    -ms-border-radius: 10px;
}
.pmbar a:hover {
    color: /*[[customcolour]]*/
}
#logo {
    background: #eceef0 url(/*[[custombanner]]*/) no-repeat 0% 5%;
    background-size: cover;
    -webkit-background-size: cover;
    -o-background-size: cover;
    -ms-background-size: cover;
    padding: 4em 0px 15.725em 0px;
}
a:link {
    transition: all 0.25s ease !important;
    -webkit-transition: all 0.25s ease !important;
    -moz-transition: all 0.25s ease !important;
    -o-transition: all 0.25s ease !important;
    -ms-transition: all 0.25s ease !important;
    color: /*[[customcolour]]*/;
    text-decoration: none
}
a:visited {
    color: /*[[customcolour]]*/;
    text-decoration: none
}
#panel .upper a.login,
#panel .upper a.lost_password {
    padding: 9px 14px 8px 12px;
    margin-left: 25px;
    font-weight: 300;
    background: /*[[customcolour]]*/
}
#panel .upper a:hover {
    color: /*[[customcolour]]*/;
    text-decoration: none
}
#footer .uppermid {
    overflow: hidden;
    padding: 2.5em 0px 2.8em;
    color: #fff;
    font-size: 13px;
    background: /*[[customcolour]]*/
}
#footer .lower a:link {
    color: /*[[customcolour]]*/
}
#debug a:link {
    color: /*[[customcolour]]*/
}
.forum_status i {
    display: inline-block;
    line-height: 50px;
    color: /*[[customcolour]]*/
}
.forum_on {
    color: /*[[customcolour]]*/
}
.tcat {
    background: /*[[customcolour]]*/;
    font-size: 12px;
    color: #fff;
    padding: 10px 10px 9px
}
.trow1 a:link {
    color: /*[[customcolour]]*/
}
.trow1 a:visited {
    color: /*[[customcolour]]*/
}
.trow2 a:link {
    color: /*[[customcolour]]*/
}
.trow2 a:visited {
    color: /*[[customcolour]]*/
}
.navigation {
    margin-bottom: /*[[curvededges]]*/;
    color: #fff;
    border-radius: /*[[curvededges]]*/;
    -webkit-border-radius: /*[[curvededges]]*/;
    -moz-border-radius: /*[[curvededges]]*/;
    -o-border-radius: /*[[curvededges]]*/;
    -ms-border-radius: /*[[curvededges]]*/;
    font-family: "Open Sans", Sans-Serif, Arial;
    font-size: 13px;
    background: /*[[customcolour]]*/;
    padding: 1.175em 1.175em 1.075em 1.275em
}
.thread_tools a:hover {
    color: /*[[customcolour]]*/;
    text-decoration: none
}
.postbit_buttons > a:link,
.postbit_buttons > a:visited,
.postbit_buttons > a:active {
    display: inline-block;
    color: /*[[customcolour]]*/;
    padding: 7px 16px 7px 14px;
    margin: 5px 2px 2px;
    font-size: 12px;
    border: 1px solid /*[[customcolour]]*/
}
.postbit_buttons > a:hover {
    background: /*[[customcolour]]*/;
    color: #fff;
    border: 1px solid /*[[customcolour]]*/
}
a.button:link,
a.button:visited,
a.button:active {
    background: /*[[customcolour]]*/;
    color: #fff;
    font-size: 14px;
    display: inline-block;
    margin: 2px 0px 6px;
    padding: 9px 19px 9px 14px
}
.jGrowl-notification {
    background-color: /*[[customcolour]]*/;
    opacity: 0.75;
    zoom: 1;
    width: 250px;
    padding: 1em;
    margin: 10px;
    text-align: left;
    display: none;
    word-break: break-all;
    min-height: 40px
}
}
