css:
@-moz-document domain("www.conjur.com.br") {
/* latin-ext */

@font-face {
	font-family: 'Playfair Display';
	font-style: normal;
	font-weight: 400;
	src: local('Playfair Display'), local('PlayfairDisplay-Regular'), url(https://fonts.gstatic.com/s/playfairdisplay/v10/2NBgzUtEeyB-Xtpr9bm1CfoVn-aGdXvQRwgLLg-TkDk.woff2) format('woff2');
	unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */

@font-face {
	font-family: 'Playfair Display';
	font-style: normal;
	font-weight: 400;
	src: local('Playfair Display'), local('PlayfairDisplay-Regular'), url(https://fonts.gstatic.com/s/playfairdisplay/v10/2NBgzUtEeyB-Xtpr9bm1Cdhy5e3cTyNKTHXrP9DO-Rc.woff2) format('woff2');
	unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}
@font-face {
	font-family: 'nytimperialregular';
	src: url('https://a1.nyt.com/fonts/family/imperial/imperial-normal-500.woff') format('woff');
	font-weight: normal;
	font-style: normal;
}
.twowords,
.title,
.sections,
.button,
h1,
h2,
h3,
h4,
h5 {
	font-family: "Playfair Display";
	font-weight: 700;
}
div.wysiwyg a,
p.authors a,
p.about a {
	background: linear-gradient(#fff, #fff), linear-gradient(#fff, #fff), linear-gradient(#FF3530, #FF3530);
	background-position: 0 95%, 100% 95%, 0 95%;
	background-repeat: no-repeat, no-repeat, repeat-x;
	background-size: .05em 1px, .05em 1px, 1px 1px;
	color: inherit !important;
	text-decoration: none !important;
	text-shadow: .03em 0 #fff, -.03em 0 #fff, 0 .03em #fff, 0 -.03em #fff, .06em 0 #fff, -.06em 0 #fff, .09em 0 #fff, -.09em 0 #fff, .12em 0 #fff, -.12em 0 #fff, .15em 0 #fff, -.15em 0 #fff;
}
div.wysiwyg a:hover,
p.authors a:hover,
p.about a:hover {
	text-decoration: none !important;
	background: 0 0;
	color: #FF3530;
	transition: color .15s;
}
div.wysiwyg p {
	font-family: "nytimperialregular";
	color: black;
	text-align: justify;
	text-rendering: optimizeLegibility;
}
nav.sharing a {
	color: inherit;
	text-decoration: none;
}
.comments p {
	color: black;
	text-align: justify;
}
.comments h1,
.comments h2 {
	text-transform: uppercase;
	font-size: 20px;
}
h2[itemprop="name"] {
	font-size: 26px;
	text-transform: uppercase;
}
header nav.reader {
	display: none !important;
}
.wrapper, header h1 {
	
	margin: 0 auto 1em !important;
	display: block !important
}
}
