    font-family:oswald;
  }

.be-channel-page__logo {
    position: absolute;
    left: -27.5px;
    width: 20px;
    height: 30px;
    top: 30px;
    margin-top: -12px;
}
.be-user-level {
    font-family:oswald;
    color: orange;
}

.be-user-sparks {
    font-family:Poiret One;
    color: orange;
}

.viewers h5 {
    color: orange;
    font-family:Poiret One;
}

.chat-dialog header h5 {
       content: "\EA51";
       font-family: Candal;
    
}

.be-user-name {
    font-family: Candal;
    color: #fff;
    content: "\EA51";
}



.text-muted {
    color: #E81E51;
    font-family: anton;
}

.icon-spark:before {
     color: white;
}

.icon-popout:before {
   color: #E81E51;
}

.icon-close:before {
    color: #E81E51;
}


.icon-twitter:before {
    color: lightblue;
}

.icon-player:before {
    color: rgb(255,145,0);
}

.icon-facebook:before {
    color:rgb(255,145,0);
}

.icon-youtube:before {
    color:rgb(255,145,0);
}



:hov

.cls

.be-player-title:before {
    content: '';
    position: absolute;
    width: 100%;
    height: 200%;
    top: 0;
    left: 0;
    -webkit-transition: all .6s ease-in-out;
    transition: all .6s ease-in-out;
    z-index: 10;
    opacity: 0;
    color: rgb(255,145,0);
    background: -webkit-linear-gradient(rgba(0,0,0,.75) 30%,rgba(0,0,0,0) 100%);
    background: linear-gradient(rgba(0,0,0,.75) 30%,rgba(0,0,0,0) 100%);
}
a {
    color: orange;
    font-family:Poiret One;
}

a:hover {
    color: yellow;
    font-family: Candal;
}

.md-button:not([disabled]):not(.md-disabled) {
    color: rgb(255,145,0);
}


.statistics div>b {
    color: rgb(255,145,0);
    font-size: 14px;
    display: block;
    font-weight: 300;
}
}
