css:
@-moz-document domain("jvforum.fr") {
/* GÉNÉRAL */


@media (min-width: 1025px)
{
  body {
    background: hsl(204, 15%, 94%);
  }
}

.topic:nth-child(odd) {
    background: transparent;
}

.inline-quote-button--shown {
    display: none;
}

html {
  height: 100% !important;
  -webkit-box-sizing: border-box !important;
  box-sizing: border-box !important;
}

*, *:before, *:after {
  -webkit-box-sizing: inherit !important;
  box-sizing: inherit !important;
}

body {
  font: 16px/1.4 -apple-system, sans-serif !important;
  margin: 0 !important;
  overflow-y: scroll !important;
  color: #333 !important;
  -webkit-text-size-adjust: 100% !important;
  height: 100% !important;
}

.body--no-bottom {
  padding-bottom: 0 !important;
}

a {
  color: #1976d2 !important;
  text-decoration: none !important;
}

h1, h2, h3 {
  margin: 0 !important;
}

@media (min-width: 1025px) {
  .mobile {
    display: none !important;
  }
}

@media (MAX-width: 1024px) {
  .desktop {
    display: none !important;
  }
}

/* COMPONENTS */

.button {
  font-weight: bold !important;
  padding: 5px 8px !important;
  border-radius: 3px !important;
  letter-spacing: .5px !important;
  cursor: pointer !important;
  -transform: uppercase !important;
  font-size: .9em !important;
  -webkit-tap-highlight-color: transparent !important;
  -webkit-transition: all .1s ease-out !important;
          transition: all .1s ease-out !important;
  -webkit-appearance: none !important; /* text input */
  border: none !important; /* text input */
  background: transparent !important; /* text input */
  outline: none !important;

  color: #2196f3 !important;
}

.button:hover {
  color: #64b5f6 !important;
}


.button--raised {
  box-shadow: 0 2px 5px 0 rgba(0,0,0,.1) !important;
  background: white !important;
}

.button--raised:hover, .button--raised:focus {
  box-shadow: 0 3px 8px 0 rgba(0,0,0,.15) !important;
}


.button--danger {
  background: #E53935 !important;
  color: white !important;
}

.button--danger:hover, .button--danger:focus {
  background: #E57373 !important;
  color: white !important;
}


.button--cta {
  background: #00e676 !important;
  color: white !important;
  text-shadow: 0 0 2px #00c853 !important;
}

.button--cta:hover, .button--cta:focus {
  background: #69f0ae !important;
  color: white !important;
  text-shadow: 0 0 2px #00e676 !important;
}


.button--large {
  padding: 7px 12px !important;
}

.button--padded {
  margin: -10px !important;
  padding: 15px 18px !important;
}


@media (min-width: 560px) {
  .button--scale {
    padding: 7px 12px !important;
  }

  .button--scale.button--large {
    padding: 10px 16px !important;
  }
}


.button--submit {
  background: #673ab7 !important;
  color: white !important;
  color: rgba(255,255,255,.87) !important;
  padding: 7px 12px !important;
}


.button-link {
  color: #1976d2 !important;
  font-weight: bold !important;
  cursor: pointer !important;
  padding: 5px !important;
  margin: -5px !important;
}


.centered-button-container {
  margin: auto !important;
  text-align: center !important;
  padding: 10px 0 !important;
}



.toast {
  position: fixed !important;
  top: 4px !important;
  left: 0 !important;
  right: 0 !important;
  margin: 0 auto !important;
  text-align: center !important;
  pointer-events: none !important;
  -webkit-transform: translateY(-50px) !important;
          transform: translateY(-50px) !important;
  -webkit-transition: -webkit-transform .15s ease-out !important;
          transition: -webkit-transform .15s ease-out, transform .15s ease-out !important;
}

.toast--shown {
  -webkit-transform: translateY(0) !important;
          transform: translateY(0) !important;
}

.toast__label {
  display: inline-block !important;
  background: #FFECB3 !important;
  color: hsla(45,110%,20%,1) !important;
  box-shadow: 0 1px 10px rgba(0,0,0,.3) !important;
  border-radius: 4px !important;
  font-size: 14px !important;
  font-weight: bold !important;
  padding: 8px 10px 6px !important;
  cursor: default !important;
  pointer-events: auto !important;
}



.page-title {
  font-size: 1.3em !important;
  margin: 10px 8px 0 !important;
  text-shadow: 0 1px 0 white !important;
  position: relative !important;
  word-wrap: break-word !important;
}

.page-title--larger {
  font-size: 1.5em !important;
}

@media (min-width: 400px) {
  .page-title {
    margin-left: 10px !important;
    margin-right: 10px !important;
    font-size: 1.5em !important;
  }

  .page-title--larger {
    font-size: 1.7em !important;
  }
}

@media (min-width: 1025px) {
  .page-title {
    margin-left: 0 !important;
    margin-right: 0 !important;
    font-size: 1.8em !important;
  }

  .page-title--larger {
    font-size: 2em !important;
  }
}

.page-title__link {
  display: inline-block !important;
  padding: 10px !important;
  margin: -10px !important;
  color: #455A64 !important;
}

.page-title--topic {
  color: #455A64 !important;
}



.mobile-menu {
  position: absolute !important;
  top: 0 !important;
  right: -10px !important;
}

@media (min-width: 1025px) {
  .mobile-menu {
    display: none !important;
  }
}

.mobile-menu__opener {
  width: 55px !important;
  height: 55px !important;
  cursor: pointer !important;
  margin: -10px 0 !important;
  vertical-align: middle !important;
  position: relative !important;
  -webkit-tap-highlight-color: transparent !important;
}

.mobile-menu__opener-icon {
  background: url('http://image.noelshack.com/fichiers/2015/51/1450382811-1450382091-bon-tri-bouton.png') center no-repeat !important;
  width: 24px !important;
  height: 28px !important;
  position: absolute !important;
  top: 50% !important;
  margin-top: -14px !important;
  right: 5px !important;
}

.mobile-menu--opened .mobile-menu__opener-icon {
  background-color: white !important;
  border-radius: 3px !important;
  box-shadow: 0 2px 10px rgba(0,0,0,.5) !important;
}

.mobile-menu__items {
  display: none !important;
  position: absolute !important;
  width: 200px !important;
  right: 5px !important;
  top: 40px !important;
  background: white !important;
  box-shadow: 0 2px 10px rgba(0,0,0,.5) !important;
  border-radius: 2px !important;
  z-index: 1 !important;
}

.mobile-menu--opened .mobile-menu__items {
  display: block !important;
}

.mobile-menu__item {
  font-size: 16px !important;
  font-weight: normal !important;
  color: #212121 !important;
  display: block !important;
  padding: 10px 12px !important;
  cursor: pointer !important;
}



.card {
  background: white !important;
  margin: 10px 8px !important;
  position: relative !important;
  border-radius: 2px !important;
  box-shadow: 0 1px 2px rgba(0,0,0,.2) !important;
  padding: 10px !important;
  font-size: 14px !important;
  word-wrap: break-word !important;
}

@media (min-width: 400px) {
  .card {
    margin: 10px 10px !important;
    padding: 10px 12px !important;
  }
}

.card__header {
  color: #9E9E9E !important;
  color: rgba(0,0,0,.38) !important;
  padding: 0 0 5px !important;
  text-transform: uppercase !important;
  font-weight: bold !important;
  border-bottom: 1px solid #EEEEEE !important;
  border-bottom-color: rgba(0,0,0,.067) !important;
  margin: 0 -10px !important;
  text-indent: 10px !important;
}

@media (min-width: 400px) {
  .card__header {
    margin: 0 -12px !important;
    text-indent: 12px !important;
  }
}



.card--poll {
  margin-top: 0 !important;
  margin-bottom: 16px !important;
  background: #EDE7F6 !important;
}

@media (min-width: 600px) {
  .card--poll {
    margin-bottom: 20px !important;
  }
}

@media (min-width: 1025px) {
  .card--poll {
    margin-left: 200px !important;
    margin-right: 0 !important;
  }
}

.poll__title {
  margin: 0 0 10px !important;
  color: #512DA8 !important;
}

.poll__answer {
  position: relative !important;
  padding: 2px 5px !important;
  margin-bottom: 10px !important;
  box-shadow: 0 1px 1px rgba(0,0,0,.1) !important;
  border-radius: 2px !important;
  background: white !important;
}

.poll__answer:first-child {
  border-top-width: 1px !important;
}

.poll__answer-bar {
  position: absolute !important;
  height: 100% !important;
  background: #D1C4E9 !important;
  margin: -2px -5px !important;
  border-radius: 2px !important;
}

.poll__answer-name {
  position: relative !important;
  z-index: 1 !important;
  color: #311B92 !important;
  margin-right: 45px !important;
}

.poll__answer-votes {
  text-align: right !important;
  z-index: 1 !important;
  font-weight: bold !important;
  color: #673AB7 !important;
  position: absolute !important;
  right: 5px !important;
  top: calc(50% - 10px) !important;
}

.poll__info {
  margin-top: 10px !important;
  color: #9575CD !important;
}

.poll__closed-notice {
  color: #424242 !important;
  font-size: 12px !important;
}

.poll__go-vote {
  color: #4527A0 !important;
}



.jvc {
  font-variant: small-caps !important;
  font-weight: bold !important;
}



.number {
  word-spacing: -1px !important;
}

/* HEADER */

.header {
  display: table !important;
  width: 100% !important;
  margin-top: -5px !important;
  font-weight: bold !important;
  background: #90CAF9 !important;
  box-shadow: inset 0 -2px 3px rgba(0,0,0,.1) !important;
}

@media (min-width: 1025px) {
  .header {
    margin-top: -10px !important;
    margin-left: -30px !important;
    width: 110% !important;
  }
}

.header__home-cell {
  display: table-cell !important;
  width: 1px !important;
}

.header__home-link {
  color: hsla(216,85%,30%,1) !important;
  padding: 10px 20px !important;
  display: inline-block !important;
}

.header__home-link--accompanied {
  box-shadow: inset -2px 0 3px rgba(0,0,0,.05) !important;
}

.header__forum-cell {
  display: table-cell !important;
  text-transform: none !important;
  background: #BBDEFB !important;
  box-shadow: inset 0 -2px 3px rgba(0,0,0,0.08), inset 1px 0 1px rgba(255,255,255,.2) !important;
  white-space: nowrap !important;
}

.header__forum-link {
  color: #1565C0 !important;
  padding: 10px 20px !important;
  display: inline-block !important;
}

@media (MAX-width: 399px) {
  .header__home-link--accompanied,
  .header__forum-link {
    padding: 10px 10px !important;
  }
}

/* SHEET */

.sheet {
  background: #ECEFF1 !important;
  padding: 5px 0 10px !important;
  position: relative !important;
  overflow: hidden !important;
}

.body--no-bottom .sheet {
  padding-bottom: 0 !important;
}

.sheet--nothing-over {
  border-width: 0 !important;
  padding-top: 0 !important;
}

@media (min-width: 1025px) {
  .sheet {
    margin: auto !important;
    max-width: 1400px !important;
    box-shadow: 0 0 100px rgba(0,0,0,.5) !important;
    padding: 10px 30px !important;
    min-height: 100% !important;
  }

  .body--no-bottom .sheet {
    padding-bottom: 10px !important;
  }

  .sheet--nothing-over {
    border-width: 0 !important;
    padding-top: 0 !important;
    margin-top: 15px !important;
  }
}

.sheet-title {
  padding: 0 10px !important;
  margin: 5px 0 10px !important;
  word-wrap: break-word !important;
}

@media (min-width: 1025px) {
  .sheet-title {
    padding: 0 !important;
    margin: 5px -10px 10px !important;
  }
}

.sheet-title a {
  color: #333 !important;
}

@media (min-width: 1025px) {
  .sheet-title a {
    padding: 10px !important;
  }
}

@media (min-width: 1025px) {
  .sheet > .content {
    width: calc(80% - 15px) !important;
    margin-right: 15px !important;
    float: left !important;
  }

  .sheet > .content.no-menu {
    width: initial !important;
    margin-right: initial !important;
    float: initial !important;
  }

  .sheet > .content.login-fake-table {
    display: table !important;
    width: 100% !important;
  }
}

.timeout {
  padding: 10px !important;
}

@media (min-width: 1025px) {
  .timeout {
    padding: 30px !important;
  }
}

/* MENU */

.aside {
  display: none !important;
}

@media (min-width: 1025px) {
  .aside {
    display: block !important;
    width: 20% !important;
    float: left !important;
    margin: 10px 0 !important;
  }
}

.aside__moderation-actions {
  text-align: right !important;
  margin: 10px 0 !important;
}

.aside__top-buttons {
  display: table !important;
  margin: 0 0 12px 15px !important;
  width: 100% !important;
  width: -webkit-calc(100% - 15px) !important;
  width:         calc(100% - 15px) !important;
}

.aside__top-button {
  display: table-cell !important;
  background: top no-repeat !important;
  width: 100px !important;
  height: 42px !important;
  cursor: pointer !important;
  position: relative !important;
}

.aside__top-button--open-jeuxvideocom {
  background-image: url('http://image.noelshack.com/fichiers/2015/51/1450382817-1450382114-open-jvc.png') !important;
}

.aside__top-button--favorite {
  background-image: url('http://jvforum.fr/images/add-to-favorites.2.svg') !important;
}

.aside__top-button--unfavorite {
  background-image: url('http://jvforum.fr/images/remove-from-favorites.svg') !important;
}

.aside__top-button-label {
  display: block !important;
  font-size: 12px !important;
  position: absolute !important;
  bottom: 0 !important;
  width: 100% !important;
  left: 0 !important;
  text-align: center !important;
  overflow: hidden !important;
  white-space: nowrap !important;
  color: #424242 !important;
}
.menu {
  margin: 15px 10px !important;
  background: #BBDEFB !important;
  padding: 2px 0 !important;
  border-radius: 3px !important;
  box-shadow: 0 1px 2px rgba(0,0,0,.25) !important;
}

@media (min-width: 1025px) {
  .menu {
    margin: 15px 0 !important;
  }
}

.aside__menu {
  margin: 0 0 15px 15px !important;
}

.menu__item {
  display: block !important;
  color: hsla(216,85%,17%,1) !important;
  padding: 3px 10px !important;
  font-weight: bold !important;
  font-size: 14px !important;
  cursor: pointer !important;
  text-shadow : none !important;
  -webkit-tap-highlight-color: transparent !important;
}

@media (MAX-width: 1024px) {
  .menu__item {
    padding: 9px 10px !important;
  }
}

.menu__item:hover {
  color: hsla(216,85%,8%,1) !important;
  background: #90CAF9 !important;
}

.menu__item--blank-state {
  text-align: center !important;
  font-weight: normal !important;
  font-size: 11px !important;
  color: #1976D2 !important;
  cursor: default !important;
}

.menu__item--blank-state:hover {
  background: transparent !important;
  color: #1976D2 !important;
}

.slider.sliding {
  position: fixed !important;
  top: 15px !important;
  max-height: 90% !important;
  max-height: -webkit-calc(100% - 30px) !important;
  max-height:         calc(100% - 30px) !important;
  overflow-y: auto !important;
}

/* SALESPAGE */

.salespage {
  padding: 10px 10px !important;
  max-width: 800px !important;
  margin: auto !important;
}

@media (min-width: 1025px) {
  .salespage {
    padding: 20px 0 !important;
  }
}

.salespage .screen-mobile {
  margin-left: 5px !important;
}

.salespage .screen-mobile img {
  width: 90px !important;
  height: 160px !important;
}

.salespage .screen-desktop {
  margin-left: 5px !important;
}

.salespage .screen-desktop img {
  width: 232px !important;
  height: 136px !important;
}

.salespage .cta {
  padding: 1em 0 !important;
}

.salespage .cta a {
  padding: 1em 0 !important;
}

/* ACCUEIL */

@media (min-width: 500px) {
  .favorites-index {
    overflow: auto !important;
  }

  .favorites-forums, .favorites-topics {
    float: left !important;
  }

  .favorites-forums {
    width: 40% !important;
  }

  .favorites-topics {
    width: 57% !important;
    margin-left: 3% !important;
  }
}

.favorites-index h3 {
  padding: 0 10px !important;
  margin: .5em 0 !important;
}

@media (min-width: 1025px) {
  .favorites-index h3 {
    padding: initial !important;
  }
}

.favorites-index .favorite {
  background: #f9f9f9 !important;
  display: block !important;
  padding: 10px !important;
}

@media (min-width: 1025px) {
  .favorites-index .favorite {
    padding: 4px 10px !important;
  }
}

.favorites-index .favorite:nth-child(odd) {
  background: #EFF4FC !important;
}

.homepage-links {
  margin: 10px 0 !important;
  text-align: center !important;
}

.logout-container {
  margin: 20px 0 10px !important;
  text-align: center !important;
}

.logout-container a {
  color: #E53935 !important;
}

/* CONNEXION */

.form-container {
  padding: 0 10px !important;
}

@media (min-width: 1025px) {
  .form-container {
    padding: 0 !important;
  }

  .connexion-bloc1 {
    float: left !important;
    width: 50% !important;
  }

  .connexion-bloc2 {
    float: left !important;
    width: 50% !important;
  }
}

.connection-error {
  background: rgba(255,0,0,.1) !important;
  padding: 1em !important;
  border-radius: 2px !important;
}

.input {
  font-size: 1.5em !important;
  border: 1px solid #9c0 !important;
  border-radius: 1px !important;
  padding: 2px 5px !important;
  font-family: sans-serif !important;
  width: 100% !important;
  box-shadow: inset 0 1px 3px rgba(153, 204, 0, .3) !important;
  -webkit-appearance: none !important; /* Permettre de changer la shadow inset d’iOS */
}

@media (min-width: 1025px) {
  .input {
    width: initial !important;
  }
}

.input:focus {
  background: hsl(75, 100%, 95%) !important;
  outline: none !important;
}

.submit {
  border: 1px solid #ccc !important;
  background: #d0d0d0 !important;
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #f4f4f4), color-stop(50%, #dedede), color-stop(50%, #c5c5c5)) !important;
  background: -webkit-linear-gradient(#f4f4f4 0%, #dedede 50%, #c5c5c5 50%) !important;
  background:         linear-gradient(#f4f4f4 0%, #dedede 50%, #c5c5c5 50%) !important;
  color: #333 !important;
  font-weight: bold !important;
  border-radius: 0 5px 0 5px !important;
  font-size: 1em !important;
  padding: 5px 15px !important;
  text-shadow: 0 1px 10px white !important;
  cursor: pointer !important;
  -webkit-appearance: none !important; /* iOS 6 */
}

.captcha {
  border-radius: 3px !important;
}

.input-captcha {
  font-size: 2em !important;
  width: 240px !important;
}

/* FORUM */

.liste-topics {
  box-shadow: 0 1px 2px rgba(0,0,0,.2) !important;
  background: white !important;
  display: table !important;
  width: 100% !important;
  margin: 10px 0 !important;
}

@media (min-width: 1025px) {
  .liste-topics {
    border-radius: 2px !important;
  }
}

.topic {
  display: table-row !important;
  color: #1976D2 !important;
}

.topic--new-messages {
  color: #039BE5 !important;
}

.topic--visited {
  color: #757575 !important;
}

.topic--small {
  font-size: 13px !important;
}

@media (min-width: 1025px) {
  .topic--small {
    font-size: 14px !important;
  }
}

.topic:nth-child(even) {
  background: hsl(207, 90%, 98%) !important;
}

.topic:hover {
  background: #E3F2FD !important;
}

.topic__label {
  display: table-cell !important;
  vertical-align: middle !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
  height: 44px !important;
  width: 30px !important;
}

@media (min-width: 360px) {
  .topic__label {
    width: 32px !important;
  }
}

@media (min-width: 400px) {
  .topic__label {
    width: 34px !important;
  }
}

@media (min-width: 600px) {
  .topic__label {
    width: 38px !important;
  }
}

@media (min-width: 1025px) {
  .topic__label {
    height: 32px !important;
  }
}

.topic--visited .topic__label {
  height: 40px !important;
}

@media (min-width: 1025px) {
  .topic--visited .topic__label {
    height: 28px !important;
  }
}

.topic--blacklisted .topic__label {
  height: 28px !important;
}

.topic__label--dossier1 {
  background-image: url('http://jvforum.fr/images/topic-list-icons/dossier1.2.svg') !important;
}
.topic__label--dossier2 {
  background-image: url('http://jvforum.fr/images/topic-list-icons/dossier2.2.svg') !important;
}
.topic__label--lock {
  background-image: url('http://jvforum.fr/images/topic-list-icons/lock.3.svg') !important;
}
.topic__label--marque-on {
  background-image: url('http://jvforum.fr/images/topic-list-icons/marque-on.2.svg') !important;
}
.topic__label--marque-off {
  background-image: url('http://jvforum.fr/images/topic-list-icons/marque-off.2.svg') !important;
}
.topic__label--resolu {
  background-image: url('http://jvforum.fr/images/topic-list-icons/resolu.2.svg') !important;
}
.topic__label--favorite {
