.sticker {
  width: 50px !important;
  height: 50px !important;
  position: relative !important;
  top: 3px !important;
  cursor: pointer !important;
}

.message__content-text > .sticker {
  width: 85px !important;
  height: 85px !important;
  top: 0 !important;
}

.sticker--enlarged, .message__content-text > .sticker--enlarged {
  width: 280px !important;
  height: 280px !important;
}

@media (-webkit-min-device-pixel-ratio: 1.5), (min-resolution: 144dpi), (MAX-width: 1024px) {
  .sticker {
    width: 35px !important;
    height: 35px !important;
  }

  .message__content-text > .sticker {
    width: 70px !important;
    height: 70px !important;
  }

  .sticker--enlarged, .message__content-text > .sticker--enlarged {
    width: 140px !important;
    height: 140px !important;
  }
}

/* TOPIC - Message - Actions desktop */

.message__quick-actions {
  display: none !important;
}

@media (min-width: 1025px) {
  .message__quick-actions {
    display: block !important;
    position: absolute !important;
    bottom: -2px !important;
    right: 3px !important;
  }
}

.message__quick-action {
  width: 28px !important;
  height: 28px !important;
  cursor: pointer !important;
  opacity: .2 !important;
  display: inline-block !important;
  margin-left: 5px !important;
}

.message__quick-action:hover {
  opacity: .75 !important;
}

.message__quick-action--quote {
  background: url('http://image.noelshack.com/fichiers/2015/51/1450381758-1450381629-quote.png') center no-repeat !important;
}

.message__quick-action--edit {
  background: url('http://image.noelshack.com/fichiers/2015/51/1450381753-1450380797-edit.png') center no-repeat !important;
}

.message__quick-action--delete {
  background: url('http://image.noelshack.com/fichiers/2015/51/1450381760-1450381641-delete.png') center no-repeat !important;
}

.message__quick-action--delete-red {
  background: url('http://image.noelshack.com/fichiers/2015/51/1450381760-1450381641-delete.png') center no-repeat !important;
}

.message__quick-action--kick {
  background: url('http://jvforum.fr/images/kick.2.svg') center no-repeat !important;
}

.message__quick-action--punish {
  background: url('http://image.noelshack.com/fichiers/2015/51/1450383305-1450383247-punish.png') center no-repeat !important;
}

/* TOPIC - Message - Ignoré */

.message__ignored-notice {
  color: #666 !important;
  padding-left: 10px !important;
}

@media (min-width: 1025px) {
  .message__ignored-notice {
    margin-left: 200px !important;
  }
}

.message__ignored-notice_show-message-button {
  position: absolute !important;
  right: 10px !important;
  color: #888 !important;
  cursor: pointer !important;
  margin: -10px !important;
  padding: 10px !important;
}

.message__ignored-notice_show-message-button:hover {
  color: #444 !important;
}

.message__ignored-notice_regular {
  display: none !important;
}

@media (min-width: 480px) {
  .message__ignored-notice_regular {
    display: inline !important;
  }

  .message__ignored-notice_compact {
    display: none !important;
  }
}

.message__end-anchor {
  position: relative !important;
  top: 5px !important;
}

/* TOPIC - Motif de lock */

.lock-alert {
  border: 0 solid #4e342e !important;
  margin-top: 1em !important;
}

@media (min-width: 1025px) {
  .lock-alert {
    border-width: 1px !important;
    border-radius: 2px !important;
    box-shadow: 0 1px 4px rgba(0,0,0,.2) !important;
  }
}

.lock-alert__title {
  padding: 8px 10px !important;
  padding-left: 35px !important;
  background-color: #4e342e !important;
  background-image: url(http://jvforum.fr/images/lock_18px.svg) !important;
  background-repeat: no-repeat !important;
  background-position: center left 10px !important;
  color: #d7ccc8 !important;
  font-weight: bold !important;
  margin-top: 0 !important;
}

.lock-alert__cause {
  padding: 1em 10px !important;
  font-size: 14px !important;
  background: #efebe9 !important;
  color: #3e2723 !important;
}

/* TOPIC - FAB */

.fab-post {
  position: fixed !important;
  bottom: 10px !important;
  right: 10px !important;
  padding: 10px !important;
  font-size: .9em !important;
  letter-spacing: .5px !important;
  font-weight: bold !important;
  text-transform: uppercase !important;
  -webkit-tap-highlight-color: transparent !important;
  outline: none !important;
  color: white !important;
  cursor: pointer !important;
}

.fab-post__inner {
  background: hsl(198, 100%, 80%) !important; /* Light Blue A100 is 75%, too eye-catching */
  text-shadow: 0 0 2px #80D8FF !important;
  border-radius: 3px !important;
  box-shadow: 0 2px 5px rgba(0,0,0,.1) !important;
  padding: 5px 10px !important;
  -webkit-transition: all .1s ease-out !important;
          transition: all .1s ease-out !important;
}

.fab-post:hover .fab-post__inner {
  background: hsl(198, 100%, 87.5%) !important; /* 75% by default */
  text-shadow: 0 0 2px hsl(198, 100%, 80%) !important;
  box-shadow: 0 3px 8px 0 rgba(0,0,0,.15)
}

@media (min-width: 1025px) {
  .fab-post {
    display: none !important;
  }
}

/* FORUM/TOPIC - Couleurs pseudos modos/admins */

.pseudo--modo {
  color: #00c853 !important;
}

.pseudo--admin,
.pseudo--staff {
  color: #D50000 !important;
}

/* FORUM/TOPIC - Formulaire de post */

.form {
  width: 100% !important;
  background: #E0E0E0 !important;
  padding: 8px !important;
  border: 0 solid #bdbdbd !important;
  border-width: 1px 0 !important;
  position: relative !important;
  z-index: 1 !important; /* For FAB */
  box-shadow: none !important;
}
.form__topic, .form__textarea { box-shadow: none !important;}

.form--touches-bottom {
  border-bottom-width: 0 !important;
}

@media (min-width: 1025px) {
  .form {
    border-width: 1px !important;
    border-radius: 4px !important;
    box-shadow: 0 1px 4px rgba(0,0,0,.2)
  }
}

.form__block {
  margin: 10px 0 !important;
}

.form__text {
  font-size: 14px !important;
  color: #424242 !important;
}

.form__choice {
  display: block !important;
}

.form__draft {
  display: none !important;
  font-size: 12px !important;
  margin-bottom: 8px !important;
  text-indent: 2px !important;
}

.form__draft--visible {
  display: block !important;
}

.form__draft-recover {
  color: #2196f3 !important;
  cursor: pointer !important;
  -webkit-tap-highlight-color: transparent !important;
  -webkit-transition: color .1s ease-out !important;
          transition: color .1s ease-out !important;
  display: inline-block !important;
  margin: -8px !important;
  padding: 8px !important;
}

.form__draft-recover:hover {
  color: #64b5f6 !important;
}

.form__error {
color: #E53935 !important;
font-size: 14px !important;
font-weight: bold !important;
}

.form__errors {
  display: none !important;
  color: #E53935 !important;
  padding: 5px 2px 10px !important;
  font-size: 14px !important;
  font-weight: bold !important;
}

@media (min-width: 1025px) {
  .form__errors {
    padding-right: 5px !important;
    padding-left: 5px !important;
  }
}

.form__errors p {
  margin: 0 !important;
}

.form__errors.shown {
  display: block !important;
}

.form__topic, .form__textarea {
  -webkit-appearance: none !important;
  font: 14px/1.4 -apple-system, sans-serif !important;
  width: 100% !important;
  border: 1px solid #c7c7cc !important;
  padding: 2px !important;
  outline: none !important;
  background: white !important;
  color: black !important;
}

.form__topic {
  margin-bottom: .5em !important;
  border-radius: 3px !important;
}

.form__textarea {
  resize: vertical !important;
  border-radius: 5px !important;
}

.form__topic:focus, .form__textarea:focus {
  border-color: #bdbdbd !important;
  box-shadow: inset 0 2px 5px 0 rgba(0,0,0,.1) !important;
}

@media (min-width: 1025px) {
  .form__topic, .form__textarea {
    font-size: 18px !important;
  }

  .form__topic {
    padding: 3px 10px !important;
  }

  .form__textarea {
    padding: 7px 10px !important;
  }
}

.form__actions {
  display: table !important;
  width: 100% !important;
}

.form__action {
  display: table-cell !important;
  width: 20% !important;
}

.form__action--left {
  padding-left: 2px !important;
  position: relative !important;
}

.form__select {
  padding: 3px !important;
}



.smileys-link {
  display: inline-block !important;
  position: absolute !important;
  width: 45px !important;
