.validate {
  vertical-align: middle !important;
  border: 1px solid #6f6f6f !important;
  background: #464646 !important;
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #9b9b9b), color-stop(50%, #949494), color-stop(50%, #595959), color-stop(50%, #464646)) !important;
  background: -webkit-linear-gradient(#9b9b9b 0%, #949494 50%, #595959 50%, #464646 100%) !important;
