@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube-mp3.org") {

body { background-color: #f2f2f2;}

@font-face {
  font-family: 'Raleway';
  font-style: normal;
  font-weight: 400;
  src: local('Raleway'), local('Raleway-Regular'), url(https://fonts.gstatic.com/s/raleway/v10/0dTEPzkLWceF7z0koJaX1A.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;}
@font-face {
  font-family: 'Fira Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Fira Sans'), local('FiraSans-Regular'), url(https://fonts.gstatic.com/s/firasans/v5/EjsrzDkQUQCDwsBtLpcVQVtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;}
    
#content #inner {max-width:70%;font-family: 'Fira Sans', sans-serif !important}
#rad {  visibility: hidden;
        display: none ;
        padding: 0px !important;
        padding-top: 0px !important;
        position:absolute; top:-999px; left:-9999px !important;} 
#sad {  visibility: hidden;
        display: none ;
        padding: 0px !important;
        padding-top: 0px !important;
        position:absolute; top:-999px; left:-9999px !important;} 
#description h2 {
        border-top: solid 5px #DC143C;
        font-family: 'Open Sans', sans-serif;
        font-size:15px;
        color: white;
        background: #ff5757;}
#description p {
        font-family: 'Raleway', sans-serif !important;
        font-size:15px !important;
        color: #262626;}
#inner h1 a img {
        display: block;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        background-image: url(http://img15.hostingpics.net/pics/961227youtubemp3.png);
        background-repeat: no-repeat!important;
        width: 0px!important;
        height: 60px;
        padding-left: 300px;}
input#submit {
        border : solid 0px #ffffff!important;
        font-size : 15px!important;
        color : #ffffff!important;
        padding : 9px 20px!important;
        background : #4d4d4d!important;}
input#submit:hover {
        background-color : #DC143C!important;}
#form #youtube-url{
    padding:1.2%;
    border-color: #660000 !important;
    border-width:1px;
    border-right-width: 1px;
    font-family: 'Fira Sans', sans-serif !important;
    border-top-width: 1px;
    margin-bottom: 10px !important;}
.success {
    color : white;
    background-color: #ff6666;
    border-width: 2px;
    border-color: #DC143C;}
.normal {
    background-color: #ffff99;
    border: solid 2px #ff8000;
  }
#dl_link a {
    background-color: #4d4d4d;
    border:2px solid #4d4d4d;
    display:inline-block;
    cursor:pointer;
    color:white;
    padding:6px 13px;
    text-decoration:none;
}
#dl_link a:hover {
    background-color: #DC143C;
    border-color: #DC143C;
    color: white;
}
#dl_link a:active {
    position:relative;
    top:1px;}
#footer {
    border-top: solid 5px #DC143C;
    background-color:#ff5757;}
#footer a {color: white !important;} 
}
