css:
@-moz-document domain("www.edmodo.com") {
#topbar,
.groups-container {
    background-color: #2E2C40;
    color: white;
    border: none;
    box-shadow: none;
}
body {
    background-color: #4f4b6d !important;
    color: #5BB59B;
}
#topbar-content .menu>li {
    border: none;
}
#footer,
.right-column,
.nav,
.parent-code-container,
.communities-container,
.avatar-container,
li.library,
li.spotlight,
.panelarrow,
.notification-seeall-row,
#facet-search,
.sponsored,
.user-types-container,
.go-container,
.promo-banner-container,
.quotes-container,
.join-container,
.footer-container,
.pull-right .modal-view-list,
.sso-signup-container,
.lp7 #facebox-overlay,
.header.no-title,
.sso-sign-up-container-header,
.account-prompt,
.separator,
.sub-title,
.postbox-send-or,
.postbox-close,
.postbox-library.library-icon.backpack.ttip,
.icon {
    display: none !important;
}
.span9 {
    display: block;
}
.main-column {
    width: auto !important;
}
#notifications-panel {
    position: fixed !important;
    top: 0px !important;
    right: 0px !important;
    bottom: 0px !important;
    height: 100% !important;
    max-width: 400px;
    background-color: #2E2C40;
}
.inner-panel {
    height: 100% !important;
    background-color: #2E2C40;
}
#notifications-list {
    height: auto;
    background-color: #2E2C40;
    color: white !important;
    max-height: calc(100% - 40px) !important;
}
#notifications-list p {
    color: white !important;
}
.panel-title,
.border3,
#notifications-list .drill-down,
.notification-reply-list {
    border-radius: 0px;
    background-color: inherit;
    color: white;
    border: none;
}
.notification-reply-list,
.notification-reply-container,
.notification.new,
.notification.unread {
    background-color: #201e2c !important;
    border: 0px;
}
.panel-title p {
    color: white;
}
.notification .subtext strong {
    color: white;
}
#notifications-panel .inner-panel,
.notification,
#notifications-list .drill-down li,
#notifications-list .border-top {
    border: none;
    box-shadow: none;
}
#component-app-quicklaunch,
#component-app-quicklaunch .apps-dock ul li,
.section-header-bg-white,
.pb-content,
.stream-content-header,
.sub-section-header {
    background-color: #201e2c !important;
    color: white !important;
    border: none;
}
.button-container {
    position: fixed;
    bottom: 16px;
    left: 0px;
    right: 0px;
}
.main-title {
    position: fixed;
    top: 72px;
    left: 0px;
    right: 0px;
}
.btn,
.blue-btn,
.blue-btn:hover,
a.blue-btn:hover,
.e-tour .introjs-nextbutton:hover,
.section,
.blue-btn,
.blue-btn:focus,
.blue-btn:visited,
a.blue-btn,
a.blue-btn:visited,
.e-tour .introjs-nextbutton,
.btn,
.btn:visited,
a.btn,
a.btn:visited,
.e-tour .introjs-button,
.link-list-group-options li,
.section .section-header-bg-white,
.postbox-selected-content,
.pb-content,
.stream-content-header {
    -webkit-border-radius: 0px !important;
    border-radius: 0px !important;
    border: none;
}
.stream-content-header {
    margin-bottom: 10px;
}
.group-message-count,
.link-list-group-threaded>li>div>a,
.link-list-group-options li a,
.section-header-bg-white {
    color: white !important;
}
.sunken {
    background-color: inherit;
}

@media print {
    #topbar {
		display: none;
    }
}
}
