* {
  font-family: Roboto, sans-serif;
}

b {
  font-weight: 500 !important;
}

td[width="143"] {
  width: 160px;
}

td[width="143"] a:after {
  content: "+";
  color: #616161;
  font-size: 32px;
  position: absolute;
  top: 34px;
  left: 138px;
  line-height: 55px;
  padding-top: 4px;
}

#guser {
  background-color: #eee;
  width: 100%;
  margin-left: -8px;
  padding-right: 16px;
}

.gbh,
.gbd {
  border-color: transparent;
}

a,
a.gb1,
a.gb4 {
  text-decoration: none !important;
  color: #3f51b5 !important;
}

a:hover,
a:active,
a.gb1:hover,
a.gb1:active,
a.gb4:hover,
a.gb4:active {
  text-decoration: underline !important;
}

.th > tbody > tr > td:nth-child(3) > a, .m > tbody > tr > td a, .lb a:not(.ml) {
  text-decoration: none !important;
}

#gbar > nobr > a > u {
  text-decoration: none;
}

#sbq, input[type="text"] {
  border-color: transparent;
  border-top: none;
  border-bottom: 2px solid #e0e0e0;
  transition: border-color 0.1s;
}

input[type="text"] {
  padding: 2px 0 1px;
}

#sbq:focus, input[type="text"]:focus {
  outline: none;
  border-bottom-color: #f44336;
}

input[type=submit],
select {
  border: none;
  padding: 2px 7px;
  background-color: #eee;
  border-radius: 2px;
  box-shadow: 0 1px 1px #757575;
  cursor: pointer;
  transition: background-color 0.1s;
}

select {
  padding: 1px 6px;
}

select:active,
select:focus {
  outline: none;
}

input[type=submit]:active,
input[type=submit]:focus {
  background-color: #bdbdbd;
  outline: none;
}

body > table:nth-child(15) > tbody > tr:nth-child(2) > td > table > tbody > tr > td:nth-child(2) {
  padding-left: 2px;
}

table,
tr,
td {
  background-color: transparent;
  border-color: transparent !important;
  transition: background-color 0.1s;
}

body > table:nth-child(16) > tbody > tr > td:nth-child(2) > table:nth-child(1) > tbody > tr > td:nth-child(2) > form > table:nth-child(4) {
  padding-bottom: 8px;
}

body > table:nth-child(16) > tbody > tr > td:nth-child(2) > table:nth-child(1) > tbody > tr > td:nth-child(2) > form > table:nth-child(6) {
  padding-top: 8px;
}

tr[bgcolor="#ffffff"] {
  color: #3f51b5;
}

.msg {
  color: #000;
  padding-left: 0;
  padding-right: 0;
}

tr[bgcolor="#E8EEF7"] > td,
tr[bgcolor="#E8EEF7"] > td > a {
  color: #757575 !important;
}

font[color="#7777CC"] {
  color: #9e9e9e;
}

table.m > tbody > tr > td[bgcolor="#C3D9FF"],
table.m > tbody > tr > td[bgcolor="#CCCCCC"],
table.m > tbody > tr > td[bgcolor="#FAD163"] {
  background-color: #f44336;
  border-radius: 4px;
}

table.m > tbody > tr > td[bgcolor="#C3D9FF"] a,
table.m > tbody > tr > td[bgcolor="#CCCCCC"] a,
table.m > tbody > tr > td[bgcolor="#FAD163"] a {
  color: #fff !important;
}

table.th > tbody > tr > td {
  line-height: 2em;
}

.th .ts {
  height: 2em;
}

body > table:nth-child(16) > tbody > tr > td:nth-child(2) > table:nth-child(1) > tbody > tr > td:nth-child(2) > form > table.th > tbody > tr[bgcolor]:hover {
  background-color: #f5f5f5;
}

img[src="https://ssl.gstatic.com/ui/v1/icons/mail/images/star_on_sm_2.gif"] {
  display: none;
}

a[href="?&s=r"]:after {
  content: "star";
  font-family: "Material Icons";
  display: inline-block;
  transform: translateY(2px);
}

td[width="120"] {
  padding-right: 8px;
}

.ft > tbody > tr:nth-child(1) > td {
  display: none;
}

.ft > tbody > tr:nth-child(2) > td > span {
  color: #9e9e9e !important;
}

#bm > div > b:before {
  content: "Gmail+ (";
}

#bm > div > b:after {
  content: ")";
}

.h, table[width="98%"][bgcolor="#cccccc"] {
  margin: 0;
  width: 100%;
}

a font[color="#006633"]:not(:empty):before {
  content: "mail";
  font-family: "Material Icons";
  display: inline-block;
  padding-right: 3px;
  transform: translateY(2px);
}

.lb a font[color="#006633"]:before {
  content: "label";
}

td[role="alert"] b:before {
  content: "info";
  font-family: "Material Icons";
  display: inline-block;
  padding-right: 3px;
  transform: translateY(2px);
}

.m > tbody > tr > td:not(:empty):not([bgcolor="#C3D9FF"]):hover {
  background-color: #f5f5f5;
  border-radius: 3px;
}

.ml {
  margin-left: 0;
}

img[src="https://ssl.gstatic.com/ui/v1/icons/mail/images/cleardot.gif"] {
  height: 0;
}

input[type="checkbox"], input[type="radio"] {
  width: 0;
  margin-right: 12px;
}

input[type="checkbox"]:after {
  content: "check_box_outline_blank";
  font-family: "Material Icons";
}

input[type="radio"]:after {
  content: "radio_button_unchecked";
  font-family: "Material Icons";
}

input[type="checkbox"]:checked:after {
  content: "check_box";
}

input[type="radio"]:checked:after {
  content: "radio_button_checked";
}

}
