css:
@-moz-document domain("metafilter.com") {

div[class="copy post"] { background-color: #d0d0f0 !important } 
div[role="main"]>div.container { background-color: #d0d0f0 !important }
  
  
/* comment info line */
.smallcopy {
    font-size: 10px !important;
    color: #555 !important;
}
a.staff { background: none !important }
/* *** uncomments bits below to not show faves/flag *** */
/* span[id^="fav"]{ display : none !important } */
/* span[id^="flag"]{ display : none !important } */
  
/* comments */
div.comments { padding: 10px 7px 30px !important  }
    
a[name] {
    margin: 0 !important;
    padding-top: 0px !important;
}
div#posts>br, div#left>br { display: none !important }
}

@-moz-document domain("www.metafilter.com"), domain("labs.metafilter.com") {
/* MEFI + LABS */

div.content { background: #065a8f !important }
/* comments */
div.comments { background-color: #cbcbeb !important; }
div.comments:nth-of-type(2n+2) { background-color: #d0d0f0 !important }

div.comments.mod {
    background: #dbdbfb !important;
    border: 1px solid #666 
}
}

@-moz-document domain("ask.metafilter.com") {
/*  ASK */
 
div.content { background: #426746 !important } 
div[class="copy post"], div[role="main"]>div.container 
	{ background-color: #e9ffe9 !important }
#triangle { border-left-color: #e9ffe9 !important }

/* comments */
div.comments {
    background-color: #ddffdd !important;
    padding: 10px 7px 30px !important
}
div.comments:nth-of-type(2n+2) { background-color: #e9ffe9 !important }

div.comments.mod {
    background: #edffed !important;
    border: 1px solid #666 
}
}

