css:
@-moz-document url-prefix("https://quizlet.com/") {
/*Secondary Color*/

.side-nav,
.faux-side-nav,
.mrnd,
.GravitySidebar,
.TermContent-contextToggles,
#timeclock,
#scatterBox div,
.HighscoreRow.is-ownHighscore,
.LearnModeSidebar,
.LearnModeMain,
.CardsItemSide--firstSide,
.CardsControlsOptionsGroup ul,
.optionsStyle,
.CardsKeyboardShortcuts-list,
.CardsControlsOptionsGroup ul,
.CardsControlsOptions-playSettings a,
.popout,
.motion-flip .CardsList-navControl,
.webkit .motion-flip .CardsList.CardsList--showSingle .CardsItem .CardsItemSide .CardsItemInner,
.react-autosuggest__suggestions,
.simple section.main section.content,
.TimeZoneSetting-select,
select.doubleblock,
.ClassJoinLinkBox-joinPath,
.setting .box,
.SearchBox-input,
.about-box,
.CreateEditClassModal-classDescription,
.CreateEditClassModal-className,
.ClassInvitesIndividualInviteModal-input,
.ClassInvitesAutomaticJoinLinkModal-joinLink,
.CreateEditFolderModal-folderName,
.CreateEditFolderModal-folderDescription,
.ShareOptions-field,
form input.text,
form textarea.text,
.button.tan.dark,
.TermContent-sideWrap,
.CreateSetHeader-textarea,
.value,
.GravityTypingPrompt-input,
#speller-inp,
.LearnModeMain-input,
.term.has-score.has-audio,
.term.has-audio,
.Divider-label,
.about-box .details,
.chunk .chunk-title .star-chunk,
.FolderOptionsPopout {
     background: #111 !important;
}
#header .user .bridge .icon,
.popout .arrow,
.popout .bridge .icon {
     color: #111;
}
/*Main Color*/

.subpage,
.page,
.matching-letter,
.CreateSetPage-container.container,
html,
.CreateSetPage-list,
.LearnModeAnalyze-round,
.LearnModeTable,
.CardsControlsList .CardsControlsList-termsWrapper,
.CardsControls,
.CardsItem-editButton.button,
.remove,
.QModal-content,
.qmodal-content,
.LogoutMobileSplash-featureRow,
.profile-photo-upload .the-label,
.feed,
.feed .timestamp,
.info,
.term .accuracy,
.layout-list .terms .details,
.accuracy {
     background: #161616 !important;
}

/******************Custom Modifications		******************/

/***********		Header					***********/
.global.header{
	background: #0776a0;
}
#HeaderLogo .link{
	background-image: url(https://o.quizlet.com/jsnkDu99hkXiteYFJT5YOA.png);
}
#header .search .text, .actual-edit-overlay {
    -webkit-text-fill-color: #999 !important;
}
#header .search .submit{
	color: #999
}
#header .search .submit:hover{
    color: #c4c4c4;
}
.global.header a.create-link .icon{
	color: #C3CCD8;
}
.global.header a.create-link{
    border-left: 1px solid #156D8E
}
.logged-in#header .popout ul li a{
	color: #999;
}
.logged-in#header .popout ul.account-links{
	border-bottom: 1px solid #014E6B;
}
.logged-in#header .popout ul li a:hover{
	background-color: rgba(100,120,150,0.1);
    color: #c4c4c4;
}


/***********		End Header					***********/

/***********		Side Nav-Bar				***********/
.side-nav section li a,
.side-nav section li a .icon,
.side-nav .behind-the-scenes .story .post-title,
.side-nav .behind-the-scenes .story p.post-preview
{
	color: #C37725;
}
.side-nav ul li.active a,
.side-nav ul li.active a .label{
	background: #212121;
	color:#D46E00
}
.side-nav ul li.active a:hover .icon, 
.side-nav ul li.active a:hover .label {
	color:#999
}
.side-nav .behind-the-scenes .story .by,
.side-nav h3,
.side-nav h3 a{
    color: #9f6428;
}
.side-nav h3 a:hover,
.side-nav .behind-the-scenes h3 a:hover{
	color: #Df6B00;
}


/***********		End Side Nav-Bar			***********/

/***********		Main Body					***********/

/*****				Body Header					*****/
.FolderPageHeader{
	background: #132d45;
}
.badge.plus {
	background: #0776a0;
}
.SetTitle-title,
.SetHeader-sectionHeader{
	color:#c4c4c4;
}
#subheader.mode{
	background: linear-gradient(to bottom, rgba(19, 45, 69, 1) 0%, rgba(22, 22, 22, 1) 100%);
}
.ProfileHighScoreBadge,
.button,
.button.large,
.LearnModeMain-answerButton{
    color: #fff;
    background: #D46E00;
    border: 1px solid #D46E00 !important;
}
.button:hover,
.button.large:hover{
	background: #b35c00;
    border: 1px solid #b35c00;
}
.button:active,
.button.large:active{
	background: #b35c00;
    border: 1px solid #b35c00;
    box-shadow: 0 0 0;
}.button:visited,
.button.large:visited{
	box-shadow: 0 0 0;
}
.button:focus,
.button.large:focus{
	box-shadow: 0 0 0 0.0001;
}
.button.facebook{
	background-color: #3a5795;
    border-color: #314a7f !important;
}
.item-title{

    color: #EAEAEA;
}

.qselect label select{
	box-shadow:0 0 0;
}

/*Edit Folder*/
.qmodal-title{
	background-color: #004B67;
}
.CreateEditFolderModal-field{
	border-bottom: 2px solid #0776a0;
}

/*Delete Folder*/
.qmodal-content p{
    border-top: 2px solid #0776a0;
}

/*Share Folder*/
.ShareOptions-item{
	border-top: 2px solid #0776a0;
}

/*Search Box*/
.SearchBox .QIcon, .SearchBox .icon,
.SearchBox-input{
	color: #999;
}
.SearchBox-input:focus{
	border: 1px solid #0776a0;
}

/*Header Icon*/
.HeaderIcon,
.HeaderInfo-title{
	color: #c4c4c4;
}


/*Folder Options*/
.FolderOptionsPopout-option{
	border-bottom: 1px solid #0776a0;
    color: #999;
}
.FolderOptionsPopout-option:hover{
	background-color: rgba(100,120,150,0.1);
    color: #c4c4c4;
}


/*Tabs Bar*/
.TabBarMenu-item a{
	color: #999;
    text-shadow: 0 0 0;
}
.TabBar,
.tags-bar,
.tabs-bar,
#subheader.mode.container{
	background: linear-gradient(to bottom, rgba(19, 45, 69, 1) 0%, rgba(22, 22, 22, 1) 100%);
    box-shadow: 0 0 0;
}
.tabs-bar .tabs a{
    text-shadow: 0 0 0;
    color: #999;
    box-shadow: -1px 0 0 #999;
}



.page-top{
 border-bottom: 1px solid #0776a0;
}
.page-top h2{
	color: #999;
}
.segmented-control a{
    border: 1px solid #999;
}
.segmented-control a.first,
.segmented-control a.last{
	color:#999;
    
}
.order-original .segmented-control a.order-original,
.order-alphabetical .segmented-control a.order-alphabetical{
	background: #161616;
    border: 1px solid #0776a0;
}
.recent .segmented-control a.order-recent,
.alphabetical .segmented-control a.order-alphabetical{
	background: #161616;
    color: #999;
    border: 1px solid #0776a0;
}


/*****				End Body Header				*****/

/*****				Set Header					*****/

/*Study*/
a.flashcards.ModeButton,
a.learn.ModeButton,
a.speller.ModeButton,
a.test.ModeButton,
/*Play*/
a.scatter.ModeButton,
a.gravity.ModeButton{
	color: #c4c4c4;
}


/***				Tools						***/
.SetTools-toolLink,
.SetTools-toolLink:visited{
	color: #c4c4c4;
}
.SetTools-toolLink:hover{
	background-color: rgba(100,120,150,0.1);
}


/*More*/
.SetTools-moreTool,
.SetTools-moreTool.SetTools-moreTool--combine{
	color: #d46e00;
}
.SetTools-moreTool:hover,
.SetTools-moreTool.SetTools-moreTool--combine:hover{
	background-color: rgba(100,120,150,0.1);
    color: #c4c4c4;
}


/***			End Tools						***/

/*****			End Set Header					*****/

/*****			Create Set						*****/

/*Create Set Header*/
.CreateSetPage-languageBar,
.AutoSavePrompt{
    background: #132D45;
    border-bottom: 1px solid #999;
}

.item-title .CreateSetHeader-pageTitle,
.CreateSetHeader-label,
.AutoSavePrompt{
	color: #c4c4c4;
}
.AutoSavePrompt a:hover{
	color: #999;
}

.button.cancel, .button.no, .button.grey, .button.gray{
	border:0 !important;
}

.CreateSetHeader-editDescriptionLink,
.CreateSetHeader-permissionsLinkChange,
a.LanguageBarSide-quickSwitchLink,
a.LanguageBar-keyboardShortcuts{
	color: #d46e00;
}
.CreateSetHeader-editDescriptionLink:hover,
.CreateSetHeader-permissionsLinkChange:hover,
a.LanguageBarSide-quickSwitchLink:hover,
a.LanguageBar-keyboardShortcuts:hover{
    color: #FF8400;
}
.CreateSetHeader,
.CreateSetPage-languageBar,
.CreateSetPage-subheaderSeparator{
	border-bottom: 1px solid #999;
}


/*Term Create*/
.TermRow.is-active .TermContent-toggleContextButton{
	color: #d46e00;
}
.cursor .TermContent-toggleContextButton:hover,
.cursor .TermRow.is-active .TermContent-toggleContextButton:hover{
	color: #b35e00;
}

/*****			End Create Set					*****/



/*Feedback*/
/*.qselect label,
.qselect label select{
    border: 1px solid #D46E00;
	background: #d46e00;
    color: #fff;
}*/


/*Terms (List)*/
.order-original .layout-list .terms,
.TermContent-sideWrap,
.TermContent-contextToggles{
	border: 1px solid #999;
}
.TermRow.is-active .TermContent-sideWrap,
.TermRow.is-active .TermContent-side--word{
	border-color: #c4c4c4;
}
.TermContent-side--word{
	border-right: 1px solid #999; 
}
.layout-list .terms .details{
	border-left: 1px solid #999;
}
.term{
	border-top: 1px solid #999;
}


.TermRow.is-active .TermContent-sideWrap {
	background-color: rgba(100,120,150,0.1);
}
.TermRow.is-active .TermContent-toggleContextButton,
.TermRow.is-active .TermContent-toggleContextLabel{
	color: #d46e00;
}


.word{
	color: #d46e00 !important; 
}
.term .definition,
.TermContent-sideWrap{
	color: #AFAFAF;
}

/*About (Info)*/
.about-box{
	border: 1px solid #999;
    color: #999;
}
.about-box .studied-by{
	border-left: 1px solid #999;
}
.about-box .details{
	border-top: 1px solid #999;
}

.about-box h3{
	color: #c4c4c4;
}
.about-box .details .highfives .highfive .thanks.label,
.meta h3{
	color: #999;
}

/*Learn Mode*/ 
.LearnModeSidebar-counter--remaining .LearnModeSidebar-counterBackground{
	background: #d46e00;
}
.LearnModeMain-metaButton{
	color: #d46e00;
}
.LearnModeMain-promptWrapper{
	border-bottom: 1px solid #999; 
}

/*Feed*/
.feed .timestamp,
.timestamp{
    border-bottom: 3px solid #d46e00;;
    text-shadow: 0 0 0 #999;
    color: #999 !important; 
}
.time-block.is-inFuture .timestamp{
	border-bottom: 3px solid #0776a0;
}
.time-block.is-inFuture .title-text{
	color: #d46e00;
	}
.feed-preview .info .created{
	color: #999;
}
.feed .story,
.side-nav section,
.side-nav .behind-the-scenes{
	border-bottom: 1px solid #014E6B;
}
.feed-preview .info .feed-title, 
.side-nav ul .new .label, 
.side-nav ul .new .icon,
.side-nav ul li.active a .icon,
.side-nav .footer a{
    color:#D46E00;
}
.feed-preview .info .feed-title .privacy-text{
	background-color: #d46e00;
    color: #c4c4c4;
}


/*****			Long-Term Learning Box			*****/

/*Backgrounds and Borders*/
.SpacedRepetitionIntro,
.SpacedRepetitionInfo-section{
    border: 2px solid #737373;
}
.SpacedRepetitionIntro-topHalfContainer,
.SpacedRepetitionHeader
{
	background: #004B67;
}
.SpacedRepetitionIntro-bottomHalfContainer,
.SpacedRepetitionInfo-section{
	background: #002838;
}
.SpacedRepetitionIntro-newFeatureBadge,
.SpacedRepetitionHeader-newFeatureBadge,
.SpacedRepetitionHeader-button.button,
.SpacedRepetitionInfo-headline{
	background: #d46e00;
    color: #fff;
}
.button:hover{
	background: #b35c00;
}
.SpacedRepetitionIntro-text,
.SpacedRepetitionHeader-studyIcon,
.SpacedRepetitionHeader-headline,
.SpacedRepetitionHeader-subheadline,
.SpacedRepetitionHeader-learnMore{
	color: #c4c4c4;
}

/* Progress Chart*/
.SpacedRepetitionOverallProgress-bucket.is-dontKnow:not(.is-empty).SpacedRepetitionOverallProgress-bucket .ProgressCircle-background,
.SpacedRepetitionOverallProgress-bucket.is-sortOfKnow:not(.is-empty).SpacedRepetitionOverallProgress-bucket .ProgressCircle-background,
.SpacedRepetitionOverallProgress-bucket.is-know:not(.is-empty).SpacedRepetitionOverallProgress-bucket .ProgressCircle-background,
.ProgressCircle-background{
	stroke: rgba(200,205,210,0.35);
}

.ProgressCircle-labelName{
	fill: #5f8094;
}
.ProgressCircle-percent,
.ProgressCircle-percentIcon{
	fill:#FF8400;
}

/*Bar Chart*/

.SimpleBarChart-barWrapper{
    background: rgba(200,205,210,0.35);
}
.SimpleBarChart.is-empty .SimpleBarChart-bar{
	background: 0;
}
.SimpleBarChart-circle{
	background: #d46e00;
    color: #fff;
    border: 1px solid #D46E00
}

/*****			End Long-Term Learning Box		*****/

/*glyphicons*/
.mode-circles .mode.studied .circle,
.mode-circles .mode.studied .circle .icon{
	background: #D36E00;
    color: #111;
}
.mode-circles .mode .circle,
.mode-circles .mode.partially-studied .circle .icon{
	background: #0776a0;
    color: #111;
}
.feed .remove .label .remove-text .icon{
	color: #FF0000;
}

/***********	End Main Body					***********/

/*****			Footer							*****/
footer.global .links .fill hr{
	border-top: 1px solid #999;
    border-bottom: 1px solid #999;
}
footer.global{
	text-shadow:0 0 0;
}
footer.global .links a{
	color: #d46e00;
}
/*****			End Footer						*****/

/******************End Custom Modifications		******************/


/*Text Color*/

.page-top,
.page-top h2,
.TermContent-toggleContextLabel,
.LearnModeTable td,
.CreateSetPage-h2,
.AutoExpandTextarea-textarea,
.LanguageBarSide-selectedLangLabel,
.LearnModeTable th,
#scatterBox,
.LearnModeAnalyze-heading,
.LearnModeAnalyze-termsList .LearnModeAnalyze-itemWrap,
.LearnModeAnalyze-termsList li,
.LearnModeAnalyze-termBlock,
.LearnModeSidebar-counterLabel,
.LearnModeSidebar-options label,
.CardsKeyboardShortcuts-list,
.CardsControlsOptionsGroup ul,
.CardsControlsOptionsGroup h2,
.CardsItem-editButton.button,
.motion-flip .CardsList-navControl,
.CardsItem-flipHint,
#subheader.mode h2.studying a,
.ListToggleModalItem-termCount,
.react-autosuggest__suggestions,
.SearchBox-input,
.optionsStyle,
.CardsControlsOptionsGroup ul,
.CardsControlsOptions-playSettings a,
.TimeZoneSetting-select,
select.doubleblock,
.ClassJoinLinkBox-subheadline,
.ClassJoinLinkBox-joinPath,
.ClassPageTools .minor .popout .tool,
.feed .timestamp,
.Divider-label,
.setting h2,
form input.text,
form textarea.text,
.button.tan.dark,
.CreateSetHeader-textarea,
.value,
.GravityTypingPrompt-input,
#speller-inp,
.LearnModeMain-input,
.CreateEditFolderModal-folderName,
.CreateEditClassModal-classDescription,
.CreateEditClassModal-className,
.ClassInvitesIndividualInviteModal-input,
.ClassInvitesAutomaticJoinLinkModal-joinLink,
.CreateEditFolderModal-folderDescription,
.ShareOptions-field,
.chunk .chunk-title .star-chunk,
.chunk .chunk-title .title,
.popout,
.mode-scores header h3 .mode-link,
.order-missed .segmented-control a.order-missed,
.order-original .segmented-control a.order-original,
.order-alphabetical .segmented-control a.order-alphabetical,
.segmented-control a,
.chunk .chunk-title .subtitle,
.definition,
.answers,
.description {
     color: #c4c4c4 ;
}
.mode-scores .completed li a {
     color: #c4c4c4;
}
/*Header Color*/

.ListToggleContainer-folderLabel,
.ListToggleContainer-classLabel {
     color: #1E6DD3 !important;
}
/*Other Stuff*/

body {
     background: #161616 !important;
     color: #c4c4c4 !important;
}
.is-active.TabBarMenu-item a,
.scores-tab .tabs-bar .scores-tab a,
.info-tab .tabs-bar .info-tab a,
.created-tab .tabs-bar .created-tab a,
.studied-tab .tabs-bar .studied-tab a,
.folders-tab .tabs-bar .folders-tab a,
.classes-tab .tabs-bar .classes-tab a,
.sets-tab .tabs-bar .sets-tab a,
.members-tab .tabs-bar .members-tab a,
.terms-tab .tabs-bar .terms-tab a {
     background: #161616 !important;
     color: #c4c4c4 !important;
     box-shadow: 1px 0 0 #161616, -1px 0 0 #161616;
}
.ClassJoinLinkBox {
     background: linear-gradient(to bottom, rgba(22, 22, 22, 1) 0%, rgba(19, 45, 69, 1) 49%, rgba(22, 22, 22, 1) 100%);
}
.ToggleSetsInClassModal-bottomPatch,
.ToggleSetsInFolderModal-bottomPatch {
     background: linear-gradient(to bottom, rgba(20, 9, 9, 0) 0%, rgba(21, 21, 21, 1) 100%);
}
.terms .details .icons .star .popout .bridge .icon,
.terms .details .icons .play-audio .popout .bridge .icon,
.terms .details .icons .inline-term .popout .bridge .icon,
.terms .details .icons .custom-audio .popout .bridge .icon {
     color: #111;
     text-shadow: 0 1px 0 #000, 1px 2px 3px rgba(0, 0, 0, 0.3);
}
form input.text,
.button.tan.dark,
.CreateSetHeader-textarea,
.CreateEditClassModal-classDescription,
.CreateEditClassModal-className,
.ClassInvitesIndividualInviteModal-input,
.ClassInvitesAutomaticJoinLinkModal-joinLink,
.CreateEditFolderModal-folderName,
.CreateEditFolderModal-folderDescription,
.ShareOptions-field,
.LearnModeMain-input,
#speller-inp,
.value,
.GravityTypingPrompt-input,
form textarea.text {
     border: 1px solid #868686;
     box-shadow: inset 1px 1px 2px #5A5A5A;
}
.TimeZoneSetting-select,
.doubleblock,
.CardsKeyboardShortcuts-list,
.ClassJoinLinkBox-joinPath {
     border-color: #868686 !important;
}
.order-missed .segmented-control a.order-missed,
.CreateSetPage-subheader,
.study-all-terms #terms .page-top .segmented-control a.study-all,
.study-stars-only #terms .page-top .segmented-control a.study-selected{
     background: #132D45;
}
.ClassJoinLinkBox-headline {
     color: #FFF;
}
#subheader.mode {
     border-bottom: 1px solid #161616;
}
.webkit .motion-flip .CardsList.CardsList--showSingle .CardsItem .CardsItemSide .CardsItemInner {
     box-shadow: 1px 1px 0 #696969, 5px 5px 16px #525252;
}
.CardsItemSide--secondSide {
     background: #212121;
}
.motion-flow .CardsItem-arrow {
     background: url(http://i64.tinypic.com/xqm81f.jpg) no-repeat 0 bottom, url(http://i64.tinypic.com/xqm81f.jpg) no-repeat 0 bottom;
}
.CardsControlsOptionsGroup ul li a.active {
     background: #92A1B5 !important;
}
.LearnModeGradeAnswerView-diff--correct {
     color: #398F34;
}
.qbutton-big {
     background: #111 url(http://i67.tinypic.com/30nd7yg_th.jpg) repeat-x bottom !important;
}
.right-q {
     background: url(http://i65.tinypic.com/wryzqv_th.png) repeat-y;
}
.wrong-q {
     background: url(http://i68.tinypic.com/juie6g_th.png) repeat-y;
}
#scatterBox div {
     text-shadow: none !important;
}
.HighscoresMessage {
     background-color: #151515;
}
.HighscoresMessage-title,
.HighscoreRow-position,
.HighscoresList-title {
     color: #2673D9;
}
.GravityOptionsView-radioButton {
     background: #161616;
}
.AutoExpandTextarea-wrapper {
     background: #111;
}
}
