css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gauchospace.ucsb.edu") {
  body {
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
        color: #ddd;
        background-color: #222 !important;
    }

     a {
        color: #608dff !important;
        text-decoration: none;
    }

    a:hover {
        color: #637ab5 !important;
        text-decoration: underline;
    }


  h1 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 700 !important;
        color: #eee !important;
    }

    h2 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 400 !important;
        color: #eee !important;
    }

    h3 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 400 !important;
        color: #ddd !important;
    }


    h4 {
        color: #ccc !important;
    }


    .reflist, .refbegin {
        background-color: #282828 !important;
    }


    .thumbcaption {
        color: #888 !important;
    }

    #content h3 {
        font-size: 20px !important;
    }

    p {
        font-weight: 300 !important;
        line-height: 1.9 !important;
    }

    .block {
        color: #ddd;
        background-color: #222 !important;
    margin-bottom: 10px;
    padding: 8px 0 4px;
    -moz-box-shadow: 5px 5px 10px rgba(177, 175, 175, 0.62);
    -webkit-box-shadow: 5px 5px 10px rgba(177, 175, 175, 0.62);
    box-shadow: 5px 5px 10px rgba(177, 175, 175, 0.62);
    border: 1px solid #bcbcba;
    font-size: 13px;
}

    .course-content .current {
    background-color: #000;
}

    .generaltable tbody > tr:nth-child(2n+1) > th {
    background-color: #000;
}
}
