css:
@-moz-document domain("habitica.com") {


/**************************/
/*       GLOBAL          */
/************************/
body
{
    background-color: #111;
}

#wrap h1, #wrap h2, #wrap h3 
{
    font-family: 'Quicksand', sans-serif;
    text-transform: uppercase;
    text-align: center;
    -webkit-font-smoothing: antialiased;
    text-shadow: rgba(0,0,0,0.01) 0 0 1px;
    color: #af9461;
    font-weight: 500;
    line-height: 1.1;
}

/* Titles */
#wrap h1{ font-size: 2.2em; }
#wrap h2{ font-size: 1.8em; }
#wrap h3{ font-size: 1.5em; }

/* Lists */
#wrap ul,
#wrap ul li,
#wrap ul li a
{
    border-color: #3E3E46;
}
#wrap ul,
#wrap ul li,
#wrap ul li a,
#wrap ul li button,
#wrap button
{
    -webkit-border-radius: 0 0 0 0!important;
	border-radius: 0 0 0 0!important;
}

#wrap ul
{
    border: 0;
}

#wrap ul li
{
    border: 1px solid #3E3E46;
}

/* Icons */
a span.glyphicon { color: silver; }

.task-actions a span.glyphicon { color: white; }

button span.glyphicon,
.task-add>button span.glyphicon
{
    color: #af9461!important;
}
/* Fond gris classique "<li>" */
/*
#wrap div:not(.tabbable) > ul li
{    
    background-color: #2D2D30!important;
    border: 1px solid #3E3E46!important;
    color: #E3E3E3!important;
}

#wrap div:not(.tabbable) > ul li a
{
    background-color: transparent;
}
*/
/* version 'grisée' */
/* Ajouter chaque type .completed etc... pour fond grisé */
#wrap div:not(.tabbable) > ul li.completed
{
    color: #989898!important;
    background-color: #d9d9d9!important;
    border: 1px solid #c3c3c3!important;
}

#wrap button
#wrap a
{
    cursor: pointer;
}

#wrap #main .tab-content .options-menu,
#wrap #main .tab-content .panel-heading,
#wrap #main .tab-content .popover-title
{
    background-color: #2D2D30;
    border-bottom: 1px solid #af9461;
}
#wrap #main .tab-content .options-submenu
{
    background-color: #3E3E46;
    border-bottom: 1px solid #af9461;
}
#wrap #main .tab-content:first-child
{
    background-color: #181819;
    border: 1px solid #252526;
    color: #E3E3E3!important;
}
#wrap #main .tab-content .panel,
#wrap #main .tab-content .popover,
#wrap #main .tab-content .well
{
    background-color: #111;
    border: 1px solid #252526;
    color: #E3E3E3!important;
    -webkit-border-radius: 0!important;
    border-radius: 0!important;
}
#wrap #main .tab-content button.selectableInventory
{
    background-color: #af9461!important;
}
#wrap #main .tab-content button:not(.active)
{
    border: 1px solid #3E3E46!important;
}
#wrap #main .tab-content button.active
{
    background-color: #111!important;
    border: 1px solid #af9461!important;
}
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th
{
    background-color: #2D2D30!important;
    border: 0!important;
    color: #af9461!important;
}
.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th
{
    background-color: #111!important;
    border: 0!important;
    color: #af9461!important;
}
#wrap #main .tab-content .border-right,
#wrap #main .tab-content .border-left
{
	border: 0;
}
hr {
    border-top: 0;
    border-bottom: 1px solid red;
    border-color: #2D2D30;
}
code
{
    background-color: #111;
}
.nav-tabs>li>a
{
    margin-right: 0;
}
/**************************/
/*       GLOBAL FIN      */
/************************/

/**************************/
/*         NAV           */
/************************/
nav.toolbar
{
    background-color: #040404;
    border-bottom: 1px solid #af9461;
    background-repeat: no-repeat;
}

nav.toolbar::before
{
    content: '';
    background-image: url('http://www.gaming1.com/assets/gaming1_small-644f96e2e0fb5d8d12d5aa35679555fe.svg');
    background-repeat: no-repeat;
    background-size: 100px 24px;
    height: 30px;
    width: 130px;
    position: relative;
    top: 10px;
    left: 5px;
    float: left;
    display: block;
}

nav.toolbar .toolbar-nav .toolbar-button .glyphicon ,
nav.toolbar .toolbar-nav .toolbar-button-dropdown .glyphicon 
{ display: none; }

nav.toolbar .toolbar-nav>li { margin-right: .65em; }
nav.toolbar .toolbar-nav .toolbar-button,
nav.toolbar .toolbar-nav .toolbar-button-dropdown
{
    height: 40px;
    vertical-align: top;
}

nav.toolbar .toolbar-nav .toolbar-button > a,
nav.toolbar .toolbar-nav .toolbar-button-dropdown > a
{
    line-height: 20px;
    padding: 7px 3px 5px 2px!important;
    vertical-align: top;
    color: white!important;
    text-transform: uppercase;
    font-family: Century, serif;
}


nav.toolbar .toolbar-nav .toolbar-button,
nav.toolbar .toolbar-nav .toolbar-button-dropdown,
nav.toolbar .toolbar-nav .toolbar-button-dropdown.highlight,
nav.toolbar .toolbar-nav .toolbar-button > a,
nav.toolbar .toolbar-nav .toolbar-button-dropdown > a,
nav.toolbar .toolbar-nav .toolbar-button-dropdown.highlight > a
{
    border: 0!important;
    background: none!important;
    -webkit-border-radius: 0!important;
    border-radius: 0!important;
    text-align: center;
}

nav.toolbar .toolbar-nav .toolbar-button:hover > a,
nav.toolbar .toolbar-nav .toolbar-button-dropdown:hover > a
{
    color: #af9461!important;
}
nav.toolbar .toolbar-nav .toolbar-button-dropdown > a:first-child:hover::before,
nav.toolbar .toolbar-nav .toolbar-button-dropdown > a:first-child:hover::after
{
    display: block;
    height: 3px;
    margin: -17px 0 0;
    padding: 0 0 17px;
    content: url(http://www.gaming1.com/assets/menu-separator-46f16b7c6581c9b15ba673ec6f554c42.png);
}
nav.toolbar .toolbar-nav .toolbar-button-dropdown > a:first-child:hover::after
{
    margin-top: -9px;
}
/**************************/
/*        //NAV          */
/************************/

/**************************/
/*        HEADER         */
/************************/
.header-wrap
{
    background-color: #1E1E1E;
    border-bottom: 1px solid #252526;
    padding: 60px 0 0;
}

.header-wrap .herobox.noBackgroundImage { background-color: #1E1E1E; }

header .hero-stats
{
    background-color: #1E1E1E;
    border: 0;
}
/**************************/
/*      //HEADER         */
/************************/

.task-column
{
    background-color: #181819;
    border: 1px solid #252526;
}

/**************************/
/*         FORMS         */
/************************/
#wrap button,
#wrap select,
#wrap input,
#wrap fieldset.option-group button:not(active),
#wrap div.tabbable > ul li a, #wrap div.tabbable > ul li button,
#wrap div.filters ul li a, #wrap div.filters ul li button,
#wrap ul.options-menu > li > a,
#wrap div:not(.tabbable) > ul li,
#wrap .tab-content div:not(.tabbable) > ul li a,
#wrap .plusminus input[type=checkbox]+label:after
{
    background-color: #2D2D30!important;
    border: 0!important;
    color: #af9461!important;
}

#wrap div:not(.tabbable) > ul li
{
    border: 1px solid #3E3E46!important;
    color: #E3E3E3!important;
}
#wrap ul li.active > a
{
    cursor: default;
}
#wrap fieldset.option-group button.active,
#wrap div.tabbable > ul li.active,
#wrap div.filters ul li.active,
#wrap ul.options-menu > li.active,
#wrap div:not(.tabbable) > ul li.active,
#wrap .plusminus input[type=checkbox]:checked+label:after
{ border: 1px solid #af9461!important; }

#wrap button.active,
#wrap fieldset.option-group button.active,
#wrap div.tabbable > ul li.active a, #wrap div.tabbable > ul li.active button,
#wrap div.filters ul li.active a, #wrap div.filters ul li.active button,
#wrap ul.options-menu > li.active > a,
#wrap div:not(.tabbable) > ul li.active > a,
#wrap .plusminus input[type=checkbox]:checked+label:after
{
    background-color: #181819!important;
    -webkit-box-shadow: none!important;
    box-shadow: none!important;
}

#wrap button:hover,
#wrap fieldset.option-group button:hover,
#wrap div.tabbable > ul li:not(.active) a:hover, #wrap div.tabbable > ul li:not(.active) button:hover,
#wrap div.filters ul li:not(.active) a:hover, #wrap div.filters ul li:not(.active) button:hover,
#wrap ul.options-menu > li:not(.active) > a:hover,
#wrap div:not(.tabbable) > ul li:not(.active) > a:hover,
#wrap .plusminus input[type=checkbox]+label:hover:after
{
    background-color: #af9461!important;
    color: white!important;
    -webkit-box-shadow: none!important;
}

#wrap form  textarea, 
#wrap form  a, 
#wrap form  button, 
#wrap form  input,
#wrap .input-group  textarea, 
#wrap .input-group  a, 
#wrap .input-group  button, 
#wrap .input-group  input,
#wrap .input-group  .input-group-addon
{
    background-color: #181819!important;
    color: #af9461!important;
    border: 1px solid #af9461!important;
}

#wrap form > button:hover
{
    background-color: #af9461!important;
    color: white!important;
    -webkit-box-shadow: none!important;
}
#wrap form > button:hover span.glyphicon,
#wrap form > button:hover span.glyphicon
{
    color: white!important;
}

::-webkit-input-placeholder {
    color: #af9461!important;
}

.btn-link, .btn-link:active, .btn-link:hover,
a, a:active, a:hover
{
    color: #af9461;
    text-decoration: none;
}
.btn-link:hover,
footer li a:hover
{
    text-decoration: underline;
}
#wrap legend
{
    color: #af9461;
}
.save-close
{
    padding-top: 1em;
}
/**************************/
/*       //FORMS         */
/************************/


/**************************/
/*        FOOTER         */
/************************/
body footer.footer
{
    background-color: #1E1E1E;
    border-top: 1px solid #3E3E46;
}
body footer.footer h4
{
    color: white;
}
/**************************/
/*      //FOOTER         */
/************************/


/**************************/
/*      PAGE: TASKS      */
/************************/
#wrap div.filters ul li.filters-edit
{
    background-color: transparent!important;
    border: 0!important;
}

.filters .hrpg-input-group .glyphicon { color: silver; }
.btn-buy
{
    color: #af9461;
    background-color: #181819;
    padding: .5em 0 0 .5em;
}
.btn-buy:hover
{
    color: white;
    background-color: #af9461;
}
.task-column:not(.rewards) .color-neutral:not(.completed) .action-yesno label, .task-column:not(.rewards) .color-neutral:not(.completed) .task-action-btn, .task-column:not(.rewards) .color-neutral:not(.completed) .task-actions, .task-column:not(.rewards) .color-neutral:not(.completed) .task-actions a {
    background-color: #af9461!important;
}

.task :last-child:not(button) {
    margin-bottom: 2px;
}
.task-column
{
    padding: 0.75em;
}
.task-text
{
    padding: .5em 0 .5em 3.75em;
}
.task,
.rewards .task,
.btn-buy
{
    min-height: 2em;
}
.habits .habit-wide {
    min-height: 3.5em;
}

#wrap .task-column:not(.rewards) .task .task-options
{
    background-color: #111;
    color: #af9461;
}
.task-column:not(.rewards) .color-good:not(.completed) .option-group:not(.task-checklist)
{
    border-bottom: 1px solid #af9461;
}
.rewards
{
    margin-bottom: 0.5em;
    padding-bottom: 0.5em;
}
.action-yesno label:after, .action-yesno label:before
{
    opacity: .6;
    -ms-filter: "alpha(Opacity=60)";
    filter: alpha(opacity=60);
}
/**************************/
/*    //PAGE: TASKS      */
/************************/

#wrap .advanced-options-toggle.option-title, #wrap .advanced-options-toggle:hover, #wrap .advanced-options-toggle.option-title:focus,
#wrap form a.hint, #wrap form a.hint:hover
{
    background-color: transparent!important;
    border: 0px solid #af9461!important;
    outline:0;
}


}
