css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.charazay.com") {
  
ul.topmenu li:hover {
  background: #F28130;
}

ul.topmenu li {
  border: 1px #F28130 solid;
}

#primary-nav-wrap ul {
  background-color: #F28130;
}  

#primary-nav-wrap ul li:hover {
  background: #F87213;
}

#primary-nav-wrap ul ul {
   background: #F28130;
}
  

a.btngreen, button.btngreen, input.btngreen, input.inputSubmit, input.inputButton {
  border: #F87213 1px solid;
  background-color: #f28130;
}
  
a.btngreen:hover, button.btngreen:hover, input.btngreen:hover, input.inputSubmit:hover, input.inputButton:hover {
  background-color: #F87213 ;
}  

a.textlink {
    font-size: 100%;
    color: #ffffff;
}  

  .TabbedPanelsTab {
    background-color: #D7CECE;
} 
.TabbedPanelsTabSelected {
    background-color: #FFFFFF;
}  
  
.site-info-t {
  background-color: #F28130;
  color: #000;
  text-shadow: 1px 1px 1px #aaa;

}  
  
.site-info-s {
    background-color: #f0f0f0;
    border: 2px solid #F28130;

}  

a.linkButton {
    background: #F28130 none repeat scroll 0 0;
    border: 1px solid #F28130;
    box-shadow: 0 1px 2px rgba(255, 255, 255, 0.6) inset, 0 -5px 15px rgba(0, 0, 0, 0.3) inset, 1px 1px 1px #F28130;

}

#notification_container2 {
    border: 0px solid #b4ff00;
    
}
  
.site-info_toolbox .helplinks a {
  background-color: #4e463f;
  border: 0px #B4FF00 solid;
}  

div .post_top a {
    color: #f28130;
    text-shadow: 1px 1px 1px #000;
}  
  
}
