css:
@-moz-document domain("legendas.tv") {
html, body, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre abbr, acronym, address, big, cite, code, del, dfn, em, fieldset, figure, font, img, input, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, fieldset, form, label, legend, table, caption, select, tbody, textarea, tfoot, thead, tr, th, td {
    background-color: #0d0d0d! important;
}

a {
    text-decoration: none;
    color: #2c61aa !important;
}

.middle .wrapper .film h3 a span {
    color: #d4d4d4 ! important;
}

.middle .slider_wrapper .slider .item a img {
    border: 5px solid #275492 !important;
    display: block;
}

.middle .slider_wrapper .slider .item a:hover img {
    border: 5px solid #fdb62a !important;
}


.middle .wrapper .film h3 a span {
    color: #58595b !important;
}

.middle .wrapper .film p {
    color: #58595b !important;
}

.middle .wrapper .film p {
    color: #58595b !important;
}

header .menu .dropdown {
    width: 150px;
    background-color: #91cdef !important;
    background-image: -webkit-linear-gradient(to bottom, #6aa3d5, #3d7cbf);
    background-image: linear-gradient(to bottom, #1d1d1d, #1d1d1d) !important;
    position: absolute;
    top: 22px;
    left: 20px;
    padding: 5px;
    box-shadow: 1px 1px 5px rgba(0, 0, 0, 0.6);
    border-radius: 8px;
    z-index: 1;
}

div#sugestao-legenda-box {
    background-color: #1d1d1d !important;
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.37) !important;
}

header .searchbar div:after {
    content: ' ';
    width: 378px;
    height: 91px;
    background: none !important;
    position: absolute;
    bottom: -91px;
    left: -15px;
}

#sugestao-legenda li {
    margin: 0 0 5px;
    background-color: #1d1d1d !important;
}

.middle .wrapper .film p {
    color: #d4d4d4 !important;
}

#sugestao-legenda li a {
    color: #a7a7a7 !important;
    background-color: #1d1d1d !important;
}

.busca .destaque {
    background-color: #333333 !important;
}

.download section.first h3 {
    font-size: 20px;
    line-height: 20px;
    color: #275492 !important;
}

input#search-box {
    color: #bcbec0 !important;
}

sugestao-legenda li a {
    color: #bcbec0 !important;
}

header .menu button:hover {
    background-color: #275492 !important;
    text-decoration: none;
}

h1 {
    color: #275492 !important;
}

p {
    word-wrap: break-word;
    color: #275492 !important;
}

.download section p {
    font-size: 15px;
    line-height: 25px;
    color: #58595b !important;
}

div#layout {
    background-color: #0d0d0d !important;
}

.download .comments {
    background: #0d0d0d !important;
    position: relative;
    margin: auto;
}

h4 {
    color: #2c61aa !important;
}

.icon_corner {
    width: 35px;
    height: 35px;
    background: none !important;
    position: absolute;
    top: 0px;
    right: 0px;
    vertical-align: middle;
    display: inline-block;
}

body.dark .nav>ul>li>a, body.dark .nav>ul>li>div>a {
    color: #2c61aa !important;
    text-shadow: none;
}

.js_tooltip > div:hover {
    background-color: rgba(247, 247, 247, 0) !important;
    cursor: default;
}

.middle .list_element article .number {
    width: 40px;
    float: left;
    padding: 0 10px 27px;
    color: #ffffff !important;
    font-size: 20px;
    text-align: right;
}

.loading_modal {
    display: none;
    position: fixed;
    z-index: 1100;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    background: rgba(13, 13, 13, 0.76) url('http://i.legendas.tv/carregando.gif') 50% 50% no-repeat !important;
}


}
